css:
@-moz-document url-prefix("https://support.truckersmp.com") {
/**
  * TruckersMP Support Dark Theme
  * Created by Digital (https://forum.truckersmp.com/index.php?/profile/4963-digital/)
  * Last Updated: 28 September 2017 18:32 UTC
  */

body {
    background-color: #252525;
    color: #999999;
}

a {
    color: #999999;
    text-decoration: underline;
}

a:hover {
    color: #79AC2B;
}

i {
    text-decoration: bold;
    color: #999999;
}

.roundcornerstop {
    background-image: none;
}

.roundcornersright {
    background-image: none;
}

.roundcornersbottom {
    background-image: none;
}

.roundcornersleft {
    background-image: none;
}

img[src*="img/roundcornerslt.jpg"] {
    display: none;
}

img[src*="img/roundcornersrt.jpg"] {
    display: none;
}

img[src*="img/roundcornerslb.jpg"] {
    display: none;
}

img[src*="img/roundcornersrb.jpg"] {
    display: none;
}

img[src*="img/blank.gif"] {
    background: none;
}

.smaller {
    color: #999999;
}

a.smaller {
    color: #999999;
    text-decoration: underline;
}

a.smaller:hover {
    color: #79AC2B;
    text-decoration: none;
}

.homepageh3, .section {
    color: #999999;
}

.searchfield {
    background-color: #202020;
    border: solid 1px #303030;
    color: #999999;
}

.searchbutton {
    background-color: #79AC2B;
    border-color: #79AC2B;
    border-radius: 0px;
}

.searchbutton:hover {
    background-color: #80B328;
    border-color: #80B328;
    border-radius: 0px;
}

div.notice {
    background: #fff9de none repeat scroll 0 0;
    border: 1px solid #fcefa1;
    color: #363636;
    padding: 10px;
    vertical-align: middle;
}

fieldset {
    background-color: #222222;
    border: 1px solid #79AC2B;
}

legend {
    background-image: none;
    background-color: #79AC2B;
    border: 1px solid #79AC2B;
    color: #FFFFFF;
}

h1 {
    color: #999999;
}

h3 {
    color: #999999;
}

#ul_category li a:hover {
    background-color: #303030;
    color: #999999;
}

textarea {
    background-color: #202020;
    border: 1px solid #303030;
    color: #999999;
}

input[type="text"] {
    background-color: #202020;
    border: 1px solid #303030;
    color: #999999;
}

input[type="password"] {
    background-color: #202020;
    border: 1px solid #303030;
    color: #999999;
}

input[type="file"] {
    color: #999999;
}

select {
    background-color: #202020;
    border: 1px solid #303030;
    color: #999999;
}

hr {
    border-bottom: 1px dotted #333333;
}

div.online {
    border: none;
    background: #222222;
    color: #000000;
}

td {
    color: #999999;
}

.ticketrow {
    background-color: #252525;
    color: #999999;
    border: #333333 1px dotted;
}

.ticketalt {
    background-color: #222222;
    color: #999999;
}

td.admin_critical {
    background-color: #222222;
    border: 1px solid #333333;
    color: #9400d3;
}

td[class="admin_critical"] a {
    color: #9400d3;
}

td.admin_white {
    background-color: #222222;
    color: #999999;
    border: #333333 1px solid;
}

td.admin_gray {
    background-color: #252525;
    color: #999999;
    border: #333333 1px solid;
}

.optionWhiteOFF {
    padding: 2px;
    border: 1px dotted silver;
    background-color: white;
}

.onhold {
    color: #999999;
}

form#findby {
    border: solid 1px #333333;
    padding: 10px;
}

form#showt {
    border: solid 1px #333333;
    padding: 10px;
}

table.enclosing {
    background-color: #222222;
    color: #999999;
}

table.white {
    background-color: #222222;
}

.borderTop {
    border-top: #333333 1px dashed;
}

tr[style] {
    background: #252525 !important;
    border-color: #999999 !important;
}

.tabberlive .tabbertab {
    padding: 5px;
    border: 1px solid #333333;
    border-top: 1px solid #333333;
}

ul.tabbernav {
    border-bottom: none;
}

ul.tabbernav li a {
    padding: 3px 0.5em;
    margin-left: 3px;
    border: 1px solid #333333;
    border-bottom: none;
    background: #212121;
}

ul.tabbernav li.tabberactive a {
    background-color: #303030;
    border-bottom: 1px solid #303030;
}

ul.tabbernav li a:hover {
    color: #999999;
    background: #303030;
    border-color: #333333;
}

ul.tabbernav li.tabberactive a:hover {
    color: #999999;
    background: #303030;
    border-bottom: 1px solid #333333;
}

ul.tabbernav li a:link {
    color: #999999;
}

ul.tabbernav li a:visited {
    color: #999999;
}

#ul_category li:first-child {
    border: 1px solid #999999;
}

#ul_category li {
    border: 1px solid #999999;
    border-top: 0px;
}

textarea:focus,
input[type="text"]:focus,
input[type="password"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="time"]:focus,
input[type="week"]:focus,
input[type="number"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="search"]:focus,
input[type="tel"]:focus,
input[type="color"]:focus,
.uneditable-input:focus {   
  border-color: #555555;
  box-shadow: none;
  outline: 0 none;
}

select:focus,
input[type="text"]:focus,
input[type="password"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="time"]:focus,
input[type="week"]:focus,
input[type="number"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="search"]:focus,
input[type="tel"]:focus,
input[type="color"]:focus,
.uneditable-input:focus {   
  border-color: #555555;
  box-shadow: none;
  outline: 0 none;
}

.orangebutton {
    background-color: #72c02c;
    background-image: none;
    color: #FEFEFE;
    border: 1px solid #72c02c;
}

.orangebuttonover {
    background-color: #5fb611;
    background-image: none;
    color: #FEFEFE;
    border: 1px solid #72c02c;
}

.optionWhiteOFF {
    border: dotted 1px #222222;
    background: none;
}

.optionWhiteON {
    border: solid 1px #505050;
    background-color: #505050;
}

.orangebuttonsec {
    background-color: #bf6628;
    background-image: none;
    color: #FEFEFE;
    border: 1px solid #bf6628;
}

.orangebuttonsecover {
    background-color: #d35400;
    background-image: none;
    color: #FEFEFE;
    border: 1px solid #d35400;
}

div.notice {
    background: #333 none repeat scroll 0 0;
    border: 1px solid #444;
    color: #31708f;
}

table.white th {
    background-color: #1C1C1C;
    color: #CCCCCC;
    border: 1px solid #333333;
    background-image: none;
}

table.white th a {
    color: #CCCCCC;
}

div.error {
    border: 1px solid #444;
    background: #333;
    color: #a94442;
}

div.success {
    border: 1px solid #444;
    background: #333;
    color: #3c763d;
}

.optionWhiteNbOFF {
    background: none;
}

.optionWhiteNbON {
    background: #505050;
}

.kbCatListOFF {
    background-color: #222222;
}

.kbCatListON {
    background-color: #505050;
}

.black {
    color: #CCCCCC;
}

.optionBlueOFF {
    border: 1px solid #222222;
    background: none;
}

.optionBlueON {
    border: 1px solid #222222;
    background-color: #505050;
}

div.progress-container {
    border: 1px solid #444444;
    background: #333333;
}

div.progress-container>div {
    background-color: #74a62b;
}

td.notes {
    background: #333 none repeat scroll 0 0;
    border: 1px solid #444;
    color: #31708f;
}
}
