css:
@-moz-document url-prefix("https://uusi.ampparit.com/") {
.night .loading {
  background-color: #000;
}
.night .item .item-outer-wrapper {
    background-color: #222;
    border-bottom: 1px solid #222;
}
.night .fa.fa-plus {
    color: #8cd437;
}
.night .fa.fa-minus {
    color: #8cd437;
}
body.night {
    background-color: #000;
}
.night .item .item-content h4 a {
    color: #8cd437;
}
.night .item .item-content h4 a:visited {
    color: #425a26;
}
.night .item .item-content h4 a:hover {
    text-decoration: underline;
    color: #8cd437;
}
.night .time-label {
    background: #222;
    height: 0;
    padding-bottom: 18px;
    padding-top: 8px;
}
.night .time-label span {
    background: #555;
    color: #eee;
}
.night .item .item-content .item-content-left {
    border-left: 1px solid #222;
}
.night .item .item-content .item-content-left::before {
    border-right: 1px solid #222;
}
.night .menu-item a,
.night .menu-item a:visited,
.night .menu-item a:active {
    color: #bbb;
}
.night .menu-item a:hover {
    background-color: #37650b;
    color: #fff;
}
.night .menu-item.active-item > a {
    color: #fff;
    background-color: #37650b;
}
.night .menu-content > ul:nth-child(2) > li {
    border-top: 1px solid;
    border-top-color: #222;
}
.night .menu-content {
    background-color: #333;
}
.night .items-list-header {
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#333333), to(#444444));
    border-bottom: 1px solid #000000;
}
.night .items-queue.more {
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#333333), to(#444444));
}
.night .sidebox .sidebox-header {
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#37650b), to(#2a4b08));
    color: #ddd;
}
.night .sidebox .sidebox-content a {
    color: #8cd437;
    font-weight: normal;
}
.night .sidebox .sidebox-content a:hover {
    text-decoration: underline;
    color: #8cd437;
}
.night .sidebox .sidebox-content a:visited {
    color: #425a26;
}
.night .sidebox .sidebox-content a:visited:hover {
    text-decoration: underline;
    color: #8cd437;
}
.night .sidebox .sidebox-content {
    background-color: #222;
}
.night .sidebox .sidebox-content .popular:hover {
    background: #222;
}
.night .top-menu-container {
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#333333), to(#222222));
}
.night .top-menu .main-navigation li.active {
    border-bottom: 3px solid #3F6D13;
}
.night .top-menu .main-navigation li a {
    color: #aaa;
}
.night .top-menu .search .search-text {
    color: #aaa;
}
.night .top-menu .main-navigation li.active a {
    color: #fff;
}
.night .footer a {
    color: #777;
}
.night .footer a:hover {
    color: #aaa;
}
.night .footer a:visited {
    color: #999;
}
.night .footer .footer-title {
    color: #aaa;
}
.night a.footer-button {
    background: #484848;
    color: #aaa
}
.night a.footer-button:visited {
    background: #484848;
    color: #aaa
}
.night a.footer-button:hover {
    background: #777;
    color: #aaa
}
.night .footer-container {
    background-color: #000000;
    display: flex;
    align-items: center;
    justify-content: center;
}
.night .footer-contact-section {
    color: #777;
}
.night input,
textarea {
    background-color: #111;
}
.night .quickfilter {
    color: #888;
}
.night .widget-container a {
    color: #888;
    border-color: #888;
}
.night .fa-heart {
    color: #8cd437;
}
.night .widget-container .whitelist-icon {
    color: #888;
    border-color: #888;
}
.night .widget-container .blacklist-icon {
    border-color: #888;
}
.night .search-input {
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#333333), to(#222222));
    box-shadow: 0 2px 12px -4px #8cd437;
}
.night .search-input .search-icon{
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(#333333), to(#222222));
    color: #888;
    border-right: 0px;
}
.night .search-input-field-container input {
    padding-left: 10px;
    color: #8cd437;
}
.night .search-bar {
    box-shadow: 0 2px 12px -4px #8cd437;
}
}
