 * Author:			Atx
 * Version:			v1.4
 * Latest update:	07.10.2018
 */

@-moz-document url-prefix("https://plan.polsl.pl/") {
html, body
  {
    background: #1C1C1C;
  }
 
 .bg2 TD
  {
    color: #B3B3B3;
    background-color: #3C3C3C;
  }
  
  .bg1 TD
  {
    background: #A1C6EB;
    color: #1C1C1C;
  }
  
  a:link, H1
  {
    color: #A1C6EB;
  }
  
  a:visited
  {
    color: #4686CC;
  }
  div[class*="course"]
  {
    opacity: 0.75;
  }
  
  .cdS
  {
    border-top: 1px solid #858585 !important;
    border-right: 1px solid #999 !important;
    border-bottom: 1px solid #999 !important;
    border-left: 1px solid #999 !important;
  }
}

@-moz-document url-prefix("https://plan.polsl.pl/menug.php") {
body > table > tbody > tr > td > div > a > img
  {
     opacity: 0;
  }

body > table:first-of-type > tbody > tr > td > div
  {
    background: url(https://i.imgur.com/DYrfiCw.png) no-repeat;
  }
  
body > table:first-child
  {
   background: linear-gradient(to bottom, #565656 0%, #1C1C1C 30%);
  }
  
  body > table:nth-child(2) > tbody > tr, a.menu_item
  {
    background: #A1C6EB;
    color: #1C1C1C;
  }
}

@-moz-document url-prefix("https://plan.polsl.pl/main.php") {
body
  {
   background: #1C1C1C;
    color: #B3B3B3;
  }
  
  table.titles
  {
    background: #3C3C3C;
  }
  
 .bg1 TD
  {
    background: #A1C6EB;
    color: #1C1C1C;
  }
  
  a:link
  {
    color: #A1C6EB;
  }
  
  a:visited
  {
    color: #3373b9;
  }
  
  FORM.plan_search INPUT, INPUT.input
  {
    background: #1C1C1C;
    border: 2px solid #2C2C2C;
    color: #B3B3B3;
  }
  
  .titles
  {
   border: none;
  }
  
  body > p > a > img
  {
    opacity: 0;
  }
   body > p > a
  {
    background: url(https://i.imgur.com/LTvIv9i.png) no-repeat;
    display: inline-block;
    width: 226px;
    height: 60px;
  }
}

@-moz-document url-prefix("https://plan.polsl.pl/plan.php") {
.cd
  {
    background: #2C2C2C;
    color: #B3B3B3;
  }
  
.cdS
  {
    background-color: #3C3C3C;
    border: 1px solid #6C6C6C;
    margin-left: 1px;
  }
  
  .wBBox, .data
  {
    color: #B3B3B3;
    background-color: #3C3C3C;
  }
  
  .wBLeft, .wBRight, .wBCheck
  {
    color: #B3B3B3;
    background: none;
  }
  
  .wBSelect, #wBWeek
  {
    background: #2C2C2C;
    border: none;
    color: #B3B3B3;
  }
  
 .titleBar, .title
  {
    background: #A1C6EB;
    color: #1C1C1C;
  }

  a:link
  {
    color: #000066 !important;
  }

  a:visited
  {
    color: #003399 !important;
  }

.dataBox > .data > a
  {
    color: #9fbfdf !important;
  }

#print > .data > a:visited
  {
    color: #4080bf !important;
  }

#wBButton
{
	background: #3C3C3C !important;
}
}

@-moz-document url-prefix("https://plan.polsl.pl/left_menu.php") {
body
  {
    background: #3C3C3C;
  }
  table.branch2
  {
    background: none;
    color: #B3B3B3;
    border: none;
  }
  
  a:link
  {
    color: #A1C6EB;
  }
  
  
  a:visited
  {
    color: #3373b9;
  }
}

@-moz-document url-prefix("https://plan.polsl.pl/right_menu.php") {
div#right_menu
  {
    color: #1C1C1C;
    background: #A1C6EB;
  }
  
  .toggler-closed
  {
    background-color: #639BD5;
  }
  
  FORM.plan_search INPUT:first-child
  {
    background: #1C1C1C;
    border: none;
    color: #B3B3B3;
  }
}
