@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("approach0.xyz") {
  
  body {
    background-color: #222;
    color: #aaa;
    border-top-color: #222 !important; /* suppressing a border line */
  }
  
  button {
    background-color: #1c1c1c;
    border-color: #333;
    color: #aaa;
  }
  
  button:hover {
    background-color: #333;
    border-color: #333;
  }
  
  /**** Search Page ****/
  
  /*** Search Box ***/
  
  /** Links - 'handy pad', 'raw query', 'user guide' **/
  span.collapse {
    color: #4142c4; /* from #4183c4 */
  }
  
  a[href="/guide"] {
    color: #4142c4 !important; /* from #4183c4 */
  }
  
  span.collapse:hover {
    color: #aaa;
  }
  
  /* 'handy pad' Headings */
  h3 {
    color: #666;
  }
  
  /**** Search Results Page ****/
  
  /** Existing Query **/
  div.qry-div-fix {
    background-color: #282828;
    box-shadow: 0 1px 2px #555; /* only color changed */
  }
  
  span.edit {
    color: #aaa;
  }
  
  #qry-input-box {
    background-color: #3C3B37; /* default in Firefox */
  }
  
  /* Non-math expression */
  li.qry-li {
    color: #aaa;
/*     font-size: 120%; */
  }
  
  /* Math expression */
  .katex {
    color: #aaa;
/*     font-size: 150% !important; */
  }
  
  span.dele {
    color: #aaa;
  }
  
  /* Text */
  p {
    color: #aaa;
  }
  
  /** Typed Query **/
  
  /* General text */
  .pl_holder {
    color: #ccc;
  }
  
  /* Math expression */
  .mq-math-mode .mq-empty {
    background-color: #363636;
  }
  
  .mq-math-mode .mq-cursor {
    border-left-color: #aaa;
  }
  
  .mq-math-mode {
    font-size: 150%; /* enlarge while editing */
  }
  
  /** Result row **/
  
  /* Stackexchange post link */
  a.title {
    color: #4183C4;
  }
  
  span[style="color:#006d21"] {
    color: #008729 !important;
  }
  
  /* highlighted Search query */
  em.hl {
    background-color: #0a2140;
  }
  
  /** Footer **/
  div[style*="background: rgb(251, 254, 254)"] {
    background-color: #282828 !important;
    color: #999; /* for the arrows, ← and → */
  }
  
  .mainfont {
    color: #999;
  }
  
  a.page-navi, a.page-navi:visited {
    color: #4142c4; /* from #4183c4 */
  }
  
  /**** Guide Page - approach0.xyz/guide/ ****/
  
  /** Left-side Menu **/
  
  /* Expanded bookmark */
  .wy-menu-vertical li.current {
    background-color: #333;
  }
  
  .wy-menu-vertical li.current a:hover {
    background-color: #4e4a4a; /* from .wy-menu-vertical a:hover */
  }
  
  .wy-menu-vertical li.current > a {
    color: #aaa;
    background-color: #4e4a4a
  }
  
  /** Right-side Text **/
  .wy-nav-content-wrap {
    background-color: #222;
  }
  
  hr {
    border-top-color: #333;
  }
  
  h1, h2 {
    color: #666;
  }
  
  /* Code */
  code, .rst-content tt, .rst-content code {
    background-color: #282828;
    border-color: #333;
  }
  
  /* Image */
  .rst-content img {
    filter: invert(90%);
  }
  
  /* MathJax */
  span[id*="MathJax-Span"] {
    color: #aaa;
  }
  
  .MathJax_Menu {
    background-color: #282828;
    color: #999;
    border-color: #666;
  }
  
  .MathJax_MenuActive {
    background-color: #363636;
  }
  
  .MathJax_MenuRule {
    border-top-color: #666;
  }
  
}

@-moz-document domain("buttons.github.io") {
  
  /**** Search Results Page -> Footer ****/
  
  a {
    color: #aaa;
  }
  
  /* Github logo */
  .btn {
    background-color: #282828;
    background-image: none;
    border-color: #666;
  }
  
  /* Star count */
  .social-count {
    background-color: #282828;
    border-color: #666;
  }
  
  .social-count b, .social-count i {
    border-right-color: #666;
  }
  
}
