css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.kaggle.com") {
/*** Palette ***/
  :root {
    --default-color: #aaaaaa;
    --color-2: #ccc7; /* ~ rgb(204, 204, 204, 0.4); */
    --color-3: #ccca; /* ~ rgb(204, 204, 204, 0.7); */
    --color-4: #ccc;
    --default-bkgd-color: #222;
    --bkgd-color-2: #282828;
    --bkgd-color-3: #333;
    --bkgd-color-4: #33333307; /* ~ rgb(40, 40, 40, 0.03) */
    --bkgd-color-5: #484848;
    --default-border-color: #333;
    --border-color-2: #666;
    --default-link-color: #008ABC;
    --link-hover-color: #20BEFF;
  }
  
  body {
    background-color: var(--default-bkgd-color);
  }
  
  .button {
    background-color: var(--bkgd-color-3);
    color: var(--default-color);
  }
  
  .button:hover {
    background-color: var(--bkgd-color-5);
    color: var(--link-hover-color);
  }
  
  /**** Before login Page - www.kaggle.com ****/

.login__title, .login__divider, .login__subline, .signup__title {
  color: var(--default-color);
}

.modal__close-button {
  background-color: var(--default-bkgd-color);
  color: var(--default-color);
}

.login__input, .signup__input {
  border-color: var(--default-border-color);
}

.login__footer {
  background-color: var(--bkgd-color-2);
  color: var(--default-color);
}
  
  .home-learn {
    background-color: var(--default-bkgd-color);
    filter: invert(90%);
  }
  
  .home-play, .home-play__item {
    background-color: var(--default-bkgd-color);
  }
  
  .home-play__link {
    color: var(--default-color);
  }
  
  .home-work {
    background-color: var(--bkgd-color-2);
  }
  
  .home-work__image, .home-play__image {
    filter: invert(90%);
  }
  
  .site-footer {
    background-color: var(--default-bkgd-color);
  }
  
  .site-footer__nav a {
    color: var(--default-color);
  }
  
  /** Sign In Overlay **/
  
  .modal__content {
    background-color: var(--default-bkgd-color);
  }
  
  .login-modal__title {
    color: var(--default-color);
  }
  
  .login-modal__subtitle, .login-modal__divider {
    color: var(--color-2);
  }
  
  .login-modal__horizontal-divider {
    background-color: var(--default-border-color);
  }
  
  .login-modal__input {
    border-color: var(--default-border-color);
  }
  
  .login-modal__footer {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
    border-top-color: var(--default-border-color);
  }
  
  /**** Main Page - www.kaggle.com ****/
  
  .site-layout__main-content {
    background-color: var(--default-bkgd-color);
  }
  
  .newsfeed {
    color: var(--default-color);
  }
  
  /** Top Bar **/
  
  .site-header-react__extra-nav {
    background-color: var(--bkgd-color-2);
  }
  
  .site-header-react__extra-nav-primary-item a {
    color: var(--color-3);
  }
  
  .site-header-react__extra-nav-primary-item a:hover {
    background-color: var(--bkgd-color-4);
    color: var(--default-color);
  }
  
  .site-header-react__extra-nav-secondary {
    border-top-color: var(--default-border-color);
  }
  
  .site-header-react__extra-nav-secondary-item a {
    color: var(--color-2);
  }
  
  /* Triangle */
  
  .site-header-react__extra-nav::before {
    border-bottom-color: var(--bkgd-color-2);
  }
  
  /** Profile dropdown **/
  
  .site-header-react__user-nav {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .site-header-react__user-name {
    color: var(--color-2);
  }
  
  .site-header-react__user-nav-link {
    color: var(--color-3);
  }
  
  .site-header-react__user-nav-link:hover {
    background-color: var(--bkgd-color-4);
    color: var(--default-color);
  }
  
  .site-header-react__user-nav-item:last-child {
    border-top-color: var(--default-border-color);
  }
  
  .site-header-react__user-nav::before {
    border-bottom-color: var(--bkgd-color-2);
  }
  
  .site-header-react__user-nav-container::before {
    border-bottom-color: var(--bkgd-color-2);
  }
  
  /** Search suggestions **/
  
  .quick-search__results {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .quick-search__result--selected {
    background-color: var(--bkgd-color-3);
  }
  
  .quick-search__result-title {
    color: var(--color-3);
  }
  
  .quick-search__result-overview {
    color: var(--color-2);
  }
  
  .quick-search__not-found {
    color: var(--color-3);
  }
  
  /* footer */
  .quick-search__tip {
    color: var(--color-2);
    background-color: var(--default-bkgd-color);
  }
  
  .quick-search__tip pre {
    color: #3487a4; /* from default #28677d */
  }
  
  /** Posts **/
  
  /* Header */
  .newsfeed-story-header {
    background-color: var(--bkgd-color-2);
    color: var(--default-color);
  }
  
  .newsfeed-story-header__sublabel {
    color: var(--color-2);
  }
  
  .newsfeed-story-header--bordered {
    border-bottom-color: var(--default-border-color);
  }
  
  /* Body */
  .newsfeed__story-container {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .newsfeed-dataset__content-overview {
    color: var(--default-color);
  }
  
  .newsfeed-dataset__version-notes-container {
    border-top-color: var(--default-border-color);
  }
  
  .newsfeed-dataset__version-notes-label {
    color: var(--color-2);
  }
  
  .newsfeed-kernel__type {
    color: var(--color-2);
  }
  
  .newsfeed-message__original-author {
    color: var(--color-2);
  }
  
  .forum-message-content {
    color: var(--default-color);
  }
  
  .forum-message-content h1, .forum-message-content h2, .forum-message-content h3 {
    color: var(--default-color);
  }
  
  .newsfeed-competition__results-container {
    border-top-color: var(--default-border-color);
  }
  
  /* Images */
  .newsfeed-kernel__image {
    opacity: 0.6;
  }
  
  .forum-message-content img {
    opacity: 0.5;
  }
  
  /* Footer */
  .newsfeed-story-footer__context, .newsfeed-story-header__comments {
    color: var(--color-2);
  }
  
  .newsfeed-story-footer {
    border-top-color: var(--default-border-color);
  }
  
  /** User Profile **/
  
  .content-box {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  /* Header */
  .content-box__content-section--no-title {
    border-top-color: var(--default-border-color);
  }
  
  .content-box__content-section {
    border-color: var(--default-border-color);
  }
  
  .newsfeed__profile.block-link--bordered {
    border-bottom-color: var(--default-border-color);
  }
  
  .newsfeed__profile-label {
    color: var(--default-color);
  }
  
  /* Body */
  
  .content-box__simple-content {
    color: var(--default-color);
  }
  
  /** Job Position **/
  .newsfeed__bannered-job-position {
    color: var(--color-3);
  }
  
  .newsfeed__bannered-job-location {
    color: var(--color-2);
  }
  
  /** Sidebar **/
  
  /* 'Your Kernels' */
  
  .newsfeed-sidebar__image {
    border-color: var(--default-border-color);
    opacity: 0.4;
  }
  
  /* Job Listings */
  
  .sidebar-content__title-link {
    color: var(--default-color);
  }
  
  .newsfeed-sidebar__link {
    color: var(--color-3);
  }
  
  .sidebar-content {
    border-bottom-color: var(--default-border-color);
  }
  
  /** 'Loading more stories' message **/
  .smart-list__message, .smart-list__sort-label--mobile {
    color: var(--color-2);
  }
  
  /* about Kaggle */
  
  .social-icons__link {
    color: var(--color-3);
  }
  
  /**** Competitions Page - www.kaggle.com/competitions ****/
  
  .content-box__title-bar {
    background-color: var(--bkgd-color-3);
    border-color: var(--default-border-color);
  }
  
  .content-box__title {
    color: var(--color-2);
  }
  
  /* 'Search competitions' box */
  .smart-list__search {
    border-color: var(--default-border-color);
    color: var(--default-color);
  }
  
  /* Tabs - 'Active', 'All', 'Entered' */
  .content-box__tab, .content-box__tab--active {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .content-box__tab {
    color: var(--color-2);
  }
  
  .content-box__tab--active {
    color: var(--default-color);
  }
  
  .smart-list__sort-label {
    color: var(--color-2);
  }
  
  .has-value.Select--single > .Select-control .Select-value .Select-value-label {
    color: var(--default-color);
  }
  
  /* Dropdown for Prize, Deadline, Number of teams, Recently created */
  .Select-control {
    background-color: var(--bkgd-color-2);
  }
  
  .KaggleSelect .Select-control {
    border-color: var(--default-border-color);
  }
  
  /* Competition entry row */
  .competition-card__competition-link {
    color: var(--default-color);
  }
  
  .competition-card__thumbnail {
    opacity: 0.5;
  }
  
  .block-link:hover {
    background-color: var(--bkgd-color-3);
  }
  
  .competition-card__description {
    color: var(--color-3);
  }
  
  .competition-card__categories-link {
    color: var(--color-2);
  }
  
  .competition-card__reward {
    color: var(--default-color);
  }
  
  .competition-card__stats {
    color: var(--color-3);
  }
  
  /* Borders for competition categories */
  
  .competition-card--playground {
    border-left-color: #007099; /* from default #0BF; */
  }
  
  .competition-card--research {
    border-left-color: #7a6725; /* from default #F4CE4A; */
  }
  
  .competition-card--gettingstarted {
    border-left-color: #2d644b; /* from default #5AC996; */
  }
  
  .competition-card--featured {
    border-left-color: #7f312e; /* from default #FF625C; */
  }
  
  /** Footer **/
  .site-footer {
    border-top-color: var(--default-border-color);
  }
  
  /**** Datasets Page - www.kaggle.com/datasets ****/
  
  /* 'Learn more', 'New Dataset' buttons */
  .title-banner__call-to-action .button {
    background-color: var(--bkgd-color-2);
  }
  
  .title-banner__call-to-action .button:hover {
    background-color: var(--bkgd-color-3);
  }
  
  /* Awards Notice/Message Box */
  .datasets__awards {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  /* 'Sort by' label */
  .datasets__filters-label, .datasets__filters-label--mobile {
    color: var(--color-2);
  }
  
  /* Row with 'Featured', 'All', 'Mine', 'Upvoted' */
  .content-box__subtitle-bar {
    border-bottom-color: var(--default-border-color);
  }
  
  .selection-group__child--selected {
    color: var(--default-color);
  }
  
  .selection-group__items a:hover {
    color: var(--link-hover-color);
  }
  
  /* 'Search Datasets' box */
  .datasets__search {
    border-color: var(--default-border-color);
    color: var(--default-color);
  }
  
  /* Dataset entry row */
  .dataset-list-item {
    background-color: var(--default-bkgd-color);
    border-top: none;
/*     border-top-color: var(--default-border-color); */
    border-bottom-color: var(--default-border-color);
  }

  .dataset-list-item:hover {
    background-color: var(--bkgd-color-2);
  }
  
  .dataset-list-item__name {
    color: var(--default-color);
  }
  
  .dataset-list-item__summary {
    color: var(--color-3);
  }
  
  .dataset-list-item__details {
    color: #f00;
  }
  
  .dataset-list-item__details--author {
    color: var(--default-link-color);
    font-weight: 100;
  }
  
  .dataset-list-item__details--categories-link {
    color: var(--color-2);
  }
  
  .dataset-list-item:hover .dataset-list-item__details--author:hover, 
  .dataset-list-item:hover .dataset-list-item__details--categories-link:hover {
    color: var(--link-hover-color);
  }

/* 'Featured' star */
.dataset-item__classification {
  border-color: var(--default-border-color);
  color: var(--bkgd-color-2);
}

.dataset-item__main-title-link {
  color: var(--color-3);
}

.dataset-item__main-subtitle {
  color: var(--default-color);
}

.dataset-item__main-info {
  color: var(--color-2);
}

.dataset-item__main-owner-link {
  color: var(--color-2);
}

.dataset-item__tag, .dataset-item__more-tags {
  color: var(--color-3);
}
  
  .dataset-list-item__metadatum--nonlink {
    color: var(--color-2);
  }
  
  .dataset-list-item__metadatum--link {
    color: var(--color-2);
  }
  
  .dataset-list-item__metadatum--link:hover {
    color: var(--link-hover-color);
  }
  
  /**** Kernels Page - www.kaggle.com/kernels ****/
  
  /* Awards Notice/Message Box */
  .kernels-listing__awards {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  /* Kernel entry row */
  .kernel-list-item__name {
    color: var(--default-color);
  }
  
  .kernel-list-item__details {
    color: var(--color-2);
  }

.kernel-list-item__details a {
  color: var(--color-3);
}

.kernel-list-item__categories {
  color: var(--color-2);
}

.kernel-list-item__categories-link {
  color: var(--color-3) !important;
}

.kernel-list-item__info-blocks {
  color: var(--color-2);
  border-color: var(--border-color-2);
  border-radius: 2px;
}

.kernel-list-item__info-block--link {
  border-right-color: var(--border-color-2);
}
  
  .kernel-list-item__info-block--comment, .kernel-list-item__info-block--link {
    background-color: var(--bkgd-color-2);
    color: var(--color-3);
  }
  
  /**** Discussion Page - www.kaggle.com/discussion ****/
  
  /** Site Forums block **/
  
  .discussion__forums {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
    color: var(--default-color);
  }
  
  .discussion__forums-header {
    background-color: var(--bkgd-color-2);
    color: var(--default-color);
    border-bottom-color: var(--default-border-color);
  }
  
  .discussion__forum-title {
    color: var(--default-color);
  }
  
  .discussion__forum-subtitle {
    color: var(--color-3);
  }
  
  .discussion__forum-last-post {
    color: var(--color-2);
  }
  
  .discussion__forums-row {
    border-bottom-color: var(--default-border-color);
  }
  
  .discussion__forum:hover {
    background-color: var(--bkgd-color-3);
    border-left-color: var(--default-border-color);
    border-right-color: var(--default-border-color);
  }
  
  /* Discussion entry row */
  .topic-list-item__title {
    color: var(--default-color);
  }
  
  .topic-list-item__subtitle {
    color: var(--color-2);
  }
  
  .topic-list-item__link--forum-link {
    color: var(--color-2);
  }
  
  .topic-list-item__last-comment {
    color: var(--color-2);
  }
  
  .topic-list-item__comment-count {
    background-color: var(--bkgd-color-2);
    color: var(--color-3);
    border-color: var(--default-border-color);
  }
  
  .topic-list-item__comment-count:hover {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }

  /**** Learn Page - www.kaggle.com/learn ****/

.czQfBc, .gQKztu {
  background-color: var(--default-bkgd-color);
}

/** Options **/

/* Title */
.cjCVcp {
  color: var(--color-2);
}

.dBvLYm {
  color: var(--color-3);
}

/* Option bkgd image */
.cRkTyd {
  background-color: var(--bkgd-color-2);
}
  
  /**** Jobs Page - www.kaggle.com/jobs ****/
  
  #wrap {
    background-color: var(--default-bkgd-color);
  }
  
  /* Top bar -> 'Community' dropdown menu */
  #header2 ul li > ul {
    background-color: var(--bkgd-color-2);
  }
  
  #header2 ul li > ul li a {
    color: var(--default-color);
  }
  
  #header2 ul li > ul li a:hover {
    color: var(--link-hover-color);
  }
  
  /* 'Hiring?', 'Seeking?' Message board */
  .jobs-board-list .jobs-header {
    background-color: var(--bkgd-color-2);
  }
  
  .jobs-board-list .jobs-header a {
    color: var(--color-4);
  }
  
  .fa.fa-user-plus {
    color: var(--color-4) !important;
  }
  
  .jobs-board-list .jobs-header .right-panel {
    border-left-color: var(--default-border-color);
  }
  
  .jobs-board-list .jobs-header p, .jobs-board-list .jobs-header div {
    color: var(--color-3);
  }
  
  /* 'Search our listings' Search box */
  .jobs-board-list .jobs-header input[type="text"] {
    border-color: var(--default-border-color);
    background-color: var(--default-bkgd-color);
  }
  
  .jobs-board-list .jobs-header input.search[type="submit"] {
    filter: invert(86%); /* to match #222 */
  }
  
  /* Job entry row */
  .job-post-row:not(:last-child) {
    border-bottom-color: var(--default-border-color);
  }
  
  /* footer */
  #footer {
    background-color: var(--default-bkgd-color);
    border-top-color: var(--default-border-color);
  }
  
  #footer-links a {
    color: var(--color-3);
  }
  
  /**** Individual Competition Page - www.kaggle.com/c/<xyz> ****/
  
  /* Header */
  .competition-header__top {
    border-color: var(--default-border-color);
    color: var(--default-color);
  }
  
  .competition-header__details a, .competition-header__details--no-org a {
    color: var(--default-color);
  }
  
  .competition-header__top--alt {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
    color: var(--default-color);
  }
  
  .competition-header__subtitle--alt {
    color: var(--color-3);
  }
  
  .competition-header__details--alt {
    color: var(--color-2);
  }
  
  .competition-data__preview-container {
    border-top-color: var(--default-border-color);
  }
  
  /* Tabs - 'Overview', 'Data', 'Kernels', 'Discussion', 'Leaderboard', 'Rules' */
  .pageheader__bottom {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .pageheader__left-links {
    color: var(--color-2);
  }
  
  /* 'Join Competition' */
  .pageheader__calltoaction {
    color: var(--default-color);
    background-color: var(--bkgd-color-3);
  }
  
  .pageheader__calltoaction:hover {
    color: var(--color-4);
  }
  
  /** Overview tab **/
  
  /* Left bar */
  .competition-overview__nav-item {
    color: var(--default-color);
  }
  
  .competition-overview__nav-item--selected {
    color: var(--link-hover-color); /* restoring default overridden by above */
    background-color: var(--bkgd-color-2);
  }
  
  .competition-overview__wrapper {
    border-right-color: var(--default-border-color);
  }
  
  /* Content - on Pane on right side */
  .markdown-converter__text--rendered {
    color: var(--color-3);
  }
  
  .markdown-converter__text--rendered h1, 
  .markdown-converter__text--rendered h2, 
  .markdown-converter__text--rendered h3 {
    color: var(--color-3);
  }
  
  /* Content - Code */
  .markdown-converter__text--rendered code, .markdown-converter__text--rendered pre {
    background-color: var(--bkgd-color-2);
  }
  
  /* Leaderboard list */
  .teaser-list {
    color: var(--color-3);
  }
  
  /* Timeline */
  .horizontal-timeline__line {
    background-color: var(--bkgd-color-3);
  }
  
  .competition-overview__milestone-title {
    color: var(--color-2);
  }
  
  /* Stats block in bottom */
  .competition-overview__stats, .competition-overview__progression-info {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .competition-overview__stat-number, .competition-overview__progression-info-text {
    color: var(--color-2);
  }
  
  /* 'Tags' */
  .category__title {
    color: var(--color-2);
  }
  
  .category {
    background-color: var(--bkgd-color-2);
  }
  
  .category__name {
    color: var(--default-color);
  }
  
  .category__name:hover {
    color: var(--color-4);
  }
  
  /** Data tab **/
  
  /* Competition Data */
  .file-preview-list__list {
    background-color: var(--bkgd-color-2);
    border-right-color: var(--default-border-color);
  }
  
  .file-preview-list__list-item {
    color: var(--default-color);
  }
  
  .file-preview-list__list-item:hover {
    color: #158BBA;
