@-moz-document url-prefix("https://www.kaggle.io/svf") {
/*** Palette ***/
  :root {
    --default-color: #aaaaaa;
    --color-3: #ccca; /* ~ rgb(204, 204, 204, 0.7); */
    --color-4: #ccc;
    --default-bkgd-color: #222;
    --bkgd-color-2: #282828;
    --bkgd-color-3: #333;
    --code-bkgd-color: #1c1c1c;
    --code-bkgd-color-2: #272822;
    --default-border-color: #333;
    --border-color-2: #666;
    --link-hover-color: #20BEFF;
  }
  
  /*** Report ***/
  body, div.body {
    background-color: var(--default-bkgd-color);
  }
  
  body, div#notebook, div.body {
    color: var(--color-3);
  }
  
  .container-fluid h1, .container-fluid h2, .container-fluid h3 {
    color: var(--default-color);
  }
  
  .container-fluid h4, .container-fluid h5 {
    color: var(--default-color);
  }
  
  .container-fluid p {
    color: var(--color-3);
  }
  
  /* inline Code segments */
  .container-fluid p code {
    background-color: var(--code-bkgd-color);
    border-color: var(--default-border-color);
  }
  
  /* 'Code' button */
  .btn-default, 
  .btn-default:focus, .btn-default.focus {
    color: var(--default-color);
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .btn-default:hover {
    color: var(--link-hover-color);
  }
  
  /* 'Code' at top -> dropdown */
  .dropdown-menu {
    background-color: var(--bkgd-color-2);
    border-color: var(--default-border-color);
  }
  
  .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    background-color: var(--bkgd-color-2);
  }
  
  /* 'Code' -> Code retractable pane */
  .container-fluid pre {
    background-color: var(--code-bkgd-color);
    border-color: var(--default-border-color);
    color: var(--default-color);
  }
  
  .container-fluid code {
    color: var(--color-3);
  }
  
  /*** Notebook ***/
  
  #notebook-container {
    background-color: var(--default-bkgd-color);
  }
  
  div.text_cell_render {
    background-color: var(--default-bkgd-color);
    color: var(--color-3);
  }
  
  div.text_cell_render h1, div.text_cell_render h2, div.text_cell_render h3, div.text_cell_render h4, div.text_cell_render h5 {
    color: var(--default-color);
  }
  
  .rendered_html hr {
    background-color: var(--border-color-2);
  }
  
  .rendered_html p {
    color: var(--color-3);
  }
  
  /* IPython prompt */
  .prompt, div.prompt {
    color: var(--border-color-2);
  }
  
  /* inline Code segments */
  .rendered_html p code {
    border-color: var(--default-border-color);
  }
  
  .rendered_html p code, .well, .well code, div.rendered_html pre, kbd {
    background-color: var(--code-bkgd-color);
  }
  
  .rendered_html, .well code, div.rendered_html code, div.rendered_html pre, kbd {
    color: var(--color-3);
  }
  
  .highlight {
    background-color: var(--default-border-color); /* acts like border */
  }
  
  /* Code block */
  div.input_area {
    background-color: var(--code-bkgd-color);
    border-color: var(--default-border-color);
  }
  
  .code_cell, .code_cell pre {
    color: var(--default-color);
  }
  
  /** Syntax highlighting - Python **/
  
  .highlight .nn {
    color: #e02266; /* from Sublime Text -> imported package names : #F92672 */
  }
  
/*   .highlight .nn {
    color: var(--default-color);
  } */
  
  /* function name */
  .highlight .nf {
    color: #FD971F; /* from Sublime Text -> func. arg. */
  }
  
/*   .highlight .nf {
    color: #A6E22E; / * from Sublime Text -> func. name * /
  } */
  
  /* import, from */
  .highlight .kn, .highlight m {
    color: #009500; /* from default #007B00 */
  }
  
  /* keywords */
  .highlight .k {
    color: #009500; /* from default #007B00 */
  }
  
  /* print, list */
  .highlight .nb {
    color: #009500;
  }
  
  /* Numerals */
  .highlight .mi {
    color: #7aa3c3; /* from cppreference -> .cpp.source-cpp .nu0 */
  }
  
  /* Parenthesis, : */
  .highlight .p {
    color: #666; /* from .highlight .mi */
  }
  
/*   .highlight .p {
    color: #4fa2b3; / * from cppreference -> .mw-geshi a * /
  } */
  
  /** Code output **/
  div.output_subarea.output_text, div.output_subarea.output_text.output_stream.output_stdout {
    background-color: var(--code-bkgd-color-2);
  }
  
  div.output_area pre {
    color: var(--default-color);
  }
  
  /* Table */
  .rendered_html table {
    background-color: var(--default-bkgd-color);
    border-right-color: var(--default-bkgd-color); /* make line invisible */
    color: var(--color-3);
  }
  
  .rendered_html table th {
    background-color: var(--bkgd-color-3);
    border-color: var(--default-bkgd-color);
  }
  
  .rendered_html table td {
    border-color: var(--default-border-color);
  }
  
  .rendered_html table tr, .rendered_html table tr:nth-child(2n+1):not(:hover) {
    background-color: var(--bkgd-color-2);
  }
  
  .rendered_html tr, .rendered_html th, .rendered_html td {
    border-color: var(--default-border-color);
  }
  
  /* Image */
  div.output_area img, div.output_area svg {
    opacity: 0.4;
  }
}

@-moz-document url-prefix("https://www.kaggle.io/k") {
/*** Palette ***/
  :root {
    --default-color: #aaaaaa;
    --color-2: #ccc7; /* ~ rgb(204, 204, 204, 0.4); */
    --color-3: #ccca; /* ~ rgb(204, 204, 204, 0.7); */
    --default-bkgd-color: #222;
    --bkgd-color-2: #282828;
    --bkgd-color-3: #333;
    --code-bkgd-color: #1c1c1c;
    --default-border-color: #333;
    --border-color-2: #666;
  }
  
  /** Without selection **/
  
  body.notebook_app {
    background-color: var(--default-bkgd-color);
  }
  
  body > #header .header-bar {
    background-color: var(--default-bkgd-color);
  }
  
  #notebook-container, .notebook_app, .notebook_app #header {
    background-color: var(--default-bkgd-color);
  }
  
  div.cell.text_cell.rendered .input_prompt {
    background-color: var(--default-bkgd-color);
  }
  
  div.cell.text_cell {
    border-color: var(--default-border-color);
  }
  
  div.cell.text_cell.unselected:hover {
    border-color: var(--border-color-2);
  }
  
  div.text_cell_render {
    background-color: var(--default-bkgd-color);
  }
  
  .rendered_html p {
    color: var(--color-3);
  }
  
  .rendered_html, .well code, div.rendered_html code, div.rendered_html pre, kbd {
    color: var(--color-3);
  }
  
  .cm-s-ipython span.cm-variable {
    color: var(--color-3);
  }
  
  .cm-s-ipython span.cm-comment {
    color: var(--color-2);
  }
  
  div.text_cell_render h1, 
  div.text_cell_render h2, 
  div.text_cell_render h3, 
  div.text_cell_render h4, 
  div.text_cell_render h5 {
    color: var(--default-color);
  }
  
  .cm-s-ipython.CodeMirror {
    background-color: var(--default-bkgd-color);
    color: var(--color-3);
  }
  
  div.CodeMirror, div.CodeMirror pre {
    color: var(--color-3);
  }
  
  div.input_area {
    border-color: var(--default-border-color);
  }
  
  .cm-s-ipython span.cm-attribute, .cm-s-ipython span.cm-property {
    color: var(--color-3);
  }
  
  div.text_cell_render pre {
    background-color: var(--code-bkgd-color);
  }
  
  .rendered_html p code, .well, .well code, div.rendered_html pre, kbd {
    background-color: var(--code-bkgd-color);
  }
  
  .prompt, div.prompt {
    color: var(--color-2);
  }
  
  
  /** Selected Block **/
  
  div.cell.text_cell.selected, div.cell.text_cell.unrendered.selected {
    background-color: var(--bkgd-color-3);
  }
  
  div.cell.selected.text_cell {
    border-color: var(--default-border-color);
  }
  
  div.cell.selected::before {
    background-color: var(--bkgd-color-2);
  }
  
  div.cell.text_cell.unrendered div.input_area {
    background-color: var(--bkgd-color-3);
  }
  
  .mirrormark-toolbar {
    background-color: var(--bkgd-color-3);
    border-bottom-color: var(--border-color-2);
  }
  
  .mirrormark-toolbar li a {
    background-color: var(--bkgd-color-3);
  }
  
  .cell-overlay__toolbar {
    border-color: var(--default-border-color);
  }
  
  .cell-overlay__type, .cell-overlay__insert {
    border-right-color: var(--default-border-color);
  }
  
  .cell-overlay__insert {
    color: var(--color-2);
  }
  
  /* Markdown */
  div.cell.selected .input {
    background-color: var(--bkgd-color-2);
  }
  
  div.cell.text_cell .cm-s-default .cm-header {
    color: var(--default-color);
  }
  
  .cm-s-default span.cm-comment {
    color: var(--color-2);
  }
}
