css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/browser.xul"){
.panel-banner-item{
	display: none!important;
}
.toolbaritem-combined-buttons > spacer {
    width: 0px!important;
}
#appMenu-zoom-controls{
	margin-left: -4px!important;
}
#appMenu-edit-controls{
	margin-left: 15px!important;
}
#appMenuRestoreLastSession{
	display: none!important;
}
#appMenu-mainView{
  min-width: 15.5em!important;
	max-width: 15.5em!important;
}
#appMenu-mainView > .panel-subview-body{
	display: block!important;
	height: 277px!important;
  padding-left: 4px!important;
}
#PanelUI-developer{
	min-height: 48em!important;
}
.panel-viewstack > #PanelUI-remotetabs,
.panel-viewstack > #PanelUI-downloads,
.panel-viewstack > #PanelUI-history,
.panel-viewstack > #PanelUI-bookmarks{
	min-width: 30em!important;
}
.panel-subview-body > toolbarseparator{
display: none!important;
}
#appMenu-fxa-label,
#appMenu-new-window-button,
#appMenu-private-window-button,
#appMenuRestoreLastSession,
#appMenu-library-button,
#appMenu-addons-button,
#appMenu-preferences-button,
#appMenu-customize-button,
#appMenu-open-file-button,
#appMenu-save-file-button,
#appMenu-more-button,
#appMenu-developer-button,
#appMenu-print-button,
#appMenu-find-button,
#appMenu-help-button,
#appMenu-quit-button,
#appMenu-cut-button,
#appMenu-copy-button,
#appMenu-paste-button,
#appMenu-zoomReduce-button,
#appMenu-zoomReset-button,
#appMenu-zoomEnlarge-button,
#appMenu-fullscreen-button{
  border-radius: 2px!important;
  border: 1px solid transparent!important;
}
#appMenu-fxa-label:hover,
#appMenu-new-window-button:hover,
#appMenu-private-window-button:hover,
#appMenuRestoreLastSession:hover,
#appMenu-library-button:hover,
#appMenu-addons-button:hover,
#appMenu-preferences-button:hover,
#appMenu-customize-button:hover,
#appMenu-open-file-button:hover,
#appMenu-save-file-button:hover,
#appMenu-more-button:hover,
#appMenu-developer-button:hover,
#appMenu-print-button:hover,
#appMenu-find-button:hover,
#appMenu-help-button:hover,
#appMenu-quit-button:hover,
#appMenu-cut-button:hover,
#appMenu-copy-button:hover,
#appMenu-paste-button:hover,
#appMenu-zoomReduce-button:hover,
#appMenu-zoomReset-button:hover,
#appMenu-zoomEnlarge-button:hover,
#appMenu-fullscreen-button:hover{
	border-radius: 2px!important;
  border: 1px solid gray!important;
}
#appMenu-open-file-button{
  list-style-image: url("chrome://browser/skin/open.svg")!important;
}
#appMenu-save-file-button{
list-style-image: url("chrome://browser/skin/save.svg")!important;
}
#appMenu-more-button{
  list-style-image: url(chrome://browser/skin/chevron.svg)!important;
}
#appMenu-developer-button{
	list-style-image: url("chrome://browser/skin/developer.svg");  
}
#appMenu-fxa-label > .toolbarbutton-text,
#appMenu-new-window-button > .toolbarbutton-text,
#appMenu-private-window-button > .toolbarbutton-text,
#appMenuRestoreLastSession > .toolbarbutton-text,
#appMenu-library-button > .toolbarbutton-text,
#appMenu-addons-button > .toolbarbutton-text,
#appMenu-preferences-button > .toolbarbutton-text,
#appMenu-customize-button > .toolbarbutton-text,
#appMenu-print-button > .toolbarbutton-text,
#appMenu-find-button > .toolbarbutton-text,
#appMenu-help-button > .toolbarbutton-text,
#appMenu-quit-button > .toolbarbutton-text,
#appMenu-open-file-button > .toolbarbutton-text,
#appMenu-save-file-button > .toolbarbutton-text,
#appMenu-more-button > .toolbarbutton-text,
#appMenu-developer-button > .toolbarbutton-text{
  display: none!important;
}
#appMenu-fxa-label > .toolbarbutton-icon,
#appMenu-new-window-button > .toolbarbutton-icon,
#appMenu-private-window-button > .toolbarbutton-icon,
#appMenuRestoreLastSession > .toolbarbutton-icon,
#appMenu-library-button > .toolbarbutton-icon,
#appMenu-addons-button > .toolbarbutton-icon,
#appMenu-preferences-button > .toolbarbutton-icon,
#appMenu-customize-button > .toolbarbutton-icon,
#appMenu-print-button > .toolbarbutton-icon,
#appMenu-find-button > .toolbarbutton-icon,
#appMenu-help-button > .toolbarbutton-icon,
#appMenu-quit-button > .toolbarbutton-icon,
#appMenu-open-file-button > .toolbarbutton-icon,
#appMenu-save-file-button > .toolbarbutton-icon,
#appMenu-more-button > .toolbarbutton-icon,
#appMenu-developer-button > .toolbarbutton-icon{
	width: 33px!important;
	height: 33px!important;
	opacity: 0.6!important;
}
#appMenu-cut-button > .toolbarbutton-icon,
#appMenu-copy-button > .toolbarbutton-icon,
#appMenu-paste-button > .toolbarbutton-icon,
#appMenu-zoomReduce-button > .toolbarbutton-icon,
#appMenu-zoomReset-button > .toolbarbutton-icon,
#appMenu-zoomEnlarge-button > .toolbarbutton-icon,
#appMenu-fullscreen-button > .toolbarbutton-icon{
	opacity: 0.6!important;
}
#appMenu-zoom-controls > label,
#appMenu-edit-controls > label{
  display: none!important;
}
.subviewbutton[shortcut]::after,
.subviewbutton[shortcut]::after,
.PanelUI-subView .subviewbutton-nav::after {
  display: none!important;
}
}
