css:
@-moz-document domain("bato.to") {
#footer_utilities, #index_stats > *:first-child, #content > br, #commentsStart, #content > div > div > div.clear > h2.maintitle, #content > div > div > div.clear > h2.maintitle ~ table, #content > div > div > div.clear > br, #content > div > div > .ipsPad, #content > div > div > div.clear > div.clear:nth-child(4) > div:nth-child(1), #content > div > div > div.clear > div.clear:nth-child(4) > div:nth-child(2) ul.topic_buttons, table.ipb_table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(8) > td:nth-child(1) > a:nth-child(2), #register_notice {
	display: none;
}
#index_stats {
	position: absolute;
	right: 0;
	transform: scaleX(.1) scaleY(.2); /* translateX(130px) translateX(0px) */
	transform-origin: top right;
	transition: ease .4s;
}
#index_stats:hover {
	transform: scaleX(1) scaleY(1);
	box-shadow: 0px 0px 5px black;
	background: white;
}
#content div.clearfix {
	margin-right: 0 !important;
}
#branding {
	position: absolute;
	width: 100%;
	transform: scale(.4);
	transform-origin: top center;
	top: -48px;
	transition: ease .4s;
	z-index: 45;
}
#branding:hover {
	transform: scale(1);
}
#content {
	padding: 0 !important;
	overflow: hidden;
}
#content > div:nth-child(4) {
	min-height: unset !important;
}
#content > div > div > div.clear > div:nth-child(5) {
	max-height: calc(100%); /* 100vh - 120px */
	overflow-y: auto;
	transform: translateX(260px);
	transition: ease .4s;
}
#content > div > div > div.clear > div:nth-child(5):hover {
	transform: translateX(0px);
	z-index: 5;
	box-shadow: 0px 0px 5px black;
	background: white;
}
#content > div:nth-child(4) > div:nth-child(1) {
	margin-right: 50px !important;
}
div.moderation_bar:nth-child(1) {
	margin-bottom: 0px !important;
	height: auto !important;
	padding-top: 8px !important;
	text-shadow: 1px 1px 2px;
}
#comic_wrap > div:nth-child(1) {
	top: 0 !important;
	margin-bottom: 0 !important;
}
div.moderation_bar:nth-child(6) {
	margin-bottom: 0px !important;
	height: auto !important;
	padding-top: 5px !important;
	text-shadow: 1px 1px 2px;
}
#ipboard_body > #ipbwrapper > #mainContent {
	padding-top: 0px !important;
	padding-bottom: 0px !important;
	background: rgba(255, 255, 255, .75) !important;
	box-shadow: 0px 0px 5px white;
}
.chapters_list {
	background: rgba(255, 255, 255, .33);
}
#content > div > .category_block.block_wrap {
	margin: 5px 40px 0 0 !important;
}
.exclusive_titles {
	margin: 10px 40px 10px 0 !important;
}
}
