css:
@-moz-document domain("qqtimer.net") {
@media only screen and (max-width: 990px) {
    #optbox, #optbox2{
      width: 110px;
    }
    span#scramble{
      margin: 0 0 0 15px !important;
      font-size: 18px !important;
    }
}
  
  body{
    padding: 0;
    margin: 0;
    background-image: url(http://i64.tinypic.com/2q83g55.gif) !important;
    background-color: #414b52 !important;
    color: #cfdae3 !important;
    overflow-x: hidden !important;
   }
  
  table, tr, td {
    border: 0px;
    border-color: #FFFFFF;
    border-style: solid;
  }
  
  div, table, tr, td {
    font-family: Helvetica !important;
   }
    
  #menu{
    padding: 10px 20px; 
    width:100%;
    background: #20282D;
    background: -moz-linear-gradient(top, #3D4850 3%, #313d45 4%, #232B30 100%); /* firefox */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#3D4850), color-stop(4%,#313d45), color-stop(100%,#232B30)); /* webkit */
    box-shadow: 1px 1px 1px rgba(0,0,0,0.2); /* CSS3 */
    -moz-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); /* Firefox */
    -webkit-box-shadow: 1px 1px 1px rgba(0,0,0,0.2); /* Google Chrome */
    border-bottom: 5px solid #333; 
  }
  
 #scramble {
   line-height: 50px !important;
   margin: 0 0 0 18px !important;
   font-family: Helvetica !important;
   font-size: 20px !important;
   text-shadow: 0 0 5px #aaaaaa;
   display: inline-block !important;
  }

  #theTime{
    color: #1e1e1e !important;
    text-shadow: 0px 1px 2px #999999, 0px -1px 1px rgba(17, 17, 17, 0.3) !important;
    font-size: 120px !important;
    position: relative;
    bottom: 2px;
    line-height: 1.4em;
    text-transform: capitalize;
  }
  
  
  #getlast {
    display: inline-block !important;
    margin-left: 10px !important;
    text-transform: capitalize !important;
  }
  
  .a, .b{
    -moz-user-select: none;
    -webkit-user-select: none;
    transition: text-shadow 0.2s ease;
  }
  
  .a:hover{
    cursor: pointer !important;
    text-shadow: 0 0 1px #999999;
  }
  .b:hover {
    cursor: pointer !important;
    text-shadow: 0 0 2px #ffffff;
  }
  
  .a {
    color: #a3b1bc !important;
    -moz-user-select: none;
    -webkit-user-select: none;
  }
  
  span[style="background-color: yellow"] {
    background-color: #999999 !important;
  }
  
  #avgdata{
    background-color: rgba(255, 255, 255, 0.1) !important;
    position: relative;
    top: 50px;
   }
  
  	#leng {
		padding: 0 0 0 8px !important;
    margin: 0 0 0 7px;
		border: 1px solid #1c252b !important;
		height: 30px !important;
		color: #9fa8b0 !important;
		font-size: 12px !important;
		line-height: 30px !important;
		font-weight: bold !important;
		text-shadow: 1px 1px #1f272b !important;
		-moz-border-radius: 25px !important;
		-webkit-border-radius: 25px !important;
		border-radius: 25px !important;
		background: #20282D !important; /* old browsers */
		background: -moz-linear-gradient(top, #20282D 3%, #252E34 51%, #222A30 100%) !important; /* firefox */
		background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#20282D), color-stop(51%,#252E34), color-stop(100%,#222A30)) !important; /* webkit */
		-moz-box-shadow: 1px 1px 1px rgba(255,255,255,0.1) !important; /* Firefox */
		-webkit-box-shadow: 1px 1px 1px rgba(255,255,255,0.1) !important; /* Safari, Chrome */
		box-shadow: 1px 1px 1px rgba(255,255,255,0.1) !important; /* CSS3 */
	}
	#leng:focus {outline: none !important;}
  
  #theList, #stats {
    overflow-x: hidden !important;
    overflow-y: auto !important;
    height: 768px;
    padding: 8px;
  }
 
  #theList {
    background-color: rgba(255, 255, 255, 0.1) !important;
    font-family: 'Courier New' !important;
    text-transform: capitalize !important;
  }
  
  #stats {
    background-color: rgba(255, 255, 255, 0.1) !important;
  }
  
  select {
		padding: 0 0 0 8px !important;
		border: 1px solid #1c252b !important;
		height: 30px !important;
		color: #9fa8b0 !important;
		font-size: 12px !important;
		line-height: 30px !important;
		font-weight: bold !important;
		text-shadow: 1px 1px #1f272b !important;
		-moz-border-radius: 25px !important;
		-webkit-border-radius: 25px !important;
		border-radius: 25px !important;
		background: #20282D !important; /* old browsers */
		background: -moz-linear-gradient(top, #20282D 3%, #252E34 51%, #222A30 100%) !important; /* firefox */
		background: -webkit-gradient(linear, left top, left bottom, color-stop(3%,#20282D), color-stop(51%,#252E34), color-stop(100%,#222A30)) !important; /* webkit */
		-moz-box-shadow: 1px 1px 1px rgba(255,255,255,0.1) !important; /* Firefox */
		-webkit-box-shadow: 1px 1px 1px rgba(255,255,255,0.1) !important; /* Safari, Chrome */
		box-shadow: 1px 1px 1px rgba(255,255,255,0.1) !important; /* CSS3 */
    
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  /* Add some styling */
  
  max-width: 320px;
  margin: 0 10px 0 12px;
  height: 50px;
  line-height: 1.75;
  color: #333;
  border: 1px solid #cccccc;
  -ms-word-break: normal;
  word-break: normal;
}
	
	select:focus {outline: none !important;}
  
  select > option {
  margin: 3px;
  padding: 6px 8px;
  text-shadow: none;
  background: #3D4850;
  -moz-border-radius: 3px;
  -webkit-border-radius: 3px;
  border-radius: 3px;
  cursor: pointer;
}


#sessbox {
  margin: 0 0 0 8px !important;
  width: 40px;
}
}
