css:
/* i really want this to be global */

.theme-light .vs-l-board, .theme-light .vs-c-blocking-loader .el-loading-mask .is-fullscreen { background-color: #222 !important; }
.theme-light .vs-c-task-card { /*background-color: #151515;*/ border-top-color: #3c3c3c; border-right-color: #3c3c3c; border-bottom-color: #3c3c3c; }
.theme-light .vs-c-task-card:nth-child(odd) { background-color: #1d1d1d;}
.theme-light .vs-c-task-card:nth-child(even) { background-color: #0c0c0c;}
.theme-light .vs-c-task-card.vs-c-task-card-drag-selected, .theme-light .vs-c-filter-dropdown-menu .vs-c-filter-control-menu { background: #000; }
.theme-light .vs-c-task-card__title .markdown-body { color: #fff !important; }
.theme-light .vs-c-task-list { background-color: #1f1f1f !important; }
.theme-light .vs-l-board, .theme-light .vs-c-blocking-loader .el-loading-mask .is-fullscreen, .theme-light .vs-c-task-list, *::-webkit-scrollbar-thumb:horizontal { background-image: url("data:image/png;base64,") !important; background-origin: padding-box !important; background-attachment: scroll !important; background-repeat: repeat !important; background-size: auto !important; background-position: left top !important; }
.theme-light .vs-c-col__head { color: #d4d4d4; background-color: #3e0f00; border-bottom: 1px solid #3c3c3c; }
.theme-light .vs-l-project__menu { background-color: #151515; }
.theme-light .vs-l-project__menu .vs-c-list>li .vs-c-site-logo.router-link-active { background: #3e0f00 !important; }
.theme-light .vs-c-btn .vs-c-btn--primary, .theme-light .vs-l-project__title .vs-u-cursor--default { background-color: #226f5a; }
.theme-light .vs-c-task-list .vs-is-empty, .theme-light .vs-is-items-pagination-active__text { text-shadow: none; }
.theme-light .vs-c-task-card__title .vs-task-card__title-edit { background-color: inherit; }
.theme-light .vs-c-list .vb-content, .theme-light .vs-l-sidebar > .vs-c-list, .theme-light .vs-l-sidebar > .vs-c-sidebar-boards, .theme-light .vs-l-sidebar > .vs-l-sidebar__user { background-color: #0e0e0e; }
.theme-light .vs-l-project__menu { border-left: 1px solid #3c3c3c; }
.theme-light .vs-c-task-card-assignees img { box-shadow: 0 0 0 2px #545453; }
.theme-light .vs-l-project__header.vs-l-project__header__active, .el-dropdown { background-color: #1e3a32; }
.theme-light .vs-l-project__header.vs-l-project__header__active .vs-l-project__title:after { border-color: transparent transparent #1e3a32; }
.theme-light .vs-c-label--info { background-color: #5020e4; }
.theme-light .vs-c-modal__body-left, .theme-light .vs-c-modal__body-right, .theme-light .vs-c-modal__header, .theme-light .vs-c-modal__body, .theme-light .vs-c-modal__footer.vs-c-modal-footer--followers { background-color: #020202; }
.theme-light .vs-c-modal__footer.vs-c-modal-footer--followers { width: 100%; }
.theme-light .vs-c-timeline__icon, .theme-light .el-dropdown-menu__item, .theme-light .vs-c-task-followers-actions .vs-c-dropdown, .theme-light .vs-c-task-followers-actions .vs-c-dropdown { color: #a09c9c; background-color: #232222; }
.theme-light .el-textarea__inner::-webkit-input-placeholder, .theme-light .el-textarea__inner::-moz-placeholder, .theme-light .el-textarea__inner:-ms-input-placeholder, .theme-light .el-textarea__inner:-moz-placeholder { color: #a09c9c; }
.theme-light .el-textarea__inner { background-color: #0e0e0e; }
.theme-light .vue-simple-markdown { color: #696969; }
.theme-light .el-loading-mask { background-color: hsla(0, 0%, 0%, 0.9); }
.theme-light .vs-c-modal--task { top: 5%; left: 5%; width: 90%; height: 90%; }
.theme-light .vs-c-modal--task .vs-c-modal__body { height: 90%; }
.theme-light .vs-c-modal__body { max-height: 90%; color: #fff; }
.theme-light .vs-c-modal--task .vs-c-modal__body-left { width: 100%; }
.theme-light .vs-c-modal--task .vs-c-modal__body-right { width: 40%; }
.theme-light .el-textarea__inner, .theme-light .vs-c-assignee .vs-c-performer-name p, .theme-light .vue-simple-markdown, .theme-light .vs-c-project-search-results-separator, .theme-light .vs-c-task-card--search .vs-c-task-card__info, .theme-light .vs-c-task-card--search .vs-c-task-card__title, .theme-light .vs-c-filter-dropdown-menu .vs-c-filter-control-menu .vs-c-assignee, .theme-light .vs-c-filter-dropdown-menu .vs-c-filter-state>span, .theme-light .vs-c-dropdown-score .el-row .vs-c-dropdown-wrapper, .theme-light .vs-c-dropdown-score .el-row .vs-c-dropdown-wrapper>a, .theme-light .vs-c-project-search.vs-is-active .vs-c-project-search__results.vs-is-shown, .theme-light .vs-c-task-dropdown__heading, .theme-light .vs-c-asignee-name, .theme-light .vs-c-task-dropdown__item { color: #fff; }
.theme-light .el-autocomplete-suggestion.is-loading li:hover, .theme-light .el-dropdown-menu { background-color: #1f1f1f; }
.theme-light .vs-c-task-card .vs-is-task-done .vs-c-not-visible-controls { background: none; }
.theme-light .vs-c-project-search.vs-is-active .vs-c-project-search__results.vs-is-shown, .theme-light .vs-c-project-search-results-separator span, .theme-light .vs-c-controls.active .vs-c-task-dropdown-wrapper, .theme-light .vs-c-task-dropdown__heading { background: #1e1e1e; }
.theme-light .vs-c-project-search-results-board-item:hover, .theme-light .vs-c-project-search-results-board-item:hover span { color: #000; }
.theme-light .vs-c-task-card-search-header { color: #151515; background: #808080; }
.theme-light .vs-c-dropdown.vs-c-filter-dropdown-menu { background: #151515; box-shadow: 0 0 13px -1px #666; }
.theme-light div:nth-child(1) > div.vs-c-col__head,
.theme-light div:nth-child(11) > div.vs-c-col__head{ background-color: #8c0000;}
.theme-light div:nth-child(2) > div.vs-c-col__head,
.theme-light div:nth-child(12) > div.vs-c-col__head { background-color: #8c0061;}
.theme-light div:nth-child(3) > div.vs-c-col__head,
.theme-light div:nth-child(13) > div.vs-c-col__head { background-color: #56008c;}
.theme-light div:nth-child(4) > div.vs-c-col__head,
.theme-light div:nth-child(14) > div.vs-c-col__head { background-color: #00118c;}
.theme-light div:nth-child(5) > div.vs-c-col__head,
.theme-light div:nth-child(15) > div.vs-c-col__head { background-color: #008c8c;}
.theme-light div:nth-child(6) > div.vs-c-col__head,
.theme-light div:nth-child(16) > div.vs-c-col__head { background-color: #008c1e;}
.theme-light div:nth-child(7) > div.vs-c-col__head,
.theme-light div:nth-child(17) > div.vs-c-col__head { background-color: #438c00;}
.theme-light div:nth-child(8) > div.vs-c-col__head,
.theme-light div:nth-child(18) > div.vs-c-col__head { background-color: #8c8800;}
.theme-light div:nth-child(9) > div.vs-c-col__head,
.theme-light div:nth-child(19) > div.vs-c-col__head { background-color: #8c5a00;}
.theme-light div:nth-child(10) > div.vs-c-col__head,
.theme-light div:nth-child(20) > div.vs-c-col__head { background-color: #8c1a00;}
*::-webkit-scrollbar { width: 8px; }
*::-webkit-scrollbar-thumb { border: solid 1px transparent; -webkit-border-radius: 10px; -webkit-box-shadow: inset 0 0 0 1px rgb(79, 79, 79), inset 0 0 0 6px rgb(102, 102, 102); }
*::-webkit-scrollbar:horizontal { height: 25px; }
*::-webkit-scrollbar-thumb:horizontal { border: 9px solid transparent; border-radius: 50px; }
*::-webkit-scrollbar-thumb:hover { -webkit-box-shadow: inset 0 0 0 1px rgb(90, 90, 90), inset 0 0 0 6px rgb(110, 110, 110); }
.theme-light .vs-c-filter-dropdown-menu .vs-c-filter-control-menu .vs-c-assignee:hover { background: #aeaeae; color: #000; }
.theme-light .vs-c-controls .vs-c-task-dropdown-wrapper { box-shadow: 0 4px 10px rgba(72, 72, 72, 0.5), -6px 11px 11px -1px rgba(72, 72, 72, 0.5), -10px 1px 3px 0 rgba(72, 72, 72, 0.5), 10px 1px 3px 0 rgba(72, 72, 72, 0.5) }
.theme-light .vs-c-task-roi .vs-c-roi-tooltip { background: #666; }
.theme-light .vs-c-task-followers-actions .vs-c-dropdown .vs-c-assignee:hover { background-color: #e4e8f1; }
.theme-light .vs-c-task-followers-actions .vs-c-dropdown .vs-c-assignee:hover .vs-c-asignee-name { color: #48576a; }


div.vs-c-reach-textarea > div > div.vs-u-gap-bottom.el-textarea > textarea { background: #212121; color: #bbbbbb !important; border: 1px solid #424242 !important; }
div.vs-c-reach-textarea > div > div.vs-u-gap-bottom.el-textarea > textarea:focus { border: 1px solid #9c6c2a !important; }
div.vs-c-reach-textarea > div > div.vs-u-gap-bottom.el-textarea > textarea:focus::selection { color: #0487ff; background-color: #1a182d; }

.theme-light .vs-c-task-card-new .item-textarea textarea { background: #212121; color: #bbbbbb !important; border: 1px solid #424242 !important;}
.theme-light .vs-c-task-card-new .item-textarea textarea:focus {  border: 1px solid #9c6c2a !important; }
.theme-light .vs-c-task-card-new .item-textarea textarea:focus::selection { color: #0487ff; background-color: #1a182d; }

div.vs-c-task__header > div > div.vs-u-gap-bottom.vs-c-modal-mentioning-wrapper > div > textarea { background: #212121; color: #bbbbbb !important; border: 1px solid #424242 !important;}
div.vs-c-task__header > div > div.vs-u-gap-bottom.vs-c-modal-mentioning-wrapper > div > textarea:focus {  border: 1px solid #9c6c2a !important; }
div.vs-c-task__header > div > div.vs-u-gap-bottom.vs-c-modal-mentioning-wrapper > div > textarea:focus::selection { color: #0487ff; background-color: #1a182d; }
