css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("old.reactor.cc") {
  div#header {
      width: 90%;
  }
  div#page {
      width: 90%;
  }
  div#content {
      width: 100%;
  }
  div#tagArticle {
      width: 100%;
  }
  div#searchBar{
    display: flex;
  }
  form#searchform{
    float: right;
  }
  div#submenu{
    width: 75%;
  }
  div#blogName{
    max-width: 85%;
  }
  div.tagname{
    max-width: 60%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
  div#searchmenu{
    width: 25%;
  }
  div#sidebar {
      right: -285px;
      width: 259px;
      padding-right: 26px;
      margin-top: 1px;
      padding-top: 10px;
      transition: 0.2s;
      position: absolute;
      background-color: white;
      padding-left: 10px;
      z-index: 10;
  }
  div#sidebar:hover {
      right: -15px;
      box-shadow: -6px 0px 20px -5px rgba(0, 0, 0, 0.47);
  }
  div#contentinner {
      width: 98%;
  }
  div#showCreatePost {
      width: 100%;
  }
  div#add_post_holder {
      width: 100%;
  }
  div[id^=postContainer] {
      box-shadow: 10px 0px 20px -10px rgba(0, 0, 0, 0.4);
  }
  div#navcontainer {
      background-size: 100%;
  }
  div#searchBar {
      background-size: 100%;
  }
  div.blogs a img {
      width: 100%;
  }
  textarea#add_post_text {
      width: 99%;
      border: 1px solid #bbbbbb;
      background: none;
  }
  input[name="tag"], input[name="header"] {
      width: 99% !important;
      margin-top: -6px !important;
  }
  .article .ufoot {
    width: 100% !important;
  }
  .image {
    text-align: center !important;
  }
  .post_content table {
    margin: 0 auto;
  }
  .post_content_expand{
    width: 100% !important;   
  }
  .comment_list{
    border-left: 1px solid #f4f4f4;
  }
  .comment_list:hover{
    border-left: 1px dashed #d5d5d5;
  }
}
