css:
@-moz-document domain(www.europans.com) {

/*EVERYWHERE*/

*{-webkit-font-smoothing:antialiased}

p,blockquote{hyphens:auto}

a{text-decoration-skip:ink !important}

/*IPS SPECIFIC*/


.ipsApp .cke_contents,
.ipsComment_content .ipsType_richText,
.ipsType_normal.ipsType_richText{
    font-family: Georgia,'Times New Roman',serif;
    font-size: 18px;
    letter-spacing: -0.01rem;
    line-height: 1.8;
}

.ipsType_richText img,
.ipsType_richText .ipsImage{
	border-radius:0.4rem;
}

.ipsType_richText hr {
	border: none;
	border-top: medium double #ccc;
	color: #ccc;
	padding: 0;
	padding-bottom:1.6rem;
	text-align: center;
}

.ipsType_richText hr:before {
	background: white;
	content: url(http://i5.ifrm.com/1027/100/emo/star.gif);
	display: inline-block;
	font-size: 1.5em;
	padding: 0 0.25em;
	position: relative;
	top: -0.6em;
}

}
