css:
@-moz-document domain("acm.two.moe"), domain("210.38.224.114") {
* {
		font-family: Monaco, Consolas, "Microsoft YaHei", monospace !important;
	}

	/* main */

	.container {
		width: 100%;
		padding: 0;
	}

	/* header */

	@media screen and (max-width: 814px) {
		.navbar-header {
			float: none;
			margin-right: -15px !important;
		}

		.navbar-toggle {
			display: block;
			float: right;
		}

		.navbar-collapse.collapse {
			display: none !important;
		}

		.navbar-collapse.collapse.in {
			display: block !important;
		}
	}

	.navbar {
		width: 100%;
		margin: 0;
		background: #0593d3;
		border: none;
		border-radius: 0;
		opacity: 1;
	}

	.container-fluid {
		padding-left: 1%;
		padding-right: 1%;
	}

	.navbar a {
		color: #fff !important;
	}

	.open .dropdown-toggle,
	.navbar a:hover,
	.active a,
	.collapsed:hover,
	.navbar-toggle[aria-expanded="true"] {
		background: #0482ba !important;
	}

	.open .dropdown-toggle,
	.navbar a:active,
	.active a,
	.navbar-toggle[aria-expanded="true"] {
		box-shadow: inset 0 0 2em rgba(0, 0, 0, 0.075) !important;
	}

	.navbar-toggle {
		border: none !important;
	}

	.icon-bar {
		background: #fff !important;
	}

	.dropdown-menu {
		background: #0593d3;
		border: none;
		box-shadow: inset 0 0 2em rgba(0, 0, 0, 0.075) !important;
	}

	/* index */

	.jumbotron {
		background: none;
		padding: 0 15% !important;
	}

	@media screen and (max-width: 814px) {
		.jumbotron {
			padding: 0 5% !important;
		}
	}

	#broadcast {
		margin-top: 0 !important;
		color: red;
		background: none;
	}

	div.container > div > table {
		width: 100% !important;
		margin-left: auto;
		margin-right: auto;
	}

	.form-inline > *,
	#simform > * {
		display: inline-block !important;
	}

	.toprow {
		background: #0593d3;
		color: white;
	}

	.btn {
		color: white;
		text-shadow: none;
		background: #00BCD4;
		border: none;
	}

	.btn-success {
		background: #4caf50;
	}

	.btn-info {
		background: #5bc0de;
	}

	.btn-warning {
		background: #ff9800;
	}

	.btn-danger {
		background: #db2828;
	}
}

@-moz-document regexp(".*?(acm.two.moe|210.38.224.114)[^/]*/?") {
/* index */

	div.container > div > table tr > th:first-child,
	div.container > div > table tr > td:first-child {
		display: none;
	}

	div.container > div > table td img {
		width: 100%;
		height: auto;
	}
}

@-moz-document regexp(".*?(acm.two.moe|210.38.224.114).*?/problemset.php.*") {
/* problemset */

	.evenrow td[width="5"] {
		display: none;
	}

	td[colspan]:nth-last-child(2) {
		text-align: left;
	}

	td[colspan]:last-child {
		text-align: right;
	}

	.search-query {
		width: 50% !important;
	}

	button.form-control {
		width: auto;
	}

	th {
		text-align: center;
	}

	.headerSortUp,
	.headerSortDown {
		background: #0482ba;
		box-shadow: inset 0 0 2em rgba(0, 0, 0, 0.075);
	}
}

@-moz-document regexp(".*?(acm.two.moe|210.38.224.114).*?/problem.php.*") {
/* problem */

	.content {
		display: block;
		padding: 9.5px;
		margin: 0 0 10px;
		color: #333;
		word-break: break-all;
		word-wrap: break-word;
		background-color: #f5f5f5;
		border: 1px solid #ccc;
		border-radius: 4px;
	}
}

@-moz-document regexp(".*?(acm.two.moe|210.38.224.114).*?/status.php.*") {
/* status */

	.input-append {
		width: 100%;
		margin-left: auto;
		margin-right: auto;
	}

	input[type="text"],
	select {
		width: 15% !important;
	}

	input[type="submit"] {
		width: auto;
	}
}

@-moz-document regexp(".*?(acm.two.moe|210.38.224.114).*?/loginpage.php.*") {
/* login */

	#login > div * {
		float: none !important;
		margin: 0 !important;
	}

	.form-group {
		margin-left: auto !important;
		margin-right: auto !important;
	}

	.col-sm-4,
	.col-sm-8 {
		display: inline-block !important;
	}

	#login > div:nth-child(3) {
		text-align: center;
	}

	label.col-sm-4 {
		width: 35%;
		text-align: right;
	}

	div.col-sm-8 {
		width: 30%;
	}

	@media screen and (max-width: 767px) {
		div.col-sm-8 {
			width: 50%;
		}
	}

	div.col-sm-4 {
		width: 24%;
	}

	#login > div:nth-child(4) {
		text-align: center;
	}

	#login > div:nth-child(4) > div {
		width: 15%;
	}

	@media screen and (max-width: 767px) {
		#login > div:nth-child(4) > div {
			width: 30%;
		}
	}

	.btn-block {
		color: #fff !important;
		background: #0593d3;
	}

	.btn-block:hover,
	.btn-block:focus {
		background: #0482ba;
		box-shadow: inset 0 0 2em rgba(0, 0, 0, 0.075);
	}
}

@-moz-document regexp(".*?(acm.two.moe|210.38.224.114).*?/ranklist.php.*") {
/* rank */

	.form-control {
		width: auto;
	}
}
