css:
@-moz-document domain(hightech.fm) {

body{background:#000}
a {color:#a9a9a9}

.stk-image{opacity:0.2!important;transition:opacity .7s!important}
.stk-image:hover{opacity:1!important}

.ui-widget.page-content__widget,.banner--single_sidebar{display:none!important}
.page-header{background:#0e0e0e}
.main-nav__link,.heading{color:#6b6b6b}
.ui-search__icon{fill:#484848}
.theme-category--color_black{color:#404040}
.news-items__list--border,.news--border{border-bottom:3px solid #1d1d1d}


.typography p,.stk-list-item{color:#999!important;transition:color .7s!important}
.typography p:hover{color:#bbb!important}
.typography h1, .typography h2, .typography h3, .typography h4, .typography h5, .typography h6{color:#888!important;transition:color .7s!important}
.typography h1:hover,.typography h2:hover,.typography h3:hover,.typography h4:hover,.typography h5:hover,.typography h6:hover{color:#ff3e24!important}

.stk-my-article-name,a.items-more__title{color:#6f6f6f!important;transition:color .7s!important}
.stk-my-article-name:hover,.stk-list-item:hover{color:#ff3e24!important}
.stk-my-line{background-color:#171717!important;margin:0 0 20px!important}
.stk-my-article-tag,.theme-category--color_black{color:#525252!important}
.items-more__list-item {border-bottom: 3px solid #262626}

.user--post_author .user__name{color:#a7a7a7}
.ajax-post__container,.page-content .stk-post{background-color:#101010;color:rgb(226, 225, 224)}
.lead {color:#757575}
.ui-socials__item{border-color:#202020}
.ui-socials__item:after,.ui-socials__item:before{background:#202020}
.ui-socials__icon{filter:brightness(.5)}
.page-content__lead{margin-bottom:7px}
.page-content__head{margin-bottom:14px}

[class*="_separator_basic_divider"]::before{border-top-color:#202020!important}
[class*="__separator_divider"]::before{border-top-width:1px!important;border-top-color:#1b1b1b!important}
.stk-incut-article-name,.stk-incut-article-tag{color:#676767!important}
.stk-incut-line{background-color:#222222!important}
.wp-exclude-emoji{background:transparent!important;font-size:20px!important}

}
