css:
@-moz-document domain("www.truenovelist.com") {
*::-webkit-scrollbar {
    width: 7px !important;
}
 
*::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3) !important;
}
 
*::-webkit-scrollbar-thumb {
  background-color: #4e5660 !important;
  outline: 1px solid slategrey !important;
}

.ui-layout-resizer {
    background:transparent !important;
}

.ui-layout-pane {
    border-color:transparent !important;
}

#menuHeading > h4 > a, .panel-title {
    text-transform:uppercase;
    font-size:20px;
    font-weight:700;
}

body, html, h1, h2, h3, h4, h5, .mediumEditor, .mediumEditor h1, .mediumEditor h2, .mediumEditor h3, .mediumEditor h4, .mediumEditor h5, .mediumEditor h6, .mediumEditor p, .mediumEditor span, .mediumEditor div {
    font-family:'Bitter' !important;
    font-size:16px !important;
    line-height:1.6 !important;
    --accent:#3392e3 !important;
}

.mediumEditor h1, .mediumEditor h2, .mediumEditor h3, .mediumEditor h4, .mediumEditor h5, .mediumEditor h6, .mediumEditor p, .mediumEditor span, .mediumEditor div {
    font-size:14px !important;
}

#sceneTitle {
    font-size:20px !important;
    font-weight:bold;
}

#word-count, .vakata-context li>a>i, .vakata-contextmenu-sep {
    display:none !important;
}

.tooltip {
    text-transform:none !important;
    padding:5px;
    content:'blargh' !important;
}

#demo_menu, #menu {
    font-size:14px !important;
}

.vakata-context, .vakata-context, .vakata-context ul {
    font-size:12px !important;
    border:none !important;
    box-shadow:none !important;
    padding:0px !important;
}

.vakata-context li > a {
    padding: 10px !important;
    text-shadow:none !important;
}

.vakata-context > li > ul {
    bottom:-26px !important;
}

.jstree-proton-small .jstree-anchor {
    border-radius:0px !important;
}

.treeitem,.jstree-children {
    margin:5px !important;
}

.jstree-proton .jstree-clicked, .jstree-hovered, .js-tree-anchor:hover {
    box-shadow:none !important;
    background:var(--accent) !important;
    color:#fff !important;
}

.jstree-proton-small .jstree-anchor {
    padding:0px 5px 5px 5px !important;
    line-height:30px !important;
    height:30px !important;
}

.glyphicon, .jstree-ocl {
    line-height:25px !important;
    height:30px !important;
}

.glyphicon {
    margin-right:5px !important;
}

.jstree-ocl {
    background-position:-71px 0px !important;
}    

.vakata-context li>a:hover {
    box-shadow:none !important;
    background-color:#0a6aa1 !important;
    color:#fff !important;
}

.jstree-proton .jstree-anchor, .jstree-proton .jstree-wholerow {
    transition-duration:150ms !important;
}

.jstree-children > li {
    margin-top:-5px !important;
}

.btn, .btn:hover {
    border:none !important;
    text-shadow:none !important;
}

.btn {
    background:#4e5660 !important;
    transition-duration:150ms !important;
}

.btn:hover {
    background:var(--accent) !important;
    transition-duration:150ms !important;
}

#menu > .list-group > .list-group-item > a, .panel-title > a, #logoutForm a {
    transition-duration:150ms !important;
}

#menu > .list-group > .list-group-item > a:hover, .panel-title > a:hover, #logoutForm a:hover {
    color:var(--accent) !important;
    transition-duration:150ms !important;
}

a:focus, a:active, a:visited, a:hover {
    text-decoration:none !important;
    outline:none !important;
}

.small {
    margin-top:10px !important;
    font-size:12px !important;
}

.ui-layout-west .panel-heading .panel-title {
    padding-top:5px !important;
}

#btnCreateScene {
    font-size:11px !important;
    padding:10px !important;
    line-height:1.5px !important;
    margin-top:-2px !important;
}

.jstree-container-ul {
    padding:5px !important;
}

.panel {
    box-shadow:none !important;
    border:none !important;
}
}
