css:
@-moz-document domain("codeanywhere.com") {
/*
Solarized theme for code-mirror
http://ethanschoonover.com/solarized
*/

/*
Solarized color palette
http://ethanschoonover.com/solarized/img/solarized-palette.png
*/

.solarized.base03 { color: #002b36; }
.solarized.base02 { color: #073642; }
.solarized.base01 { color: #586e75; }
.solarized.base00 { color: #657b83; }
.solarized.base0 { color: #839496; }
.solarized.base1 { color: #93a1a1; }
.solarized.base2 { color: #eee8d5; }
.solarized.base3  { color: #fdf6e3; }
.solarized.solar-yellow  { color: #b58900; }
.solarized.solar-orange  { color: #cb4b16; }
.solarized.solar-red { color: #dc322f; }
.solarized.solar-magenta { color: #d33682; }
.solarized.solar-violet  { color: #6c71c4; }
.solarized.solar-blue { color: #268bd2; }
.solarized.solar-cyan { color: #2aa198; }
.solarized.solar-green { color: #859900; }

/* Color scheme for code-mirror */

.CodeMirror {
  line-height: 1.45em;
  color-profile: sRGB;
  rendering-intent: auto;
}
.CodeMirror {
  color: #839496;
  background-color: #002b36;
  text-shadow: #002b36 0 1px;
}

.CodeMirror .CodeMirror-widget {
  text-shadow: none;
}

.CodeMirror .cm-header { color: #586e75; }
.CodeMirror .cm-quote { color: #93a1a1; }

.CodeMirror .cm-keyword { color: #cb4b16; }
.CodeMirror .cm-atom { color: #d33682; }
.CodeMirror .cm-number { color: #d33682; }
.CodeMirror .cm-def { color: #2aa198; }

.CodeMirror .cm-variable { color: #839496; }
.CodeMirror .cm-variable-2 { color: #b58900; }
.CodeMirror .cm-variable-3, .cm-s-solarized .cm-type { color: #6c71c4; }

.CodeMirror .cm-property { color: #2aa198; }
.CodeMirror .cm-operator { color: #6c71c4; }

.CodeMirror .cm-comment { color: #586e75; font-style:italic; }

.CodeMirror .cm-string { color: #859900; }
.cm-s-solarized .cm-string-2 { color: #b58900; }

.CodeMirror .cm-meta { color: #859900; }
.CodeMirror .cm-qualifier { color: #b58900; }
.CodeMirror .cm-builtin { color: #d33682; }
.CodeMirror .cm-bracket { color: #cb4b16; }
.CodeMirror .CodeMirror-matchingbracket { color: #859900; }
.CodeMirror .CodeMirror-nonmatchingbracket { color: #dc322f; }
.CodeMirror .cm-tag { color: #93a1a1; }
.CodeMirror .cm-attribute { color: #2aa198; }
.CodeMirror .cm-hr {
  color: transparent;
  border-top: 1px solid #586e75;
  display: block;
}
.CodeMirror .cm-link { color: #93a1a1; cursor: pointer; }
.cm-s-solarized .cm-special { color: #6c71c4; }
.cm-s-solarized .cm-em {
  color: #999;
  text-decoration: underline;
  text-decoration-style: dotted;
}
.CodeMirror .cm-strong { color: #eee; }
.CodeMirror .cm-error,
.CodeMirror .cm-invalidchar {
  color: #586e75;
  border-bottom: 1px dotted #dc322f;
}

div.CodeMirror-selected { background: #073642; }
.CodeMirror ::selection { background: rgba(7, 54, 66, 0.99); }
.CodeMirror-line::-moz-selection, .cm-s-dark .CodeMirror-line > span::-moz-selection, .cm-s-dark .CodeMirror-line > span > span::-moz-selection { background: rgba(7, 54, 66, 0.99); }


/* Editor styling */



/* Little shadow on the view-port of the buffer view */
.CodeMirror {
  -moz-box-shadow: inset 7px 0 12px -6px #000;
  -webkit-box-shadow: inset 7px 0 12px -6px #000;
  box-shadow: inset 7px 0 12px -6px #000;
}

/* Remove gutter border */
.CodeMirror .CodeMirror-gutters {
  border-right: 0;
}

/* Gutter colors and line number styling based of color scheme */

/* Dark */
.CodeMirror .CodeMirror-gutters {
  background-color: #073642;
}

.CodeMirror .CodeMirror-linenumber {
  color: #586e75;
  text-shadow: #021014 0 -1px;
}





/* Common */
.CodeMirror .CodeMirror-linenumber {
  padding: 0 5px;
}
.CodeMirror .CodeMirror-guttermarker-subtle { color: #586e75; }
.CodeMirror .CodeMirror-guttermarker { color: #ddd; }

.CodeMirror .CodeMirror-gutter .CodeMirror-gutter-text {
  color: #586e75;
}

/* Cursor */
.CodeMirror .CodeMirror-cursor { border-left: 1px solid #819090; }

/* Fat cursor */
.CodeMirror.cm-fat-cursor .CodeMirror-cursor { background: #586e75; }
.CodeMirror .cm-animate-fat-cursor { background-color: #586e75; }

/* Active line */
.CodeMirror .CodeMirror-activeline-background {
  background: rgba(255, 255, 255, 0.06);
}

}
