css:
@-moz-document domain("beta.easydo.cn"), regexp("^https?://(.*\\.)?beta\\.easydo\\.cn(.)*$") {
.card_blocks {
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
}
.card_blocks:hover {
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
}
.work-list .card_blocks, .work-list .card_blocks:hover {
    box-shadow: none;
}
#unarranged div.panel-body ul li div, #todo-panel-body-week div.panel-body ul li div, #todo-panel-body-month div.panel-body ul li div {
    transition: .15s;
}
#header {
    box-shadow: 0 0 10px;
}
.tabs li a, #search-box,
select, input, textarea,
.select2-container--default .select2-selection--single,
.select2-container--default .select2-selection--multiple,
.select2-container--default .select2-selection--multiple .select2-selection__choice,
.button, .searchBox, .personField,
.listingBar a.selected, .listingBar a.select:hover, .listingBar a.select, .state-label,
#layout-main-content > div, #main-box-shadow, #memo-panel, .TGaddcomment,
.card_blocks, .change-task-priority, .task-priority > div,
#unarranged div.panel-body ul li, #todo-panel-body-week div.panel-body ul li, #todo-panel-body-month div.panel-body ul li, #memo-textarea, .list-group-item:first-child,
#chatpanel, .chat-head,
#simplemodal-container, .popup-container,
.app-links, .list-group-item:last-child, .input-prepend .add-on {
    border-radius: 0 !important;
}
.select2-container--default .select2-selection--multiple .select2-selection__choice {
    padding: 0 4px 0 0;
}
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove {
    padding: 0 5px;
    border-right: 1px solid #ccc;
    margin-right: 4px;
}
.select2-container--default .select2-selection--multiple .select2-selection__choice__remove:hover {
    background: #aaa;
}

.live-search {
    margin-top: .3em;
    box-shadow: 0 0 10px rgba(0,0,0,0.4);
}

a.modalCloseImg.b-close {
    background: #000;
    height: 22px;
    width: 22px;
    border: 2px solid #fff;
    border-radius: 100%;
    box-shadow: 0 0 8px rgba(0, 0, 0, 1);
    line-height: 24px;
    font-size: 24px;
    text-align: center;
    text-decoration: none;
}

a.modalCloseImg.b-close::before {
    content: 'Ã—';
    color: #fff;
}

a.modalCloseImg.b-close:hover {
    box-shadow: 0 0 8px rgba(0, 0, 0, .7);
}
#chatpanel-tray {
    box-shadow: 0 0 6px rgba(0,0,0, .4);
    border-radius: 0;
}

#chatpanel, .chat-head {
    border: none;
}
#chatpanel {
    box-shadow: 0 -1px 6px rgba(0, 0, 0, 0.7);
}

span.choice {
    padding: 0 0 0 5px;
    border-radius: 0;
}
span.choice .remove {
    padding: 0 4px;
    margin: 0;
    border-left: 1px solid #ccc;
}
span.choice .remove:hover {
    background: #ccc;
}
.content-select .selected-form {
    padding: 6px;
}
.select2-container--default .select2-search--inline .select2-search__field {
    height: auto !important;
}
.select2-container .select2-search--inline .select2-search__field {
    margin-top: inherit !important;
}
.select2-container--default .select2-selection--multiple .select2-selection__rendered {
    padding: 5px;
}

/** Select field items (attachments) **/
.select-field-items {
    margin: 2px 0 6px 0;
}
.select-field-items>div {
    padding: 0 0 0 5px;
    display: inline-block;
    background: #e4e4e4;
    border: 1px solid #ccc;
    height: 24px;
    overflow: hidden;
}
.select-field-items>div>a:first-child {
    padding-right: 5px;
    line-height: 1em;
    padding: 5px 0;
    color: #2374a0;
}
.select-field-items>div>a.deselect {
    line-height: 1em;
    height: 16px;
    display: inline-block;
    vertical-align: middle;
    text-decoration: none;
    padding: 5px;
    border-left: 1px solid #ccc;
    color: #999;
}
.select-field-items>div a.deselect:hover {
    background: #aaa;
    color: #333;
}

/** selectedItem (persons) **/
.selectedItem {
    margin: 2px;
    border: 1px solid #ccc;
    background: #e9e9e9;
    display: inline-block;
}
.selectedItem a.profile_info {
    display: inline-block;
    padding: 0 5px;
    margin-right: 0;
}
.selectedItem .deselect {
    display: inline-block;
    border-left: 1px solid #ccc;
    padding: 0 5px;
    color: #999;
    text-decoration: none;
}
.selectedItem .deselect:hover {
    color: #333;
    background: #999;
}

/** Loading animation **/
#loader,
#loader:before,
#loader:after {
  background: #1992d4;
  -webkit-animation: load1 1s infinite ease-in-out;
  animation: load1 1s infinite ease-in-out;
  width: 1em;
  height: 4em;
}
#loader {
  color: #1992d4;
  text-indent: -9999em;
  margin: 88px auto;
  position: relative;
  font-size: 11px;
  -webkit-transform: translateZ(0);
  -ms-transform: translateZ(0);
  transform: translateZ(0);
  -webkit-animation-delay: -0.16s;
  animation-delay: -0.16s;
}
#loader:before,
#loader:after {
  position: absolute;
  top: 0;
  content: '';
}
#loader:before {
  left: -1.5em;
  -webkit-animation-delay: -0.32s;
  animation-delay: -0.32s;
}
#loader:after {
  left: 1.5em;
}

@keyframes load1 {
  0%,
  80%,
  100% {
    box-shadow: 0 0;
    height: 4em;
  }
  40% {
    box-shadow: 0 -2em;
    height: 5em;
  }
}
}
