css:
@-moz-document domain(dotnetfiddle.net) {

element.style 

#vsc-initialized {background-color: #2D2D30}

span.cm-keyword{color: #569CD5 !important}
span.cm-variable{color: #DCDCDC !important}
span.cm-number{color: #B5CEA8 !important}
span.cm-comment{color: #57A64A !important}
span.cm-string{color: #D69D85 !important}
span.cm-operator{color: #569CD5 !important}

.navbar-center-container{background:#2D2D30}
.name-container{background-color: #2D2D30}
body {color: #FFFFFF; background-color: #2D2D30}
input[type="text"], textarea {color: #FFFFFF; background-color : #343B41;}
.access-type-container, .status-line .counters{background-color: #2D2D30}

.sidebar{background-color: #2D2D30}
.vsplitbar{background-color: #343B40}
.hsplitbar{background-color: #343B40}
.navbar .btn{background: #343B40}

.CodeMirror-gutters{background-color: #1E1E1E}
.CodeMirror-sizer{background-color: #2D2D30}
.CodeMirror-linenumber{color: #A5A5A5}

#output{color: #FFFFFF}
.output-container{background-color: #000000}

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th{background-color: #474749}
.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td{background-color: #474749}
table.dataTable tr.odd td.sorting_1{background-color: #474749}
table.dataTable tr.even td.sorting_1{background-color: #474749}


	@font-face {
	  font-family: 'Source Code Pro';
	  font-style: normal;
	  src: local('Source Code Pro'), local('SourceCodePro-Regular'), url(https://fonts.gstatic.com/s/sourcecodepro/v6/mrl8jkM18OlOQN8JLgasDy2Q8seG17bfDXYR_jUsrzg.woff2) format('woff2');
	  unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;}
	@font-face {
	  font-family: 'Source Code Pro';
	  font-style: normal;
	  src: local('Source Code Pro'), local('SourceCodePro-Regular'), url(https://fonts.gstatic.com/s/sourcecodepro/v6/mrl8jkM18OlOQN8JLgasD9V_2ngZ8dMf8fLgjYEouxg.woff2) format('woff2');
	  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215;}
	CodeMirror-code::-moz-selection { color: #404040 !important; }
	CodeMirror-code::selection { color: #404040 !important; }
	pre{color: #fff;}
	.CodeMirror{font-family: 'Source Code Pro';
				color: #fff !important;
				background: #1E1E1E;
				font-size: 95%;}
  
}
