css:
/* 


Создатель/редактор https://golos.io/@carpe-diem 


*/







@namespace url(http://www.w3.org/1999/xhtml);
@namespace svg url(http://www.w3.org/2000/svg);

@-moz-document domain("golos.io") {

body {
color: #bbbdbd; /*подчеркивание название прочитаных тем*/
/*background-image: url(http://flashwallpapers.com/wp-content/uploads/2015/10/light-umbrella-dark.jpg);*/
background-image: url(https://i.yapx.ru/U4fM.jpg);
background-position: center;
background-repeat: repeat-y, repeat-y;
background-position: left, right;
background-attachment: fixed, fixed;

/*background-color: #45484a; /*фон сайта*/

}
label {
color: #dedede; /*цвет заголовков во вкладке настройки*/

}
.PostFull__body {
font-family: Helvetica Neue,Arial,Roboto,sans-serif;
}
.PostsList__post_container {
background-color: RGBA(44, 59, 69, 0.78); /*цвет рамки статьи*/
}
.PostsList__post_container .PostFull {
background-color: RGBA(44, 59, 69, 0.78); /*фон основной части статьи*/
}
.Post {
background-color: RGBA(44, 59, 69, 0.78); /*цвет вокруг статьи*/
}


.Markdown {
font-family: Helvetica Neue,Arial,Roboto,sans-serif;
}
.Markdown h1, .Markdown h2, .Markdown h3, .Markdown h4, .Markdown h5, .Markdown h6, .ReplyEditor__body.rte h1, .ReplyEditor__body.rte h2, .ReplyEditor__body.rte h3, .ReplyEditor__body.rte h4, .ReplyEditor__body.rte h5, .ReplyEditor__body.rte h6 {
font-family: Roboto,sans-serif;
}

.Header__top {
background-color: #2f3f4a; /*цвет боковых полосок на уровне логотипа*/
border-bottom: 1px solid #07006e; /*цвет разделительной полосы*/
}
.Header__sub-nav {
background-color: #2f3f4a; /*цвет боковых полосок на уровне навигации*/
border-bottom: 1px solid #07006e;
}
.header .menu, .Header .menu {
background-color: #2f3f4a; /*цвет фона верхней панели*/
}
.button.success {
background-color: #fffb00;
}
.button.success:focus, .button.success:hover {
background-color: #fffb00; 
}

.HorizontalMenu .active > a {
color: #e0e0e0; /*цвет активной вкладки "лента", "новое", "актуальное", "популярное", "промо"*/
font-size: x-large;
}
.HorizontalMenu li > a:hover {
color: #737373; /*цвет при наведении на "лента", "новое", "актуальное", "популярное", "промо"*/
}
.Header__sub-nav li > a {
background-color: transparent;
color: #000; /*цвет "лента", "новое", "актуальное", "популярное", "промо"*/
font-size: large; /*размер шрифта: xx-small x-small small medium large x-large xx-large*/
}

.button.hollow:focus, .button.hollow:hover, .button.hollow.secondary:focus, .button.hollow.secondary:hover {
border-color: #050661;
color: #0cf; /*цвет кнопки "очистить" при наведении */
}
.button.hollow, .button.hollow.secondary {
border: 1px solid #050661;
color: #04a6cf; /*цвет кнопки "очистить"*/
}

a {
color: #d5d5de; /*цвет "Голос", ник, "Добавить пост"*/
outline: 0;
}
a:hover, a:focus {
color: #ededed; /*цвет "Голос", ник, "Добавить пост" при наведении курсора*/
}

.PostsIndex__topics {
border-left: 1px solid #010008; /*разделительная полоса от тегов в ленте*/
}
.PostSummary {
background-color: RGBA(69, 72, 74, 0); /*цвет фона постов*/ 
}
.PostSummary__body {
color: #8d949e; /*цвет превью постов*/
}
.PostSummary__header>h1>a {
color: #9c9c9c; /*цвет заголовков прочитаных постов*/
}
.PostSummary__header>h3>a { /*цвет заголовков непрочитаных постов*/
color: #d2d1e8;
}
.PostSummary__body a {
color: inherit;
}
.PostSummary__image {
border: 1px solid RGBA(22, 42, 69, 0.78); /*цвет линии вокруг картинки поста*/
}

.Markdown code, .ReplyEditor__body.rte code {
background-color: #272822;
}
code, kbd {
color: #F8F8F2;
}

.Comment__header-user {
color: #613f4e; /*цвет ников в комментариях*/
}
blockquote, blockquote p {
color: #3dcb5c;
}
.Topics__filter {
background-color: #404040; 
}
.Topics__filter__expand {
background-color: #606060;
}
.Topics__filter__expand:hover {
background-color: #707070;
}

.Header .menu .hamburger::after {
background: #0031d1; /*три полоски меню */
box-shadow: 0 7px 0 #2a51d1,0 14px 0 #5471d1;
}
.Header .menu .hamburger:hover::after {
background: #001352; /*три полоски при наведении меню */
box-shadow: 0 7px 0 #15296b,0 14px 0 #2b3a6b;
}

.button {
background-color: #698CC0;
}
.button:focus, .button:hover {
background-color: #546F98;
}

.Feedback a {
background-color: #698CC0;
color: #fefefe;
}
.Feedback a:focus, .Feedback a:hover {
background-color: #546F98;
color: #fefefe;
}

.TagList__horizontal a {
background: #757575;
color: #d9d9d9;
}

.TagList__horizontal a:hover {
background: RGBA(8, 71, 166, 0.84);
color: #b1d1e5;
}

.dropdown-pane {
box-shadow: 1px 1px 5px 0 rgba(215, 212, 212, 0.8);
background-color: #404040;
}
.VerticalMenu > li > a {
color: #ebebed; /*цвет символов вертикальконо меню "награды"*/
}
.VerticalMenu > li > a:hover {
background-color: #606060;
}

.DropdownMenu > .VerticalMenu {
background-color: #404040;
padding: 5px 0;
}
.menu.vertical > li {
padding: 0px;
}
.PostFull__footer .VerticalMenu > li > a {
color: #b48cdb; /*цвет ников проголосовавших за пост*/
}
.Voting .DropdownMenu ul li > a, .Voting .DropdownMenu ul li > span {
padding: 0.3rem 0.8rem;
}

.UserWallet__balance.UserWallet__balance.zebra {
background-color: #7d7d7d; /*чередование цвета во вкладке "кошелек"*/
}
.secondary {
color: #dee0e0; /*цвет текста во вкладке "кошелек"*/
}
.UserProfile__top-nav {
border-bottom: 1px solid #07006e; /*линия под "блог", "комментарии", "ответы", "награда"*/
background-color: RGBA(47, 63, 74, 0.78); /*фон меню "блог", комментарии" ...*/
font-family: 'Dancing Script', cursive;
font-size: large;
}
.WalletSubMenu > li > a {
color: #5a6a78; /*цвет вкладок в "кошелек"*/
}
.WalletSubMenu > li > a.active {
color: #065596; /*цвет активной вкладки в "кошелек"*/
}
.WalletSubMenu > li > a:hover {
color: #4ca5ed; /*цвет при наведении на вкладку в "кошелек"*/
}

.PostFull__time_author_category {
color: #698CC0;
}
.PostFull__footer .FoundationDropdownMenu__label, .PostFull__footer a {
color: #698CC0;
}
.PostFull__footer svg {
fill: #698CC0 !important;
}
.PostFull__views {
color: #698CC0;
}
.PostFull__footer .Reblog__button, .PostFull__reply, .PostFull__views {
border-right: 1px solid #698CC0;
}

table tbody, table tfoot, table thead {
background-color: #404040;
color: #d3d3d3;
border: 1px solid #999;
}
table tbody tr:nth-child(2n) {
background-color: #505050;
}

[type=color],[type=date],[type=datetime-local],[type=datetime],[type=email],[type=month],[type=number],[type=password],[type=search],[type=tel],[type=text],[type=time],[type=url],[type=week],textarea {
background: #202020;
color: #999999;
border-color: gray;
}
[type=color]:focus,[type=date]:focus,[type=datetime-local]:focus,[type=datetime]:focus,[type=email]:focus,[type=month]:focus,[type=number]:focus,[type=password]:focus,[type=search]:focus,[type=tel]:focus,[type=text]:focus,[type=time]:focus,[type=url]:focus,[type=week]:focus,textarea:focus {
box-shadow: 0 0 5px #5173b9;
background: #202020;
color: #999999;
border-color: #5f87d7;
}

.ReplyEditor__options select {
background-color: #333;
}
select.Topics {
background-color: #333;
color: #fefefe;
border: 1px solid #999;
}

.PostsList__post_overlay {
background-color: RGBA(255, 255, 255, 0.78);
}

.reveal {
background-color: #2a2a2a; 
}

.callout{
background-color: RGBA(13, 5, 26, 0.69);
color: #cfcfcf;
}
.UserProfile__top-nav .menu > li > a
{
color: #c5bcd4;
font-size: medium;
}

.UserProfile__banner > div.column, .UserProfile__banner > div.columns
{
background: none; /*убивает обложку*/

} 
.sell-color {
color: #ff2f16 !important;
}

.Author__dropdown .Author__name {
color: #698CC0;
}

.RichTextEditor__root___33zoV{
background-color: #030c0f; 
}


.Author__dropdown .Author__username {
color: #321b8f;
}

.Comment .highlighted {
background: #5a5a5a;
color: whitesmoke;
}
}
