css:
@-moz-document domain("fmovies.is") {
body { background-color: #111 !important; }
#body-wrapper { padding: 20px 0px 0px 0px !important; }
#header, a.morelink, #control, .paging .pagination>li:nth-child(2n+1) a, .cluetip-inner .inner { background: #232323 !important; }
#footer, .home-socials, .alert-primary, .row.hd-buttons, div#M156375Composite110024, #comment, #tags, .watching #control .report, .mt-md, .widget .widget-title.style2:after, .watching .col-right, .widget.hidden-sm.hidden-xs { display: none !important; }
.watching #body-wrapper .container { max-width: 1280px !important; padding: 0px !important; }
.watching #player { height: 720px !important; }
.watching #servers, .server.row, .watching #servers .server ul.episodes li a, .paging .pagination, .item, .widget-body { border-color: #232323 !important; }
#header #search form input { border-color: #333 !important; }
#header #menu>li .sub-menu, #header #menu>li.has-children:hover, .widget .widget-title .more { background: #333; }
#header #search form button { color: #333 !important; transition: all .1s ease-in-out 0s; }
#header #search form button:hover { color: #444 !important; }
.widget .widget-title .more:hover { background: #444 !important; }
}
