css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

domain("pcpartpicker.com"),
domain("au.pcpartpicker.com"), 
domain("de.pcpartpicker.com"),
domain("it.pcpartpicker.com"), 
domain("uk.pcpartpicker.com"), 
domain("es.pcpartpicker.com"), 
domain("nz.pcpartpicker.com"), 
domain("ca.pcpartpicker.com"){


header, .image-gallery
{
background:#555555 !important;
}

footer, .build-guides-hero, .details, 
.left-column, .right-column, .blog-post,
.featured-build, .registration-box-content,
#list_table tr td,
.merchant-preference-table td, .forum-list td
{
background:#444444 !important;
}

#explain_wattage, .part-list .zebra, 
.part-list-action-bar, .filter-list,
.wattage-actions a:hover,
.filter-list .est-wattage,
.ui-dialog-titlebar
{
background:#333333 !important;
}

input, textarea
{
background:#222222 !important;
}

.build-guides .btn-lgs, 
.registration-box-heading,
.system-build .action-bar-wrapper,
.part-list th, #saveobj_toc li a,
.blog-post .post-actions
{
background:#252835 !important;
}

.part-table tr, .ui-dialog
{
background-color:#444444 !important;
}
.delete div, .change-price
{
background-color:#2C85C5 !important;
}

.delete a, .no-custom-price
{
padding:12px !important;
border-radius:4px !important;
}

.create-btn, .action-box td
{
background-color:#252835 !important;
}

select, option
{
background:#000000 !important;
}

.part-list-empty-tip
{
background:#FF0000 !important;
}

.blog-post .post-actions
{
border-top: 1px #252835 solid !important;
}
.create-btn
{
border: 1px #252835 solid !important;
}

.filter-list
{
border: 1px #CACACA solid !important;
}

.action-box td
{
border: 1px #444444 solid !important;
}

select, option, input
{
border: none !important;
}

a
{
text-decoration:underline !important;
}

.primary-nav span, select, p, body,
.user-nav a, .language-selector,
.build-guides .btn-lgs, .details,
.component, .title, .title a, a, h4,
.registration-box-content, th, td,
.topic-datestamp, textarea, input,
.comment-data, .comment-points,
option
{
color:#FFFFFF !important;
}
}
