css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zadolba.li") {
  body, .nav-stories, div#xsite-nav, .footer {
    background: #282B2C;
    color: rgb(186, 189, 182);    
  }
  
  body .nav-stories, .nav>.nav-common, .sidebar .section h3 {
    filter: brightness(0.8);
  }  
  
  h2>a, li>a {
    color: rgb(186, 189, 182);
  }
  
  .story>.id {
    filter: brightness(0.5);
  }
  
  .actions .button {
    filter: brightness(0.8);    
  }
  
  div#xsite-nav>a {
    filter: brightness(0.5);    
  }
  
  .header .wrapper .logo {
    filter: invert(0.6);
  }
  
  .text a {
    color: burlywood;
  }
}
