css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {

#miniad_animation_container, .dying-links, #canvasnjuback, #wpladbottom, #wpladbox1, #wpladbox2, [data-dest="placeholder"] {
    display: none !important;
}

#nav{
    position: relative;
}

#nav + div {
    padding-top: unset;
}

.r-block.link-list-popular  {
    display: none !important;
}

.grid-right.m-reset-float.m-reset-margin.m-reset-width.m-hide {
    position: relative;
    width: 100%;
    display: block !important;
    margin-left: unset;
}

.article.clearfix.preview {
    display: none;
}

.article.clearfix.preview.dC {
    display: block;
}

.grid-main {
    margin-right: 0px !important;;
}

#nav li {
    display: block !important;
}

#add-new div {
    display: block;
}

.color-0.affect {
    color: #339933 !important;
}

.color-1.affect {
    color: #ff5917 !important;
}

.color-2.affect {
    color: #BB0000 !important;
}

#site > .wrapper > .grid > .grid-main > #itemsStream > .entry.iC.single.replyOn > .wblock.lcontrast.dC {
    display:block !important;
}

.author > .vC {
    display: /*[[optionhide]]*/
}

.blacklisted {
    display: /*[[banoption]]*/
}

#site > .wrapper > .grid > .grid-main > #itemsStream > .entry > .wblock.lcontrast.dC {
    display: /*[[mikroblog]]*/
}

.hidden-comment {
    display: /*[[hiddencomment]]*/
}

.stm-ad-player {
    display:none !important;
}

a[href*="adverts"] {
    display: none !important;
}

.cookie {
    display: none !important;
}

#site > span {
    display: none !important;
}

.link .empty-media {
    display: none !important;
}

.wblock > span {
    display: none !important;
}

body.screening .wrapper {
    max-width: 70%;
}

.closableContainerpopup_register {
    display: none;
}

.wgt-meczyki {
    display: none;
}

}
