    background-color: rgba(33, 33, 33, .8)!important;
    background-image: none!important;
}
.jw-skin-tube .jw-button-color {
    color: rgba(143, 79, 175, .6);
}
.jw-skin-tube .jw-button-color:focus,
.jw-skin-tube .jw-button-color:hover,
.jw-skin-tube:not(.jw-flag-touch) .jw-button-color:hover {
    color: #8f4faf;
}
.jw-skin-tube .jw-progress {
    background: #8f4faf;
}
.jw-skin-tube .jw-slider-horizontal .jw-knob {
    background-color: #8f4faf;
    box-shadow: 0 2px 5px rgba(0, 0, 0, .4);
    opacity: 0;
}
.jw-skin-tube .jw-slider-horizontal:hover .jw-knob {
    opacity: 1;
}
.jw-toggle {
    color: rgba(143, 79, 175, .6);
}
.jw-toggle:hover {
    color: #8f4faf;
}
.jw-knob {
    background-color: #8f4faf;
}
.jw-slider-vertical .jw-knob {
    box-shadow: 0 2px 5px rgba(0, 0, 0, .4);
    opacity: 0;
}
.jw-slider-vertical:hover .jw-knob {
    opacity: 1;
}
}
