css:
@-moz-document domain(recolor.me) {
.speech_bubble_message,
.speech_bubble_message a,
.previewmsg_container,
#previewmsg_container {
  line-height: 28px;
}
.bbcode_img {
  max-width: 100% !important;
}
.smallbutton.button_yellow {
  vertical-align: top;
}
a.button_tip img {
  margin: 3px 4px;
}
.speech_bubble_message {
  padding: 20px;
}
.speech_bubble {
  border-radius: 0px 5px 5px 5px;
  min-height: 50px;
  position: relative;
  border: none;
}
.speech_bubble_time {
  padding-top: 6px;
  background: #d9d9d9;
  border-radius: 0px 0px 5px 5px;
}
.speech_bubble_tick {
  display: none;
}
.speech_bubble:after {
  content: "";
  display: block;
  position: absolute;
  top: -0px;
  left: -20px;
  bottom: auto;
  border-style: solid;
  border-width: 0px 20px 20px 0px;
  border-color: transparent #f0f0f0;
}
.bbcode_smiley.bigger {
  width: 32px;
  height: 32px;
  vertical-align: top;
  margin: -2px auto;
}
.bbcode_smiley.s100 {
  width: 100px;
  height: 100px;
  vertical-align: middle;
  margin: -16px 0 -10px;
}
.bbcode_smiley.bigger.backshadow {
  filter: none;
}
.blockUI.blockMsg.blockPage {
  border-radius: 5px !important;
  padding: 20px !important;
}
.nightmode .speech_bubble:after {
  border-color: transparent #111;
}
.nightmode .speech_bubble_time {
  background: #222;
}
.text_container {
  margin-bottom: 20px;
  border-radius: 0;
}
.text_container p,
#sub_shout_container {
  padding-bottom: 10px;
}
.forum_topic .post .post_background {
  display: none;
}
#modify_topic form {
  padding: 0 5px;
}
.forum_topic_pages {
  margin-top: 0;
}
.speech_bubble.speech_bubble_smaller {
  border-radius: 0 5px 5px 5px;
}
.speech_bubble.speech_bubble_smaller .speech_bubble_message {
  padding: 10px;
}
.speech_bubble.speech_bubble_smaller .speech_bubble_time {
  position: static;
}
#rcmenu #chats .chat {
  border-radius: 0;
}
}
