@-moz-document domain(example.com) {
body,html {min-height: 100%!important; }

/*----------*/
/*Selection Color*/
::selection {
background: #A3BCEB;
color: #fff;
}
/*----------*/

/*----------*/
/*Text Color*/
* {color: #c0c0c0!important; border-color:#60666a!important;box-shadow: 0 0 0 #333!important}
/*----------*/

/*----------*/
/*Header Color*/
body h1, body h1 *, body h2, body h2 *, p>strong:only-of-type,  p>b:only-of-type{color: #9592CE!important}
/*----------*/

/*----------*/
/*Link Color*/
* body a, body a *{color: #A3BCEB!important;}
body a:hover, body a:hover *{color: #9592CE!important; text-decoration: none!important}
/*----------*/


input.button {text-shadow: 0 0px 0 #ccc!important;}

html, body{background-color:#484848!important}
body>*:not(:empty){background-color:#222!important}
body>*>*:not(:empty){background-color:#222!important}
body>*>*>*:not(:empty){background-color: transparent}
body>*>*>*>*:not(:empty){background-color:#282828!important}
body>*>*>*>*>*:not(:empty){background-color:#383838!important}
body>*>*>*>*>* *:not([class^="issue-data-block activity-comment twixi-block expanded focused"]):not([class^="tm-label-circle"]):not([class^="tm-status-dropdown"]):not([class^="ghx-grabber"]):not([class*="ghx-active"]){background-color:#383838!important}
body table:not(:empty){background-color:#282828!important}
body table>tbody>tr:nth-child(even){background-color:rgba(0,0,0,.2)!important}
body table>tbody>tr>td:nth-child(odd){background-color:rgba(0,0,0,.1)!important}
body *:empty:not([class*="icon"]):not([class^="tm-"]):not([class^="ghx-grabber"]):not([class^="text"]):not([class^="aui-field"]){background-color: #383842!important}

body p:not(:empty), body p *, body h1, body h1 *, body h2, body h2 *, body h3, body h3 *, body h4, body h4 *, body h5, body h5 *, body strong>*, body b>*, body em>*, body i>*,  body span>*:not(img){background:transparent none!important}
body h3, body h3 *, body h4, body h4 *{color: #eee!important}


*:not([onclick]):not(input):not(a):not(img):not([class^="UI"]):not([class^="tm-"]):not([class="icon"]):not([class*="mce"]):not([class*="imagebacked"]):not([class^="aui-list-"]):not([clas^="action-links"]):not([class^="aui-icon"]):not([class^="aui-ss-entity-icon"]):not([class^="icon "]):not([class^="fancy-ico"]):not([class^="overlay-icon icon icon-edit-sml"]):not([class^="field-tools"]):not([class^="dialog-menu-item selected"]):not([class^="dialog-menu-item "]):not([class^="activity-stream-view "]):not([class^="ghx-icon "]),
body a:not(:empty):not([class^="inplace_"]):not([class*="tempo-actions-trigger"]):not([class^="aui-list-"]):not([clas^="action-links"]):not([class^="aui-icon"]):not([class^="icon "]):not([class^="issueaction-attach-file "]):not([class^="user-hover user-avatar"]):not([class^="aui-dd-link"]):not([class^="icon-permalink"]):not([class^="lnk "]):not([class^="wiki-renderer "]):not([class^="js-feedback-link ghx-feedback-link ghx-spch-bub-inside"]),
div:not([onclick]):not([class^="tm-"]):not([class*="icon"]):not([class^="aui-list-"]):not([clas^="action-links"]):not([class^="aui-icon"]):not([class^="overlay-icon icon icon-edit-sml"]){background-image:none!important;}
*[onclick] {color:#79a!important}
*[onclick]:hover {color:#99a8aa!important}
body hr{background: #666 none!important; color: #666!important; border:1px solid #666!important; height: 1px!important; overflow:hidden!important; display: block!important}


/* body a:visited, body a:visited *{color: #666!important;} */
body img,a[href] img, a[href] button, input[type="image"],*[onclick]:empty, body a:empty{opacity:1!important}
body img:hover,a[href]:hover img, a[href]:hover button, *[onclick]:empty:hover, body a:empty:hover{opacity:1!important}

body span>img{visibility:visible!important}

body input[type],  input, body textarea, body select{-moz-appearance:none!important; color: #bbb!important; border-radius:4px !important; border-width: 1px!important; border-color:rgba(100,100,100,.5)!important;border-style:solid!important; background:-moz-linear-gradient( top , #303030 40%, #383838, #343434 ) #333 !important; box-shadow: inset 1px 1px 1px #111!important}




/*button color*/
body input[type="submit"], body input[type="button"], body input[type="image"],  body input[type="reset"], body button
{
    -moz-appearance:none!important; color: #2F3256!important; border-radius:4px !important; border-width: 1px!important; border-color: #b2b8ad!important;border-style:solid!important; background:linear-gradient(to bottom, #A5CCE5 5%, #758EA5 100%)!important; box-shadow: 1px 1px 1px #111!important
}


body input[type="submit"]:hover, body input[type="button"]:hover, body input[type="image"]:hover,  body input[type="reset"]:hover, body button:hover
{
    -moz-appearance:none!important; color: #392356!important; border-radius:4px !important; border-width: 1px!important; border-color: #b2b8ad!important;border-style:solid!important; background:linear-gradient(to bottom, #C5BCE5 5%, #7D70A5 100%)!important; box-shadow: 1px 1px 1px #111!important
}
/*----------*/





body input[type]>*, body textarea>*{-moz-appearance:none!important; background:transparent none  !important; color: #bbb!important; border-style:solid!important; border-width: 0px!important;}
body select *{background-color:transparent !important; color: #bbb!important; border-style:solid!important; border-width: 0px!important;}

pre:not(:empty), code:not(:empty) , cite:not(:empty), pre:not(:empty) *, code:not(:empty) *, cite:not(:empty) *{background-image:url(data:image/gif;base64,R0lGODlhBAAEAIAAABERESIiIiH5BAAAAAAALAAAAAAEAAQAAAIGTACXaHkFADs=)!important; color: #bcc8dc!important;}

.aui-toolbar .toolbar-group .toolbar-trigger, .aui-toolbar .aui-dd-parent a.aui-dd-trigger {
  text-shadow: 0 0px 0 black;
}

a.js-quickfilter-button.last.ghx-active, a.js-quickfilter-button.first.ghx-active, a.js-quickfilter-button.ghx-active, .ghx-controls-filters dd a.ghx-active {
    background: #3572b0!important;
}

div.ui-draggable {
    display: none;
}

.editable-field .overlay-icon {
	color: transparent!important;
}

.tm-wc-item:hover .editable-field .overlay-icon:after, .tm-description:hover .editable-field .overlay-icon:after {
	content: "\f142";
    color: #319D84;
    font-family: "Atlassian Icons";
    left: 10px;
    position: absolute;
    top: 10%;  
}

.ghx-swimlane-header:after, .ghx-swimlane-header.ghx-selected:after {
    visibility:hidden;
}

.ghx-grabber {
  filter: brightness(60%);
  -webkit-filter: brightness(60%); 
}

.tm-label-circle {
  filter: brightness(60%) contrast(300%);
  -webkit-filter: brightness(60%) contrast(300%);  
}

.tm-status-dropdown {
  filter: brightness(60%) contrast(150%);
  -webkit-filter: brightness(60%) contrast(150%); 
}

::-webkit-scrollbar {
  width: 12px;
  height: 12px;
  /* This is more usable for users trying to click it. */
  background-color: #444;
  -webkit-border-radius: 100px;
}
/* hover effect for both scrollbar area, and scrollbar 'thumb' */
::-webkit-scrollbar:hover {
  background-color: #444;
}

/* The scrollbar 'thumb' ...that marque oval shape in a scrollbar */
::-webkit-scrollbar-thumb:vertical {
  /* This is the EXACT color of Mac OS scrollbars. 
     Yes, I pulled out digital color meter */
  background: #999;
  -webkit-border-radius: 100px;
}
::-webkit-scrollbar-thumb:vertical:active {
  background: #bbb; /* Some darker color when you click it */
  -webkit-border-radius: 100px;
}

/* The scrollbar 'thumb' ...that marque oval shape in a scrollbar */
::-webkit-scrollbar-thumb:horizontal {
  /* This is the EXACT color of Mac OS scrollbars. 
     Yes, I pulled out digital color meter */
  background: #999;
  -webkit-border-radius: 100px;
}
::-webkit-scrollbar-thumb:horizontal:active {
  background: #bbb; /* Some darker color when you click it */
  -webkit-border-radius: 100px;
}

.aui-theme-default #header .global {
    background-color: #242424;
    color: #ffffff;
}

.aui-theme-default #header .local {
    background-color: #383838;
    border-top: 1px solid #383838;
    color: #ffffff;
}

.issue-data-block.focused {
    background-color: #777777;
}
}
