css:
@-moz-document domain("ivark.github.io")
{

body,
.container {
    color: #eee !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}

.invert {
    background: #f2f2f2 url("http://i.imgur.com/IXe5opr.png") repeat fixed !important;
}

#game {
    color: #eee !important;
    border-color: #262626;
}

#news {
    color: #eee !important;
}

#logotxt {
    filter: invert(100%);
}

.storebtn,
.postinfcrunch,
.newdim {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px double #127A20 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
}

.storebtn:hover,
.postinfcrunch:hover,
.newdim:hover {
    background: linear-gradient(to top, #127A20, #1a1a1a, #127A20) !important
}

.unavailablebtn {
    background: linear-gradient(to top, #1a1a1a, #333333, #1a1a1a) !important;
    border: 1px double #b30000 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;
}

.unavailablebtn:hover {
    background: linear-gradient(to top, #b30000, #333333, #b30000) !important;
}

.tabbtn,
.secondarytabbtn {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px double #4980CC !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    text-shadow: -1px -1px 0 #404040, 1px -1px 0 #404040, -1px 1px 0 #404040, 1px 1px 0 #404040;
}

.tabbtn:hover,
.secondarytabbtn:hover {
    background: linear-gradient(to top, #4980CC, #1a1a1a, #4980CC) !important;
}

.infinitybtn,
.autobuyerbtn {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px double #ff9900 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    text-shadow: -1px -1px 0 #404040, 1px -1px 0 #404040, -1px 1px 0 #404040, 1px 1px 0 #404040;
}

.infinitybtn:hover,
.autobuyerbtn:hover {
    background: linear-gradient(to top, #ff9900, #1a1a1a, #ff9900) !important;
}

.achievementunlocked {
    border: 1px double #127A20 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
}

.achievementlocked {
    border: 1px double #b30000 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
}

#coinAmount,
#infinityPoints1,
#infinityPoints2,
#infDimMultAmount,
#infPowAmount,
.IPAmount1,
.IPAmount2,
#replicantiamount,
#replicantimult {
    color: #00ff00 !important;
    text-shadow: -1px -1px 0 #404040, 1px -1px 0 #404040, -1px 1px 0 #404040, 1px 1px 0 #404040;
}

#coinAmount,
#infPowAmount,
#infDimMultAmount {
    font-size: 150% !important;
}

#infinityPoints1,
#replicantiamount,
#replicantimult {
    font-size: 100% !important;
}

#infinityPoints2 {
    display: none;
}

#progress {
    background: #1a1a1a url("http://i.imgur.com/IXe5opr.png") repeat fixed !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
}

#progressbar {
    background: linear-gradient(to top, #0f2d06, #1e5a0c) !important;
    color: white;
    text-align: center;
    font-size: 120%;
    text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;
}

#statistics {
    color: #eee !important;
}

a {
    color: #66b3ff;
    text-shadow: -1px -1px 0 #404040, 1px -1px 0 #404040, -1px 1px 0 #404040, 1px 1px 0 #404040;
}

.challengediv {
    background: #1a1a1a;
    color: inherit;
    border-color: #262626;
}

.completedchallengesbtn {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px solid #127A20 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
}

.challengesbtn,
.onchallengebtn {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px double #127A20 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
}

.challengesbtn:hover {
    background: linear-gradient(to top, #127A20, #1a1a1a, #127A20) !important;
}

.infinistorebtnbought {
    background: linear-gradient(to top, #127A20, #0d5917, #127A20) !important;
    border: 1px double #127A20 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    width: 200px;
}

.infinistorebtnlocked {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px double #127A20 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    width: 200px;
}

#break,
.infinimultbtn,
.newdimlocked {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px double #ff6666 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
}

#break {
    width: 200px;
}

#break:hover,
.infinimultbtn:hover,
.newdimlocked:hover {
    background: linear-gradient(to top, #ff6666, #1a1a1a, #ff6666) !important;
    color: #eee !important;
}

.infinistorebtn1 {
    background: linear-gradient(to top, #000000, #1a1a1a, #000000) !important;
    border: 1px double #eb7adc !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    width: 200px;
}

.infinistorebtn1:hover {
    background: linear-gradient(to top, #e44ed0, #1a1a1a, #e44ed0) !important;
    border: 1px double #eb7adc !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    width: 200px;
}

.infinimultbtn {
    width: 200px;
}

#maxall {
    top: 0;
    right: 0;
    position: relative;
    height: 25px;
}

.autoBuyerDiv {
    border: none;
    padding: 0px 10px;
}

td {
    border: none;
}

input[type=checkbox] {
    filter: invert(100%);
}

select,
option {
    color: #eee;
    background: transparent;
    border: none;
}

input {
    background: #262626;
    color: #eee;
    border: 1px solid #363636;
    text-align: center;
}

.completedrow {
    background: linear-gradient(to top, #127A20, #0d5917, #127A20) !important;
    border: 1px double #127A20 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    border-radius: 0px;
}

.achievementunlocked {
    background: #179b29;
}

.achievementlocked {
    background: #808080;
}

#resetLabel {
    width: 90%;
}

.postinfcrunch,
.newdimlocked {
    top: 10px !important
}

[data-notify-text],
[data-notify-html] {
    text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;
}

.notifyjs-bootstrap-base {
    background: linear-gradient(to top, #fcf8e3, #fbeed5, #fcf8e3) !important;
    border: 1px ridge #fbeed5 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    border-radius: 0px;
}

.notifyjs-bootstrap-info {
    background: linear-gradient(to top, #4980CC, #48C0E8, #4980CC) !important;
    border: 1px ridge #48C0E8 !important;
    box-shadow: inset 0 0 1px #fff, 1px 1px 2px #000;
    color: #eee !important;
    border-radius: 0px;
}

.newdimlocked {
    display: inline-block !important;
    visibility: visible !important;
}

#softReset,
#secondSoftReset {
    width: 210px !important;
}

}
