css:
@-moz-document domain("livebook.manning.com") {
  body{
    color: rgba(195, 196, 198, .5);
  }
  #main-page-content{
    background-color: #31363b;
  }
  h1,h2,h3,h4,h5,h6{
    color: #EEE;
  }
  .callout-container h5{
    color: #333;
  }
  kbd{
    color: #333;
    background-color: rgba(255,255,255,.5);
    border: 1px solid rgba(255,255,255,.8);
  }
  body.theme-1 .callout-container,
  .listing-container,
  .figure-container,
  .callout-container,
  .browsable-container{
    background-color: #CCC;
    color: rgba(77, 77, 77, .5);
  }
  body.theme-1 .introduction-summary{
    background: rgba(255,255,255,.3);
    background-color: rgba(255,255,255,.3);
  }
  .figure-container h5,
  .browsable-container h5{
    color: #333;
  }
  .listing-environment-container{
    border-top-color: #888;
  }
  #track-point-column {
    background-color: rgba(60, 103, 162, 0.4);
  }
  #book-markup-container .readable-text a {
    color: #808fe5;
  }
}
