css:
@-moz-document domain("8ch.net") {
/*-----Board Toolbar---*/

/* Text and Icons */
div.boardlist {
    background-color: #0D1021 !important;
    color: #0D1021;
}

/* Main Bar */
div.boardlist a {
    color: #fbde2d;
}

/*-----Posts-----*/

/* Children */
div.post.reply {
    background-color: #0D1021;
    border: 1px solid #0D1021;
    margin-bottom: 2px;
    margin-left: 16px;
    margin-top: 2px;
}

/* OP */
body {
    background: #0D1021 none;
    color: #C5C8C6;
}

/*----Text Colors----*/

/* Making a post */
form table tr th {
    background: #0c1021;
    border: 1px solid #0c1021;
    color: #FFF;
}

/* Name */
p.intro span.name {
    color: #8da6ce;
}

/* Board Volunteer Name */
.intro span.capcode, p.intro a.capcode, p.intro a.nametag {
    color: purple;
    margin-left: 0;
}

/* Main Text */
body {
    color: #FFF;
}

/* Subject */
p.intro span.subject {
    color: #ff6400;
}

/* [Reply], [Show All Posts], and other various links */
a:link,
a:visited,
p.intro a.email span.name {
    color: #fbde2d;
}

/* Child links */
div.post.reply div.body a {
    color: #fbde2d;
}
}
