@-moz-document domain("checkio.org") {

  /* map, missions, and mission info pages */
  
  .page__container #main__container {
    background-color: rgb(63, 79, 120);
  }
  
  .island-tasks__container {
    background-color: rgb(63, 79, 120) !important;
  }
  
  .island-tasks__container .island-tasks__info {
  background-color: rgb(57, 63, 76);
  }
  
  .map__scroll {
    background-image: url("https://i.imgur.com/4myyqm1.png");
  }

  body {
    background-image: url("https://i.imgur.com/4myyqm1.png");
  }
  
  .map__station__panel {
    background-color: rgb(57, 63, 76);
  }
  
  .CodeMirror-scroll {
    background-color: rgb(57, 63, 76) !important;
    color: rgb(182, 196, 191);
  }
  
  .page__container #main__container .main-container__missions__title {
    color: rgb(182, 196, 191);
  }
  
  .page__container #main__container .main-container__missions__title .missions-title__name {
    background-color: rgb(57, 63, 76);
  }
  
  .page__container #main__container .missions-content__footer .task-info__hints .hints__group__hint__title {
    background-color: rgb(57, 63, 76);
    color: rgb(182, 196, 191);
  }
  
  .page__container #main__container .missions-content__footer .task-info__hints .hints__group__hint__question .hints__group__hint__question__text {
    background-color: rgb(57, 63, 76);
    color: whitesmoke;
  }
  
  .page__container #main__container .missions-content__footer .task-info__hints .hints__group__hint__answer .hints__group__hint__answer__text {
    background-color: rgb(57, 63, 76);
    color: darkgrey
  }
  
  .page__container #main__container .missions-content__footer .task-info__hints .hints__group__hint__question .hints__group__hint__question__user .username {
    color: rgb(12, 140, 220);
  }
    
  .page__container #main__container .missions-content__footer .task-info__hints .hints__group__hint__answer__user .username {
    color: rgb(12, 140, 220)
  }
    
  .page__container #main__container .missions-content__footer a {
    color: rgb(12, 140, 220);
  }
    
  .map__station__panel__description {
    color: rgb(182, 196, 191);
  }
  
  .island-tasks__container .island-tasks__buy {
    color: rgb(150, 150, 150);
  }
  
  .island-tasks__container .island-tasks__info .island-tasks__task__summary .island-tasks__description {
    color: rgb(12, 140, 220);
  }
  
  .island-tasks__notes a {
    color: rgb(12, 100, 180);
  }
  
  .main-container__missions__content {
    border-bottom: 2px solid black;
    border-top: 2px solid black;
  }
  
  .page__container #main__container .mission-content__metainfo a {
    color: rgb(12, 140, 220);
  }
  
  .content-controls label {
    color: rgb(12, 140, 220);
  }
  
  .user-info .username {
    color: rgb(12, 140, 220);
  }
  
  .content-controls label a {
    color: rgb(12, 140, 220);
  }
  
  #main__container .island-description__summary {
    color: grey;
  }
  
  .content-description__text p {
    color: darkgrey;
  }
  
  
  
  /* IDE */
  .hints__group__hint__title {
    color: darkgrey;
  }
  
  .hints__group__hint__question__user {
    color: grey;
  }
  
  .hints__group__hint__question__text {
    color: darkgrey;
  }
  
  .hints__group__hint__answer__user {
    color: grey;
  }
  
  .hints__group__hint__answer__text {
    color: darkgrey;
  }
  
  .x-panel-header-default {
    background-color: rgb(29, 31, 33);
    border-color: rgb(63, 79, 120);
  }
  
  .x-panel-header-text-default {
    color: rgb(190, 190, 190);
  }
  
  #task-description p {
    color: rgb(230, 230, 230);
  }
  
  .ace-tm .ace_gutter {
    background-color: rgb(41, 44, 47);
    color: rgb(129, 133, 133);
  }
  
  .x-panel-body-default {
    background-color: rgb(29, 31, 33);
    color: rgb(190, 190, 190);
  }
  
  .ace_scroller {
    overflow-x: auto !important;
  }
  
  .x-mask {
    background-color: rgb(29, 31, 33);
  }
  
  .x-mask-msg {
    background-color: grey;
