@-moz-document domain("last.fm"){
body {
/* ---------------- Common Rules ---------------- */
    background: #000033 !important ;
    color: #000033 !important;
    font-family: Times, Times New Roman, Tahoma, Arial, Helvetica, sans-serif !important;
}
    link {color: #330000 !important;}
    visited {color: #666666 !important;}
    hover {color: #3333FF !important;}
/*  div  {background: #7799EE !important; border: #330000 !important;} */
    
    /*--Remove extras--*/
    .share-desktop   {display: none  !important;}

    /*--Remove Ad Blocks--*/
    .buylinks     {display: none !important;}
    .lastfm-ad    {display: none  !important;}
    .related-content {display: none  !important;}
    .related-ads  {display: none  !important;}
}
