css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.dinamalar.com/district.asp") {

  #dmr-navbars, #page > div:nth-child(4), 
    #selMenuss1, #sell3menu, .breadcrumb, 
    span.wd320, .sepeinner > div:nth-child(2) > div:nth-child(10) 
     {
    display: none !important;
  }
  
    /* Separator */
  div.cbh10 {
    height: 1px !important;
  }
  
  .sepeinner > div:nth-child(2) > div:nth-child(8), #currencydiv {
    font-size: 8px !important;
  }
  
  /* இன்றைய நிகழ்ச்சி */
  .sepeinner > div:nth-child(2) > div:nth-child(16) > div:nth-child(1) > a:nth-child(1) {
    background-image: none;
    background-color: darkgreen;
    color: white;
  }
  
  /* மேலும் படிக்க */
  .sepeinner > div:nth-child(2) > div:nth-child(16) > div:nth-child(3) > div:nth-child(4) {
    width: 100%;
    float: none;
    font-size: 14px;
    background-color: bisque;
    height: 1.75em;
    text-align:  center;
    vertical-align:  middle;
  }
  
  .sepeinner > div:nth-child(2) > div:nth-child(16) > div:nth-child(3) > div:nth-child(4):after {
    white-space:  pre;
    content: "          ";
    background-color: bisque;
  }
  
  .btn {
    margin: 0 0 0 0;
  }
}
