css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mtxserv.fr") {

html, body, #taigachat_box li, #taigachat_box, .textCtrl, .textCtrl:focus, .textCtrl.Focus, .Menu .primaryContent, .alertsPopup .Alert:hover, #ConversationsMenu .listItem:hover, .discussionListItem, .avatar, .discussionListItem .stats, .messageText a.username:hover, .message a:hover,
.bbCodeQuote .quoteContainer, .message .messageUserBlock, .messageUserBlock div.avatarHolder, .avatar img, .messageUserBlock .extraUserInfo, .breadcrumb, .breadcrumb .crust a.crumb, .profilePage .tabs.mainTabs li a:hover, .Menu .PrefixGroup h3, .message a:focus, .ugc a:hover, .ugc a:focus {
 	background-color: #000;
}

html, body, .textCtrl, .Menu .primaryContent, .searchResult .snippet a, .profilePage .tabs.mainTabs li.active a, .profilePage .tabs.mainTabs li.active a:hover, .profilePage .tabs.mainTabs li a, .textHeading {
    color: #DDD;
}

.profilePage .tabs.mainTabs li.active a, .profilePage .tabs.mainTabs li.active a:hover, .titleBar, #content, .mainContainer, #content .pageContent, .sidebar .secondaryContent, .messageInfo, .editDate, .messageMeta, .profilePage .tabs.mainTabs li.active a, .profilePage .tabs.mainTabs li.active a:hover, .primaryContent {
	background-color: #222;
}

.profilePage .tabs.mainTabs li.active a, .profilePage .tabs.mainTabs li.active a:hover, .profilePage .tabs.mainTabs li a, .sidebar .secondaryContent, textarea.textCtrl:focus, .Menu .secondaryContent, .node .nodeLastPost, .discussionListItem .stats, .secondaryContent, .sectionFooter,
.messageText a.username:hover, .message a:hover, .bbCodeQuote .quoteContainer, .breadcrumb, .breadcrumb .crust a.crumb, .breadcrumb .crust:hover a.crumb, .profilePage .tabs.mainTabs li.active a, .profilePage .tabs.mainTabs li.active a:hover, .profilePage .tabs.mainTabs li a, .navigationSideBar ul li a:hover,
.Menu .PrefixGroup h3, .Menu .noPrefix, .ctrlUnit .Focus, .xenForm .ctrlUnit > dd .textCtrl, .message a:focus, #taigachat_message:focus, .navPopup .listPlaceholder ol.secondaryContent.Unread, .ugc a:hover, .ugc a:focus {
    background-image: none;
}

.Menu .secondaryContent, .sectionMain, .nodeList .forumNodeInfo, .node .nodeLastPost, .secondaryContent, .sectionFooter, #DiscussionListOptionsHandle a, .hasJs .bbCodeSpoilerText, .PreviewContainer .messageText, .profilePage .tabs.mainTabs {
    background-color: #111;
}
.profilePage .tabs.mainTabs li a {
    background-color: #555;
}
.titleBar, .sidebar .secondaryContent {
    color: #CCC;
}
.textCtrl:focus, .textCtrl.Focus {
    color: #FFF;
}
.listPlaceholder .secondaryContent {
    background-color: #232222;
}
.messageText a.username:hover {
    color: #F00;
    box-shadow: 0 0 20px 10px #000;
}
.message a:hover, .message a:focus, .ugc a:hover, .ugc a:focus {
    box-shadow: 0 0 5px 1px #000;
    color: #0288D1;
}
.messageUserBlock .arrow span, .breadcrumb .crust .arrow span {
   border-left-color: #000;
   -moz-border-left-colors: #000;
}
.breadcrumb .crust:hover a.crumb {
    background-color: #300;
}
.breadcrumb .crust:hover .arrow span {
    border-left-color: #300;
   -moz-border-left-colors: #300; 
}
.bbCodeBlock pre, .bbCodeBlock .code {
    background-color: #112;
}
#AccountMenu .menuColumns ul li a:hover {
    background-color: #232;
}
.navigationSideBar ul li a:hover {
    background-color: #100;
}
.Menu .PrefixGroup {
    background-color: #444;
}
.pollOptions .pollOption label:hover {
    background-color: #333; 
}
.navPopup .listPlaceholder ol.secondaryContent.Unread {
    background-color: #030;
}
    
}
