@-moz-document domain("vk.com") {

.wall_module .post_like_icon, .wall_module .like_icon{
        background: url('data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%2216%22%20height%3D%2214%22%20viewBox%3D%22-1%20-1%2018%2016%22%20style%3D%22fill%3A%20transparent%3Bstroke%3A%20%232a5885%3B%22%3E%3Cpath%20d%3D%22M8%203.2C7.4-0.3%203.2-0.8%201.4%201%20-0.5%202.9-0.5%205.8%201.4%207.7%201.9%208.2%206.9%2013%206.9%2013%207.4%2013.6%208.5%2013.6%209%2013L14.5%207.7C16.5%205.8%2016.5%202.9%2014.6%201%2012.8-0.7%208.6-0.3%208%203.2Z%22%2F%3E%3C%2Fsvg%3E')  center / cover;
        opacity: 1 !important;
    }
    .my_like .post_like_count._count {
        color: #F44336!important;
    }
    .post_like_link._link {
        display: none;
    }
    .wall_module .my_like .post_like_icon, .wall_module .my_like .like_icon{
        background: url('data:image/svg+xml;charset=utf-8,<svg%20xmlns%3D"http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg"%20width%3D"16"%20height%3D"14"%20viewBox%3D"0%200%2016%2014"%20style%3D"fill%3A%23F44336%3B"><path%20d%3D"M8%203.2C7.4-0.3%203.2-0.8%201.4%201%20-0.5%202.9-0.5%205.8%201.4%207.7%201.9%208.2%206.9%2013%206.9%2013%207.4%2013.6%208.5%2013.6%209%2013L14.5%207.7C16.5%205.8%2016.5%202.9%2014.6%201%2012.8-0.7%208.6-0.3%208%203.2Z"%2F><%2Fsvg>')  center / cover;
        animation: like_bounce 0.5s;
    }
    @keyframes like_bounce {
        0% {
            -webkit-transform: scale(1);
            -o-transform: scale(1);
            transform: scale(1)
        }
        30% {
            -webkit-transform: scale(1.3);
            -o-transform: scale(1.3);
            transform: scale(1.3)
        }
        60% {
            -webkit-transform: scale(0.8);
            -o-transform: scale(0.8);
            transform: scale(0.8)
        }
        80% {
            -webkit-transform: scale(1.06);
            -o-transform: scale(1.06);
            transform: scale(1.06)
        }
        to {
            -webkit-transform: scale(1);
            -o-transform: scale(1);
            transform: scale(1)
        }
    }
}

@-moz-document domain("www.instagram.com") {
.coreSpriteHeartFull{
	animation: like_bounce 0.5s;
}
@keyframes like_bounce {
        0% {
            -webkit-transform: scale(1);
            -o-transform: scale(1);
            transform: scale(1)
        }
        30% {
            -webkit-transform: scale(1.3);
            -o-transform: scale(1.3);
            transform: scale(1.3)
        }
        60% {
            -webkit-transform: scale(0.8);
            -o-transform: scale(0.8);
            transform: scale(0.8)
        }
        80% {
            -webkit-transform: scale(1.06);
            -o-transform: scale(1.06);
            transform: scale(1.06)
        }
        to {
            -webkit-transform: scale(1);
            -o-transform: scale(1);
            transform: scale(1)
        }
    }
}
