@-moz-document domain("macheist.com") {

/* common.css */
/* Top Bar */
.page.tf29400 .nav .glow, .page.tbb41a2 .nav .glow , .page.t86d006 .nav .glow, .page.t00aae9 .nav .glow {
	background-position: 0 -76px !important;
}
/* Menu Selection */
.tf29400 ul li.sel, .tbb41a2 ul li.sel, .t86d006 ul li.sel, .t00aae9 ul li.sel {
	background-position: -648px -3px !important;
}
/* Briefing/Loot selection */
.tf29400 ol.subnav li ul li.selected, .tbb41a2 ol.subnav li ul li.selected, .t86d006 ol.subnav li ul li.selected, .t00aae9 ol.subnav li ul li.selected { 
	background-position: 0 -276px !important;
}

/* loot.css */
/* Price Strike */
.p_loot.tf29400 .details h3 .price .strike, .p_loot.tbb41a2 .details h3 .price .strike , .p_loot.t86d006 .details h3 .price .strike, .p_loot.t00aae9 .details h3 .price .strike {
	background-position: 0 -120px !important;
}



}
