css:
@-moz-document domain("bitconnect.co") {
.dib.mr10.header-wallet.mobile-br-none,
form.admin-form.form-ref-link.mt5.w70p.dib,
div#toggle_sidemenu_r {
    display: none !important;
}
.btn-sm, .btn-group-sm > .btn {
    padding: 12px 24px;
    font-size: 16px;
    line-height: 1.6;
    border-radius: 0;
}
.btn {
    display: inline-block;
    padding: 12px 24px;
    margin-bottom: 0;
    font-size: 16px;
    font-weight: bold;
    line-height: 1.6;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    cursor: pointer;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-image: none;
    border: 1px solid transparent;
    border-radius: 0;
}
.navbar-nav {
    float: right;
    padding-right: 20px;
}
.bg-dark {
    background-color: #3b4d94!important;
    color: #868fb3;
}
#sidebar_left,
.sidebar-menu>li>ul {
    background-color: #3b4d94 !important;
}
header#topbar {
    background: #ffffff;
}
.header-wallet {
    border: none;
}
.panel-body {
    background: #ffffff;
    border: 0;
}
#content .panel-group.accordion .panel .panel-heading a {
    color: #ffffff !important;
    background: #3b4d94 !important;
}
.panel-group .panel {
    border-radius: 0;
    border: 1px solid #e4e4e4 !important;
}
div#spy6 {
    margin-bottom: 40px !important;
}
div#spy6 .text-black,
div#spy6 .text-black span {
    color: #ffffff !important; 
    font-size: 21px;
    font-weight: normal;

}
div#spy6 .cb_detail{
    border: none;
}
div#spy6 .panel-body {
 background: #337ab7;
}
.sidebar-left-content {
    background-color: transparent;
    box-shadow: none;
}
}
