* {
    box-sizing: border-box;
    border-radius: 0 !important;
}

body {
    font-family: Roboto, sans-serif;
    background: #f7f7f7;
    font-weight: 400;
    line-height: 1.6;
}

h1, h2 { font-weight: 300; color: #000; }
h3 { font-weight: 400; }
h4, h5 { font-weight: 500; }
h1 { font-size: 2.6rem; border-bottom: 1px solid #eee;}
h2 { font-size: 2.0rem; }
h2::after { display: block; content: ''; width: 100px; height:3px; background: #eee; }
h3 { font-size: 1.5rem; }

a:link, a:visited { color: #66bb6a; /* green lighten-1 */ text-decoration: underline; }
a:link:hover, a:visited:hover { color: #1b5e20; /* green darken-4 */ text-decoration: underline; }

.dokuwiki a.wikilink1 { color: #66bb6a; /* green lighten-1 */ text-decoration: underline; }
.dokuwiki a.wikilink1:hover { color: #1b5e20; /* green darken-4 */ text-decoration: underline; }
.dokuwiki a.wikilink2 { color: #ef6c00; /* orange darken-3 */ }
.dokuwiki a.wikilink2:hover { color: #e65100; /* orange darken-4 */ }

.dokuwiki .page ol,
.dokuwiki .page ul { line-height: 1.3; /*display: table;*/ }
.dokuwiki .page ol li,
.dokuwiki .page ul li {
	margin-top: 10px;
    margin-bottom: 10px;
    color: #e65100; /* orange darken-4 */
}

blockquote {
    background: #e0f2f1; /* teal lighten-5 */
    border-left: 4px solid #009688; /* teal */
    margin-left: 1em;
    padding: 0.5em 2em; padding-right: 0.5em;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);         /* 1 */
    font-style: italic;
}
blockquote blockquote { margin: 2px 0; }

/*== ======================================= ==*/

#dokuwiki__site {
    max-width: none;
    /*padding-top: 200px;*/
}
#dokuwiki__site > .site { padding: 0; }
#dokuwiki__site > .site > .wrapper { margin-left: auto; margin-right: auto; padding: 0 40px; max-width: 80em; }

#dokuwiki__pagetools { right: 0; }

#dokuwiki__header {
	position: relative;
    left: 0;
    top: 0;
    width: 100%;
    padding: 10px 0 0 0;
    background: #37474f; /* blue-grey darken-3 */
    color: #fff;
    z-index: 1000;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.3);        /* 2 */
    font-weight: 300;
    margin-bottom: 1rem;
}
#dokuwiki__header > .pad { max-width: 80em; margin: auto; padding: 0 40px; }
#dokuwiki__header > .pad > .headings { width:200px; }

#dokuwiki__header a { color: #fff; }

#dokuwiki__header h1 { font-size: 1.2em; font-weight: 300; line-height: 1; border: none; }
#dokuwiki__header h1 a { display: block; float:left; text-align: center; }
#dokuwiki__header h1 img { display: inline-block; height: 70px; width: auto; margin: auto; float: none; }
#dokuwiki__header h1 span { padding: 0; margin-top: -10px; color: #fff; display: block; }

#dokuwiki__header .tools::before, #dokuwiki__header .tools::after { content: none; }
#dokuwiki__header .tools { 
    display: flex;
    flex-direction: row-reverse;
    justify-content: space-between;
    width: auto;
    float: none;
    margin: 0;
    position: relative;
}
#dokuwiki__header .tools li:first-child { margin-left: 0; }

#dokuwiki__usertools { position: relative; top: auto; right: auto;  width: auto; }
#dokuwiki__usertools bdi { font-weight: 700; }

#dokuwiki__sitetools { position: static; width: auto; }
#dokuwiki__sitetools form.search { position: absolute; top: 110%; left: 0; right:0; margin: 0; }
#dokuwiki__sitetools form.search .no { display: block; position: relative; left: -4px; }

.dokuwiki div.breadcrumbs { clear: none; margin-top: 2.55rem; margin-left: 200px; margin-bottom: 0; border: none; }
.dokuwiki div.breadcrumbs div { border: none !important; }


/*== Page ==*/

.dokuwiki .pageId span {
    color: #777;
    background: #fff;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);         /* 1 */
}

.dokuwiki div.page {
    background: #fff;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);         /* 1 */
}

/*== Side ==*/

.dokuwiki .aside { overflow: visible; }

.dokuwiki .aside ul { padding: 0; list-style: none; margin-left: -1em; margin-bottom: 2rem; font-weight: 700;}
.dokuwiki .aside ul li { margin: 0; border-bottom: 1px solid #efefef; }
.dokuwiki .aside ul li a { display: block; padding: 0.9em 1em; color: #000; text-decoration: none; }
.dokuwiki .aside ul li a:hover { background: #efefef; text-decoration: none; }

.dokuwiki .aside h3 { font-size: 1.4rem; font-weight: 300; margin-bottom: 0.5rem; }
.dokuwiki .aside p > br { display: none; }
.dokuwiki .aside p strong > a {
    display: block;
    padding: 1em;
    background: #26a69a !important; /* teal lighten-1 */
    color: #fff !important;
    margin-bottom: 1em;
    text-decoration: none;
    transition: 0.2s ease;
}
.dokuwiki .aside p strong > a:hover {
    background: #009688 !important; /* teal */
    color: #fff !important;
    text-decoration: none;
}


