form.search input { padding: 0; color: #fff; width: 100%; }
form.search input::-webkit-input-placeholder { color: #999; }
form.search input::-moz-placeholder { color: #999; }
form.search input:-ms-input-placeholder { color: #999; }
form.search input::placeholder { color: #999; }
form.search button { 
	background: url(data:image/svg+xml;base64,);
	background-size: cover;
    width: 16px;
    height: 16px;
}


/*== Новая страница ==*/

.addnewpage {
    background: white;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    padding: 0.25em 0.5em;
    margin-bottom: 1.5rem;
}
.addnewpage form { display: block; width: 100%; }
.addnewpage input[type=text] { font-size: 1rem; margin-bottom: 0.5rem; padding: 1rem 0.75rem !important; width: 100%; display: block; box-sizing: border-box !important; }
.addnewpage [type=submit] {
    display: block;
    width: 100%;
    border: none;
    color: #fff;
    padding-left: 1em;
    padding-right: 1em;
}
.addnewpage [type=submit]:hover { background: #80cbc4; /* teal lighten-3 */ }
.page .addnewpage {
    background: none;
    box-shadow: none;
    padding: 0;
}
.page .addnewpage form input[type=text] {
    margin: 0 20px 0 0;
    width:auto;
    flex-grow: 10;
}
.page .addnewpage form input[type=submit] {
    width: auto;
    flex-shrink: 0;
}
.page .addnewpage form {
    display: flex;
    flex-wrap: wrap;
    align-items: flex-end;
}


/*== Теги ==*/

div.dokuwiki div.tags {
	border-top: 1px solid #ccc;
    padding-top: 0.5em;
    margin-left: -40px;
    margin-right: -40px;
    padding-left: 40px;
    padding-right: 40px;
}
div.dokuwiki div.tags span, div.dokuwiki div.tagstop span { background: none; padding: 0; font-size: 0; }
div.dokuwiki div.tags span > a, div.dokuwiki div.tagstop span > a {
    display: inline-block;
    font-size: 12px;
    font-weight: 500;
    color: rgba(0,0,0,0.6);
    padding: 0 12px 2px;
    border-radius: 16px !important;
    background-color: #e4e4e4;
    margin-bottom: 5px;
    margin-right: 5px;
    text-decoration: none;
    transition: 0.3s ease;
}
div.dokuwiki div.tags span > a:hover, div.dokuwiki div.tagstop span > a:hover { color: #fff; text-decoration: none; }
div.dokuwiki div.tags span > a.wikilink1:hover, div.dokuwiki div.tagstop span > a.wikilink1:hover { background: #66bb6a; /* green lighten-1 */ }
div.dokuwiki div.tags span > a.wikilink2:hover, div.dokuwiki div.tagstop span > a.wikilink2:hover { background: #e65100; /* orange darken-4 */ }


/*== Таблицы ==*/

table, th, td { border: none; }
tr { border-bottom: 1px solid #ddd;}

table { width: 100%; }
table.inline { width: auto; }

.dokuwiki table.inline tr:hover th { background: #eee; }
.dokuwiki table.inline tr:hover td { background: #f7f7f7; }

.dokuwiki div.table table {	width: 100%; }
div.dokuwiki table.ul ul { margin-left: 0; }
div.dokuwiki table.ul tr { border-bottom: 1px solid #ddd;}
div.dokuwiki table.ul td { padding: 0.75em .5em; }
div.dokuwiki table.ul td.date { font-family: Consolas, monospace; }
div.dokuwiki table.ul ul li { margin-left: 0; }
div.dokuwiki table.ul ul li:first-child:last-child { margin-top: 0; margin-bottom: 0; }


/*== Код ==*/

pre, code, samp, kbd {
	background: #eceff1; /* blue-grey lighten-5 */
    box-shadow: none;
    border: none;
}
pre {
    box-shadow: inset 0 2px 2px 0 rgba(0, 0, 0, 0.14), inset 0 1px 5px 0 rgba(0, 0, 0, 0.12), inset 0 3px 1px -2px rgba(0, 0, 0, 0.2);         /* 1 */
}
code { padding-left: 0.5em; padding-right: 0.5em; border: 1px solid #D4D6D8; }


/*== Оглавление ==*/

#dw__toc {
	border-left: none;
    background: #37474f; /* blue-grey darken-3 */
}
#dw__toc > div {
    padding: 0 0 0.5em;
}
#dw__toc a {
    display: block;
    padding: 0.5em 1.5em;
    position: relative;
	color: #ff9800; /* orange */
    text-decoration: none;
}
#dw__toc a:hover {
    background: #455a64; /* blue-grey darken-2 */
    text-decoration: none;
	color: #e65100; /* orange darken-4 */
}
#dw__toc a::before {
    content:'';
    position: absolute;
    left: 0.75em;
    width:4px;
    height:4px;
    border-radius: 5px;
    background: #ddd;
    top: 10px;
}
#dw__toc ul {
	padding: 0;
    list-style: none;
}
#dw__toc ul li {
	padding: 0;
    list-style: none;
    margin: 0;
}
#dw__toc ul li div.li { padding: 0; }
.dokuwiki h3.toggle {
    color: #ddd;
	font-size: 1rem;
    font-weight: 400;
}
#dw__toc ul ul { padding: 0; }
#dw__toc ul a { padding-left: 2.5em; }              #dw__toc ul a::before { left: 1.5em; }
#dw__toc ul ul a { padding-left: 3.5em; }           #dw__toc ul ul a::before { left: 2.5em; }
#dw__toc ul ul ul a { padding-left: 4.5em; }        #dw__toc ul ul ul a::before { left: 3.5em; }
#dw__toc ul ul ul ul a { padding-left: 5.5em; }     #dw__toc ul ul ul ul a::before { left: 4.5em; }
#dw__toc ul ul ul ul ul a { padding-left: 6.5em; }  #dw__toc ul ul ul ul ul a::before { left: 5.5em; }

/*== Inputs materialize ==*/

:disabled {pointer-events: none; }

input:not([type]),
input[type=text] {
    background-color: transparent;
    border: none;
    border-bottom: 1px solid #9e9e9e;
    border-radius: 0;
    outline: none;
    height: 1.5rem;
    width: 100%;
    font-size: 0.75rem;
    font-weight: 300;
    padding: 0;
    box-shadow: none;
    box-sizing: content-box;
    transition: all 0.3s;
}

input:not([type]):disabled, input:not([type])[readonly="readonly"], 
input[type=text]:disabled,  input[type=text][readonly="readonly"] {
    color: rgba(0,0,0,0.42);
    border-bottom: 1px dotted rgba(0,0,0,0.42)
}

input:not([type]):focus:not([readonly]),
input[type=text]:focus:not([readonly]) {
    border-bottom: 1px solid #26a69a;
    box-shadow: 0 1px 0 0 #26a69a
}

/*== Buttons ==*/

input[type=submit], input[type=button], input[type=reset], input.button, a.button, button, .qq-upload-button {
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.14), 0 1px 5px 0 rgba(0,0,0,0.12), 0 3px 1px -2px rgba(0,0,0,0.2);
    border: none;
    display: inline-block;
    height: 36px;
    line-height: 36px;
    padding: 0 2rem;
    text-transform: uppercase;
    vertical-align: middle;
    -webkit-tap-highlight-color: transparent;
    font-size: 1rem;
    outline: 0;
    
    text-decoration: none;
    color: #fff;
    background: #26a69a;
    text-align: center;
    letter-spacing: .5px;
    cursor: pointer;
    
    position: relative;
    overflow: hidden;
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
    z-index: 1;
    transition: .3s ease-out;
}
input[type=submit]:hover, input[type=button]:hover, input[type=reset]:hover, input.button:hover, a.button:hover, button:hover, .qq-upload-button:hover {
	background: #2bbbad;
    box-shadow: 0 3px 3px 0 rgba(0,0,0,0.14), 0 1px 7px 0 rgba(0,0,0,0.12), 0 3px 1px -1px rgba(0,0,0,0.2);
}
.dokuwiki .secedit button {
    font-size: 0.75rem;
    height: 20px;
    line-height: 20px;
    padding: 0 1rem;
}


/*== Edit textarea ==*/

.dokuwiki textarea.edit {
    font-size: 1rem;
    border: 1px solid #aaa;
    padding: 0.5rem;
    box-shadow: inset 0 2px 2px 0 rgba(0, 0, 0, 0.14), inset 0 1px 5px 0 rgba(0, 0, 0, 0.12), inset 0 3px 1px -2px rgba(0, 0, 0, 0.2);
    background: #f7f7f7;
    transition: none;
    min-height: 75vh;
}

.dokuwiki textarea.edit:focus {
    outline: none;
}
html { background: #263238; /* blue-grey darken-4 */ }
#dokuwiki__footer {
    padding: 1rem 40px;
    background: #263238; /* blue-grey darken-4 */
    margin: 0;
    color: #ccc;
}
#dokuwiki__site + .no { position: fixed; left: -999px; bottom: 100px; }

}
