.ripple-effect {
    position: absolute;
    border-radius: 50%;
    opacity: 0.4;
    background: white;
    -webkit-animation: ripple-animation 2s cubic-bezier(.25, .8, .25, 1);
    animation: ripple-animation 2s cubic-bezier(.25, .8, .25, 1);
}

@-webkit-keyframes ripple-animation {
    from {
        transform: scale(0);
    }
    to {
        transform: scale(1);
        opacity: 0;
    }
}

@keyframes ripple-animation {
    from {
        transform: scale(0);
    }
    to {
        transform: scale(1);
        opacity: 0;
    }
}

