css:
@-moz-document url-prefix('https://web.telegram.org') {
body {
        background: #000 url(http://YOUR_PIC_HERE.jpg)!important;
	background-attachment: fixed!important;
	background-size: 100% auto!important;
	background-repeat: no-repeat!important;
	background-position: center!important;
	overflow: auto!important;
	color: #839496 !important;
}
::-webkit-scrollbar {
	max-width: 10px !important;
	max-height: 10px !important;
	background: #181818 !important;
}
::-webkit-scrollbar-track,
::-webkit-scrollbar-corner {
	background: #181818 !important;
}
::-webkit-scrollbar-thumb {
	border-radius: 5px !important;
	background: rgba(80, 80, 80, 0.6) !important;
}
::-webkit-scrollbar-thumb:hover {
	background: rgba(96, 96, 96, 0.5) !important;
}
code,
pre {
	background: rgba(0, 0, 0, 0) !important;
	color: white;
}
pre {
	border: none;
}
.im_record {
	background: none;
}
.user_bgcolor_1,
.user_bgcolor_2,
.user_bgcolor_2,
.user_bgcolor_2,
.user_bgcolor_2,
.user_bgcolor_2,
.user_bgcolor_2,
.user_bgcolor_2,
.tg_page_head .tg_head_split,
.tg_page_head .navbar,
.im_dialogs_col .im_dialog_wrap.active a.im_dialog,
.im_dialogs_col .im_dialog_wrap a.im_dialog:hover,
.im_message_wrap .im_message_unread .icon-message-status,
.im_bottom_panel_wrap .composer_rich_textarea:active,
.im_bottom_panel_wrap .composer_rich_textarea:empty,
.im_bottom_panel_wrap .composer_rich_textarea:hover,
.im_bottom_panel_wrap .composer_rich_textarea:focus {
	background: rgba(0, 0, 0, 0) !important;
}
.dropdown.open .tg_head_btn,
.tg_head_btn:hover,
.dropdown-menu {
	background: rgba(50, 50, 50, 0.8) !important;
	border: none!important;
}
.tg_head_logo_dropdown .dropdown-menu > li > a,
.im_submit {
	color: #aaa;
}
.im_page_wrap {
	background: rgba(0, 0, 0, 0.8) !important;
	color: #839496 !important;
	border-left: 0px solid #2e2e2e !important;
	border-right: 0px solid #2e2e2e !important;
	border-bottom: 1px solid #000 !important;
	border-radius: 4px!important;
	box-shadow: none !important;
}
.im_dialogs_col_wrap {
	background: rgba(0, 0, 0, 0) !important;
	border-right: 0px solid #2e2e2e !important;
}
.im_dialogs_col_wrap input.im_dialogs_search_field {
	background: rgba(0, 0, 0, 0) !important;
	border: 1px solid #3a3a3a !important;
}
.im_dialogs_col .im_dialog_wrap a.im_dialog {
	border-top: 1px solid #3a3a3a !important;
	border-bottom: 1px solid #3a3a3a !important;
}
.im_dialogs_col .im_dialog_wrap .im_dialog_message_wrap .im_dialog_peer {
	color: #cad0d0 !important;
}
.im_dialogs_col .im_dialog_wrap .im_dialog_message_wrap .im_dialog_chat_from {
	color: #cb4b16 !important;
}
.im_dialogs_col .im_dialog_wrap .im_dialog_message_wrap .im_dialog_message_text {
	color: #93a1a1 !important;
}
.im_message_wrap {
	background: rgba(0, 0, 0, 0) !important;
	color: #839496 !important;
}
.im_message_wrap .im_message_text {
	color: #93a1a1 !important;
}
.im_message_wrap a {
	color: #cb4b16;
	text-decoration: none;
}
.im_message_unread_split,
.im_message_date_split,
.im_service_message {
	background: rgba(0, 0, 0, 0) !important;
	color: #eee8d5 !important;
}
.mobile_scrollable_wrap .im_message_wrap .im_message_body {
	background: rgba(0, 0, 0, 0) !important;
	padding: 7px 10px;
	border-radius: 0px;
}
.mobile_scrollable_wrap .im_message_wrap .im_message_text {
	line-height: 140%;
	font-size: 14px;
}
.im_bottom_panel_wrap .form-control,
.im_bottom_panel_wrap .composer_rich_textarea:hover,
.im_bottom_panel_wrap .composer_rich_textarea:active,
.im_bottom_panel_wrap .composer_rich_textarea:focus,
.im_bottom_panel_wrap .composer_rich_textarea:empty {
	box-shadow: none !important;
	border-color: #212121;
}
.tg_head_split {
	opacity: 0;
}
.tg_head_split:hover {
	opacity: 1;
}

.footer_empty {
    height: 48px;
}
.im_dialogs_col .im_dialog_wrap a.im_dialog {border: 0 !important;}

.im_dialogs_col_wrap input.im_dialogs_search_field {border: 0 !important; 	background: rgba(96, 96, 96, 0.1) !important;}
.form-control {color:#ccc;}
}
