css:
@-moz-document url-prefix("https://plamcheg.000webhostapp.com/wp-admin/post-new.php") {

/* WP add post */

html {
	-webkit-filter: invert(1) grayscale(100%)!important;
	background: #000 url(http://plamcheg.esy.es/stuff/fireman.jpg)!important; /* your pic here */
	background-attachment: fixed!important;
	background-size: 100% auto!important;
	background-repeat: no-repeat!important;
	background-position: center top!important;
	overflow: auto!important;
	margin: 0!important;
	padding: 0!important;
	border: 0!important;
}
img,
#tabtiles,
#tabtiles_tooltip,
iframe {
	-webkit-filter: invert(1)!important;
}
body,
div,
#adminmenu {
	border: 0!important;
	background: rgba(0, 0, 0, 0)!important;
	border-radius: 3px!important;
	box-shadow: none!important;
}
#post-body-content {
	margin-top: -20px;
	margin-left: -20px;
}
#postbox-container-1 {
	margin-top: -20px;
}
#content {
	height: 240px!important;
}
#ed_toolbar {
	border-radius: 3px 3px 0 0!important;
}
.wp-editor-area {
	border-radius: 0 0 3px 3px!important;
}
#adminmenu,
#wpadminbar {
	opacity: 0;
}
#adminmenu:hover,
#wpadminbar:hover {
	opacity: 1;
}
tbody,
label,
#post-status-info,
#minor-publishing-actions,
#titlediv,
#wpfooter,
#wp-admin-bar-top-secondary,
#contextual-help-link-wrap,
#screen-options-link-wrap,
.js .postbox .handlediv,
.misc-pub-post-status,
.misc-pub-visibility,
.misc-pub-curtime,
.misc-pub-section {
	display: none!important;
}
#new-tag-post_tag-desc {
opacity:0;
}
.misc-pub-section {
	margin-top: -15px!important;
}
.wrap h2,
.wp-heading-inline {
	opacity: 0;
}
#minor-publishing {
	height: 10px;
}
#prn_publish {
	top: 50%;
	position: absolute;
	left: 50%;
	margin-right: -50%;
	transform: translate(-50%, -50%);
}
textarea {
	background: rgba(255, 255, 255, 0.8)!important;
}
#ed_toolbar,
#submitdiv,
#tagsdiv-post_tag {
	background: rgba(255, 255, 255, 0.8)!important;
}
.wp-switch-editor {
	top: 30px;
}
#wp-content-editor-tools {
	margin: 0!important;
	padding: 0!important;
}
#insert-media-button {
	display: inline-block!important;
	position: absolute;
	top: 5px;
	left: 580px;
}
}
