css:
@-moz-document domain("forum.fivem.net") {
.d-header.clearfix {
    background-color: #1d1d1d !important;
}
body {
    font-family: unset !important;
}
html {
    background-color: #121313 !important;
}

.category-list .featured-topic a.last-posted-at,
.category-list .featured-topic a.last-posted-at:visited {
    color: #b7b7b7 !important;
}
.title {
    color: #00e2ff !important;
}
.title {
    color: #00e2ff !important;
}
.btn {
    background: #235767 !important;
}
.list-cell,
.table-heading,
.category-list td,
.category-list th {
    color: #dadada !important;
}
.hljs {
 background: #252525 !important;   
}
aside.quote .title {
	background-color: #252525 !important;
}
blockquote {
 background-color: #252525 !important; 
}
.category-list .topics .badge-notification,
.category-list .category .badge-notification {
    color: #f3ff91 !important;
}
.num.age.activity {
    color: #e1e1e1 !important;
}
.badge-posts[href] {
    color: #e1e1e1 !important;
}
.heatmap-high {
    color: rgb(255, 173, 109) !important;
}
.heatmap-med {
    color: #fd7d00 !important;
}
.heatmap-low {
    color: #cc6900 !important;
}
.topic-list th {
    color: #ededed !important;
}
.topic-list td {
    color: #dcdcdc !important;
}
.lang-lua.hljs {
    background: #1f1f1f !important;
}
.topic-excerpt {
    color: #ededed !important;
}
.topic-map {
    background-color: #232323 !important;
}
num age coldmap-high activity .category h3 a[href] {
    color: #ffffff !important;
}
[data-category-id="5"] .category {
    border-color: #ffc800 !important;
}
[data-category-id="16"] .category {
    border-color: #3cd0ff !important;
}
[data-category-id="30"] .category {
    border-color: #ff30cf !important;
}
[data-category-id="35"] .category {
    border-color: #0098ff !important;
}
[data-category-id="27"] .category {
    -webkit-box-sizing: content-box;
    -moz-box-sizing: content-box;
    box-sizing: content-box;
    width: 320px;
    border: none;
    font: normal 100%/normal Arial, Helvetica, sans-serif;
    color: #FFFFFF !important;
    text-shadow: rgba(0, 0, 0, 1) 1px 2px 4px;
    -o-text-overflow: clip;
    text-overflow: clip;
    background: -webkit-linear-gradient(45deg, rgba(255, 255, 255, 0.2) 25%, rgba(0, 0, 0, 0) 25%, rgba(0, 0, 0, 0) 50%, rgba(255, 255, 255, 0.2) 50%, rgba(255, 255, 255, 0.2) 75%, rgba(0, 0, 0, 0) 75%, rgba(0, 0, 0, 0) 0), rgb(70, 44, 0);
    background: -moz-linear-gradient(45deg, rgba(255, 255, 255, 0.2) 25%, rgba(0, 0, 0, 0) 25%, rgba(0, 0, 0, 0) 50%, rgba(255, 255, 255, 0.2) 50%, rgba(255, 255, 255, 0.2) 75%, rgba(0, 0, 0, 0) 75%, rgba(0, 0, 0, 0) 0), rgb(70, 44, 0);
    background: linear-gradient(45deg, rgba(255, 255, 255, 0.2) 25%, rgba(0, 0, 0, 0) 25%, rgba(0, 0, 0, 0) 50%, rgba(255, 255, 255, 0.2) 50%, rgba(255, 255, 255, 0.2) 75%, rgba(0, 0, 0, 0) 75%, rgba(0, 0, 0, 0) 0), rgb(70, 44, 0);
    -webkit-background-origin: padding-box;
    background-origin: padding-box;
    -webkit-background-clip: border-box;
    background-clip: border-box;
    -webkit-background-size: 50px 50px;
    background-size: 50px 50px;
}
.topic-avatar img {
    width: 55px !important;
    height: 55px !important;
}
.topic-avatar {
    width: 65px !important;
}
}
