css:
@-moz-document domain("www.yeppudaa.com") {
/* Main */
*
{
  box-sizing: border-box !important;
}

*,
*:focus,
*:active
{
  outline: 0 !important;
}

body
{
  background: #fff;
  color: #222;
}

.page
{
  background: #fff;
  border-radius: 0px;
  color: #222;
  border: 0px;
  width: 100% !important;
  padding: 12px;
}

.page>div[align="left"]
{
  padding: 0px !important;
}

.page>div[align="left"]>center
{
  background-color: #f2594b;
  padding: 6px;
}

.page>div[align="left"]>center a,
.page>div[align="left"]>center a:hover
{
  color: #FFF;
}

.page>div[align="left"]>table
{
  background-color: #EEE;
  width: 100%;
  padding: 12px;
}

.page>div[align="left"]>table td
{
  background-color: transparent !important;
  padding: 0px;
  border: 0px;
}

.page>div[align="left"]>table .alt2 form
{
  text-decoration: none;
}

.page>div[align="left"]>table .alt2 form input,
.page>div[align="left"]>table .alt2 form select
{
  width: 100% !important;
  display: block;
  padding: 9px;
  border-radius: 4px;
  color: #777 !important;
  height: 30px !important;
  margin-bottom: 4px;
  border: 1px solid #BDBDBD;
}

.page>div[align="left"]>table .alt2 form br
{
  display: none;
}

.page>div[align="left"]>table.tborder tr:first-child td.alt2 .smallfont
{
  padding: 12px;
}

.page>div[align="left"]>div.tborder
{
  border: 0px !important;
  padding: 0px !important;
  margin: 12px 0;
}

.page>div[align="left"]>div.tborder .vbmenu_control
{
  padding: 0px;
  border-radius: 0px;
  background-color: #626def;
}

.page>div[align="left"]>div.tborder .vbmenu_control a
{
  padding: 12px;
  display: block;
  border-bottom: 4px solid transparent;
}

.page>div[align="left"]>div.tborder .vbmenu_control font
{
  color: #FFF !important;
}

.page>div[align="left"]>div.tborder .vbmenu_control a:hover
{
  color: #FFF;
  border-color: #FFF;
}

.chromestyle
{
  width: 100%;
  min-width: 0;
  margin: 0px;
  background-color: #626EEF;
  padding: 12px;
}

.chromestyle:after
{
  display: none;
}

.chromestyle ul
{
  background: transparent none;
  width: auto;
  padding: 0px;
  text-align: left;
}

.chromestyle ul li
{
  display: inline-block;
}

.chromestyle ul li:last-child
{
  float: right;
}

.chromestyle ul li:last-child a
{
  display: inline-block;
}

.chromestyle ul li a
{
  color: #FFF;
  padding: 9px;
  border: 0px;
  border-radius: 3px;
  display: block;
}

.chromestyle ul li a img[src="search/yeni.gif"]
{
  margin-right: 6px;
}

.chromestyle ul li a:hover,
.chromestyle ul li a.selected
{
  color: #FFF;
  border: 0px;
  background: rgba(0, 0, 0, 0.4) none;
}

.dropmenudiv
{
  background-color: #626def;
  padding: 12px;
}

.dropmenudiv li
{
  list-style: none;
}

.dropmenudiv li a
{
  padding: 6px;
  font-size: 12px;
  color: #FFF;
  border-bottom: 1px solid rgba(255, 255, 255, 0.5);
}

.dropmenudiv li a img[src="search/yeni.gif"]
{
  margin-right: 6px;
}

.dropmenudiv li a:hover
{
  background-color: rgba(0, 0, 0, 0.4);
}

.dropmenudiv li:last-child a
{
  border-bottom: 0px;
}

iframe[src="http://www.yeppudaa.com/magazin.html"]
{
  margin-top: 12px;
  height: 151px;
}

iframe[src="http://www.yeppudaa.com/manset/"]
{
  width: 100%;
  height: 615px;
  margin-bottom: 12px;
}

body>div:nth-child(7)>div>div>b>div.alert-message.error
{
  padding: 32px;
  text-align: center;
  border: 0px;
}

body>div:nth-child(7)>div>div>b>div.alert-message.error a
{
  background-color: #FFF;
  padding: 12px 22px;
  border: 1px solid #BBB;
  text-align: center;
  font-weight: 600;
  box-shadow: 0px 0px 6px 1px rgba(0, 0, 0, 0.21) inset;
  border-radius: 32px;
}

body>div:nth-child(7)>div>div>b>div.alert-message.error a,
body>div:nth-child(7)>div>div>b>div.alert-message.error a font
{
  color: #222;
}

.tcat, .thead
{
  background-color: #878ecd;
  font-size: 14px;
  color: #FFFFFF;
  padding: 18px;
}

#collapseobj_cybcb_boxswitch tr .alt2
{
  border-radius: 0px;
  padding: 12px;
  border: 0px !important;
}

#collapseobj_cybcb_boxswitch tr .alt2:nth-child(1)
{
  width: 25%;
}

#collapseobj_cybcb_boxswitch tr .alt2:nth-child(1) > div
{
  height: auto !important;
  padding: 6px;
  background-color: #DDD;
  border-radius: 4px 4px 0px 0px;
}

#collapseobj_cybcb_boxswitch tr .alt2:nth-child(1) > div > select,
#collapseobj_cybcb_boxswitch tr .alt2:nth-child(1) > div > .button
{
  background-color: #FFF !important;
  border: 0px;
  height: 24px;
  padding: 3px 12px;
  width: auto !important;
}

#collapseobj_cybcb_boxswitch tr .alt2:nth-child(1) > div > select
{
  width: 64px !important;
}

#collapseobj_cybcb_boxswitch tr .alt2:nth-child(1) > #ccb_entermessage
{
  background-color: #EEE !important;
  padding: 6px;
  border-radius: 0px 0px 4px 4px;
  height: 200px !important;
  width: 100% !important;
}

#collapseobj_cybcb_boxswitch tr .alt2:nth-child(2)
{
  border-left: 2px solid #EEE;
}

#cybcb_messagearea
{
  background-color: #FFF !important;
  height: 275px !important;
}

.tcat>center
{
  text-align: left;
}

.alt1Active
{
  font-size: 11px;
}

.alt1Active > div > a,
.alt1Active > div > a *
{
  color: #333;
}

body > b > form > br
{
  display: none;
}

body > b > form > div
{
  color: #FFF;
  background-color: #878ecd;
  padding: 32px;
}


/* Magazin */
body > div > fieldset
{
  background-color: #FFF !important;
  margin: 0px !important;
  width: 100% !important;
  border: 3px solid #BBB !important;
  height: 150px !important;
  padding: 12px;
  color: #555 !important;
  font-family: Verdana;
  font-size: 13px !important;
}

body > div > fieldset a
{
  margin-bottom: 6px;
  margin-right: 12px;
  display: inline-block;
  text-decoration: none;
  color: #333;
}

body > div > fieldset a:hover
{
  background-color: transparent;
  color: #C84361;
}
/* Manset */
.indentmenu ul
{
  width: 100%;
  border: 0px;
  background: #878ECD;
  padding: 6px 6px 0px 6px;
  float: none;
  border-radius: 4px 4px 0px 0px;
}

.indentmenu ul+br
{
  display: none;
}

.indentmenu ul li
{
  display: inline-block;
  vertical-align: top;
}

.indentmenu ul li:last-child a
{
  padding: 10px 12px;
}

.indentmenu ul li a
{
  padding: 12px;
  float: none;
  border: 0px;
  color: #FFF;
  display: block;
}

.indentmenu ul li a:hover,
.indentmenu ul li a.selected
{
  padding: 12px;
  background: #FFF;
  border-radius: 4px 4px 0px 0px;
  color: #878ECD !important;
}

.indentmenu ul li a:visited
{
  color: #FFF;
}

.indentmenu ul center li a
{
	background: transparent none;
    border: 1px solid #FFF;
    border-radius: 4px;
    margin-right: 6px;
    padding: 4px 9px;
}


.indentmenu ul center li a:hover,
.indentmenu ul center li a.selected
{
  padding: 4px 9px;
  border-radius: 4px;
}
  
fieldset .indentmenu ul {
  margin: 0px;
    padding-bottom: 12px;
}

#petsdivcontainer
{
  border: 0px !important;
  width: 100% !important;
  height: 570px !important;
  background-color: #FFF;
  padding: 6px !important;
  border-width: 0px 6px 6px 6px !important;
  border-style: solid !important;
  border-color: #878ecd !important;
  border-radius: 0px 0px 4px 4px;
}

#petsdivcontainer > iframe
{
  height: 555px !important;
  min-height: 0px !important;
}
/* anasayfamansett.php */
body
{
  width: 100%;
  overflow-y: auto;
  height: auto;
}

div.wrapper
{
  margin: 6px;
  border: 0px;
  display: inline-block;
  float: none;
  width: 10%;
  overflow: hidden;
}

div.wrapper > a
{
  display: block;
}

div.wrapper > a:hover .description
{
  background-color: #f2594b;
}

div.wrapper > a > img
{
  width: 100%;
  height: 125px;
}

div.wrapper > a > img:hover
{
  background: transparent none;
  border-radius: 0px;
}

div.wrapper > a > .description
{
  height: auto;
  background-color: rgba(0, 0, 0, 0.7);
  color: #fff;
  padding: 6px;
  border-radius: 0px;
  right: 0px;
  bottom: 0px !important;
  left: 0px;
  opacity: 1;
}

div.wrapper > a > .description .description_content
{
  margin: 0px;
  background: transparent;
}

div.wrapper > a > .description .description_content:hover
{
  height: auto;
  border-radius: 0px;
}
}
