css:
@-moz-document url-prefix("https://yandex.ru/search") {
body{
    font-size: small;
    font-family: arial,sans-serif;
}

:root{
	--color-action: #4285f4;
    --color-arrow: #4285f4;
    --color-arrow-active: #4285f4;
    --color-selection: #4285f4;
}

/** head **/

.user__name, .favicon__icon, .websearch-button__text:before, .input__voice-search, .input__clear, .navigation__more-label{
    display: none !important;
}

.serp-header__main{
   	padding: 9px 12px 4px 0px;
}

.serp-header__search2{
	padding: 9px 0px 0px 2px;
}
.serp-header__wrapper, .popup2, .suggest2__items, .suggest2_search_yes .suggest2__content:before, .suggest2_theme_large:after{
    background-color: #fafafa !important;
}

.suggest2-item_selected_yes{
	background: #eee;
}

.main_distro-footer_yes {
    margin-top: -405px;
    padding-top: 422px;
}

.main__center {
    margin-left: 116px;
}

.content__right.content__right_cols_12{
	width: auto;
	max-width: 536px;
    padding: 24px 36px;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
}

.ticker .ticker__count {
    display: inline-block;
    text-decoration: none;
    color: #fff;
    border-radius: 20px;
    width: 20px;
    height: 20px;
    padding: 3px 0px;
    font-size: 11px !important;
    text-align: center;
    box-sizing: border-box;
    line-height: 20px;
    background: red;
    font: inherit;
}

/** logo **/

.serp-logo {
    position: relative;
}

.serp-header__logo a{
    background-image: url('data:image/png;base64,');    
    height: 44px;
    width: 120px;
    padding-left: 13px;
    padding-right: 12px;
}

/** search input **/

.input__control{
	font-size: 16px !important;
}

.input_theme_websearch{
    border: 2px solid #ffffff;
}

.websearch-button__text{
    border: none;
    font-size: 0px;
    background: url('data:image/svg+xml,%3Csvg%20focusable%3D%22false%22%20xmlns%3D%22http%3A//www.w3.org/2000/svg%22%20viewBox%3D%220%200%2024%2024%22%3E%3Cpath%20d%3D%22M15.5%2014h-.79l-.28-.27C15.41%2012.59%2016%2011.11%2016%209.5%2016%205.91%2013.09%203%209.5%203S3%205.91%203%209.5%205.91%2016%209.5%2016c1.61%200%203.09-.59%204.23-1.57l.27.28v.79l5%204.99L20.49%2019l-4.99-5zm-6%200C7.01%2014%205%2011.99%205%209.5S7.01%205%209.5%205%2014%207.01%2014%209.5%2011.99%2014%209.5%2014z%22%20style%3D%22fill%3A%20%234285f4%3B%22%3E%3C/path%3E%3C/svg%3E');
    background-repeat: no-repeat;
    width: 39px;
    background-color: #fff;
    background-position: center;
    background-size: 25px;
    height: 36px;
    margin: 0;
}

.search2{
	box-shadow: 0 2px 2px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.08);
    border-radius: 2px;
    padding: 4px;
    background: #fff;
    font: 16px arial,sans-serif;
}


/** navbar **/

.navigation_type_horizontal {
    height: 55px;
    background: #fafafa;
    padding: 0px 146px;
    top: 60px;
}

.navigation__item .service__name {
    color: #777;
    text-transform: none;
    font-weight: 100;
    
    margin: 0px !important;
}

.navigation__item a{
    padding: 12px !important;
    font-size: 13px !important;
    font-family: arial,sans-serif !important;
}

.navigation__item_state_selected a {
    box-shadow: inset 0px -12px 0px 0px #4285f4;
}
.navigation__item_state_selected span.service__name {
    color: #4285f4;
    font-weight: bold;
}
.navigation__item_state_selected .service__name:after{
   	display: none;
    opacity: 0;
}



}
