css:
@-moz-document url-prefix("https://www.instalki.pl") {
/*

NOWE WERSJE DOSTĘPNE NA: 
<a  href="https://greasyfork.org/scripts/395041-instalki-pl-ciemny-wygl%C4%85d">GreasyFork.org</a>, 
<a  href="https://userstyles.world/style/148/instalki-pl-ciemny-wygl-d">UserStyles.world</a>, 
<a  href="https://github.com/pabli24/instalki-dark">GitHub.com</a> 

*/

/* tekst */
body, .more.moreactive, 
div.cat_list ul li a:link, div.cat_list ul li a:visited, div.cat_list h3,
#user5 .breadcrumbs, #user5 .breadcrumbs a, #show_sort .links .active, #show_sort .links .normal, #show_items .desc,
#dj_bcatalog, #dj_catalog, #dj_gcatalog, #djcatalog, .user_profile, .djcatalog_item .details .row, .djcatalog_item .details .row_voting, .djcatalog_item .size_update_box .row, .item_comments .comments_title, ul li {
	color: #ccc;
}
h2, h2 a, strong, #show_items .name a .version, .djcatalog_item .item_name_prog .version, #right .djmod2.module_tab.prog_cat table .version_date {
	color: #e5e5e5;	
}
a:link, a:visited,
#show_items .name a, .djcatalog_item .item_name_prog, .djcatalog_item .item_name_prog h1.name, .djcatalog_item .description_title .title_box, #download_mirrors .mirror_title, #right .djmod2.module_tab.prog_cat table.items_numerical a, a > strong {
	color: #93b2ce;
}
a:link:hover, a:visited:hover,
#show_items .name a:hover {
	color: #3688d4;
}
.cat_icon, .cat_icon a {
	color: #fff!important;
}
/* tlo */
body, #top-left-content, #wrapper, #main, #content_in, .panel.active,
#article_title, ul.mostread li, .banner_itemsc_center, .pagenavbar, iframe, .twoj_tabs_class3 {
	background: #000!important;
}
.more.moreactive {
	background-color: #000;
}
.more.moreactive {
	border-bottom: solid 1px #151515; /* border pod newsem na glownej */
}
#footer_outer {
	background: #000;
	border-top: 1px solid #000;
}
#footer {
	border-top: 1px solid #000;
	border-bottom: 1px solid #444;
}
div#pageBreakNavigation {
	background: #000000;
	border-top: 1px dotted #311a00;
}
/* top menu ciemniejsze */
#topmenu_2_outer, #topmenu_1 ul.menu li#current a, #topmenu_1 ul.menu li.active a,
#topmenu_1 ul.menu #current, #topmenu_1 ul.menu .active, #topmenu_1 ul.menu li:hover {
	background: #366978;
}
/* przyciski czytaj wiecej i inne */
.blog .commentBlogView .show-linkmore a, .djcatalog_item .description_title .content_rm #djcat_content_rm_in, .inst_see_more_box a, .panel .show-linkmore a,
.blog_more .moremore {
	color: #bbb;
	background: url(https://www.instalki.pl/images/icons/icons-new.png) right -461px no-repeat #0e0e0e;
}
.blog_more .moremore a, a.readon:hover {
	color: #bbb;
}
/* menu glowne i inne */
#right div.module_tab2 #twoj_container-2 ul.twoj_tablink.ui-tabs-nav, #right div.module_tab2 #twoj_container-4 ul.twoj_tablink.ui-tabs-nav, div.djmod2 h3, div.djmodprim h3 {
	border-bottom: 3px solid #222; /* pasek pod menu glowne i innymi pomaranczowo-bialy  */
}
.module .primdjmod-content .menu li {
	border-bottom: 1px solid #000;
}
#right .djmod2.module .mod-content, #right .djmod2.module .mod-content.gradient, div.djmodprim .primdjmod-content {
	background: #0a0a0a;
	border-top: 5px solid #000;
}
.module .primdjmod-content .menu a {
	color: #ccc;
}
.module .primdjmod-content .menu a:hover {
	color: #ffffff;
}
.level0 > a > span[style="color: red;"] {
	color: #ea7575!important; /* sklep z programami */
}
/* prawa strona programy */
#right div.module_tab2 #twoj_container-2 .twoj_tab_content .twoj_li_top, #right div.module_tab2 #twoj_container-4 .twoj_tab_content .twoj_li_top {
	border-right: 2px solid #000;
}
#right div.module_tab2 #twoj_container-4 #twoj_container-2 .ui-tabs-nav .twoj_li_top a, #right div.module_tab2 #twoj_container-4 #twoj_container-3 .ui-tabs-nav .twoj_li_top a {
	background: #151515;
}
#right div.module_tab2 #twoj_container-2 .twoj_tab_content .twoj_tablink.ui-tabs-nav, #right div.module_tab2 #twoj_container-4 .twoj_tab_content .twoj_tablink.ui-tabs-nav {
	border-bottom: 2px solid #0a0a0a;
}
#right div.module_tab2 #twoj_container-2 .twoj_tab_content .twoj_tab_content, #right div.module_tab2 #twoj_container-4 .twoj_tab_content .twoj_tab_content {
	background: #0a0a0a!important;
}
table.items_numerical a:link, table.items_numerical a:visited {
	color: #ccc;
}
.djmod2.module.img100 .rsstable td {
	border-bottom: 11px solid #000;
}
/* news podziel sie */
.banner_article_comments > div > div {
	background-color: #0a0a0a!important;
}
/* news podobne tematy */
#wrapper .rtih .rtih_bloc .rtih_entry .rtih_title, ul.latestnews li a:link, ul.latestnews li a:visited, ul.mostread li a:link, ul.mostread li a:visited {
	color: #ccc;
}
.rtih_entry:hover {
	background: #151515;
}
/* zalety wady */
#ver-minimalist td, #ver-minimalist th {
	border-right: 30px solid rgba(0, 0, 0, 0);
	border-left: 30px solid rgba(0, 0, 0, 0);
}
#ver-minimalist th {
	border-bottom: 2px solid #ffa849;
	color: #6296ff;
}
#ver-minimalist td {
	color: #ccccff;
}
/* programy gry */
#user5 .breadcrumbs {
	border-bottom: 1px solid #151515;
}
.contentheading, #show_sort .links #sort_by, #show_sort #on_page {
	color: #aaa;
}
#show_sort {
	background: #111;
}
#show_sort .links .active.ord_asc,
#show_sort .links .active, #show_sort .links .normal:hover{
	background: #222!important;
}
#show_sort .links .active {
	padding-left: 25px;
}
#show_sort .links {
	width: 650px;
}
#show_items .item_all, #show_items .item_all.item_all_bg0,
.djcatalog_item .item_name_prog, .djcatalog_item .details .row, .djcatalog_item .details .row_voting, .djcatalog_item .size_update_box .row {
	background: #050505;
}
#show_sort .limit select {
	border: 1px solid #222;
	border-left: 4px solid #222;
	background: #111;
}
.djcatalog_item .description_title #djcat_content_rm.content_rm {
	background: linear-gradient(to bottom,rgba(0, 0, 0, 0) 0,rgb(0, 0, 0) 75%)!important;
}
.djcatalog_item .images_slider .islider_outer {
	border-left: 40px solid #000;
	border-right: 40px solid #000;
}
.djcatalog_item .images_slider .islider_right, .djcatalog_item .images_slider .islider_left.blocked {
	filter: invert(1);
}
.djcatalog_item .links_tools .users_screenshots {
	background: url(https://www.instalki.pl/images/icons/icons-new.png) 29px -593px no-repeat #151515;
}
.djcatalog_item .links_tools .error {
	background: url(https://www.instalki.pl/images/icons/icons-new.png) 30px -620px no-repeat #151515;
}
.djcatalog_item .fav_links .favorites a {
	background: url(https://www.instalki.pl/images/icons/icons-new.png) 34px -593px no-repeat #151515;
	border-right: 2px solid #000;
}
.djcatalog_item .fav_links .users_favorites a {
	background: url(https://www.instalki.pl/images/icons/icons-new.png) 48px -593px no-repeat #151515;
	border-left: 2px solid #000;
}
.djcatalog_item .fav_links .favorites a, .djcatalog_item .fav_links .users_favorites a, .djcatalog_item .links_tools .buy, .djcatalog_item .links_tools .error, .djcatalog_item .links_tools .users_screenshots {
	color: #eee;
}
.twoj_tabs_class3 .ui-tabs-panel {
	border: 1px solid #000;
}
/* dol */
#user8 #category_columns {
	border: 1px solid #333;
	border-top: 3px solid #333;
}
#user8 .twoj_tabs_class3 .ui-tabs-nav .twoj_li_top, #user8 .twoj_tabs_class3 .ui-tabs-nav .twoj_li_top.ui-tabs-selected {
	border-bottom: 3px solid #333;
}
#user8 .twoj_tabs_class3 .ui-tabs-nav .twoj_li_top.ui-tabs-selected a,
#user8 .twoj_tabs_class3 .ui-tabs-nav .twoj_li_top.ui-tabs-selected a span {
	background: #333;
	color: #fff;
}
#user8 .twoj_tabs_class3 .ui-tabs-nav .twoj_li_top.ui-tabs-selected a span {
	color: #fff;
}
div.comment_box1 {
	background: #0a0a0a;
}
.item_comments .comments_title {
	border-bottom: 3px solid #262626;
}
/*[[kafelki]]*/
}
