css:
@-moz-document regexp("https?:\\/\\/lodash.com\\/docs\\/.*") {
header,
html.docs .doc-main,
html.docs .toc-container {
  background-color: #3f3f3f;
}

header .logo {
  border-right-color: #7a7a7a;
}

header .logo a {
  background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 2500 2500'><path d='M70 188.5h220v1460h870v180H70v-1640zm1776 396c-225-3-450 140-520 357-100 250-54 564 142 755 190 158 476 170 690 58 147-78 240-233 260-396 26-170 13-356-70-510-98-164-290-270-480-263l-22-3zm20 174c165 0 313 126 340 288 50 183 20 407-130 536-140 114-365 98-480-43-140-150-140-380-68-560 50-130 183-223 323-220h18z' fill='#ebebeb'/><path d='M70 2061.5h2360v250H70v-250z' fill='#3492ff'/></svg>");
}

html.docs select,
html.docs .doc-container h3 {
  background-color: #555;
}

html, h1, h2, h3, h4,
html.docs select,
html.docs .search input,
html.docs .toc-container ul li a {
  color: #ebebeb;
}

html.docs select {
  border-color: #555;
}

a {
  color: #6a8dee;
  border-bottom-color: #a8a8a8;
}

a:hover {
  color: #7a9efa;
  border-bottom-color: #c7c7c7;
}

html.docs .doc-container h3+p a {
  color: #888;
}

html.docs .doc-container h3+p a:hover {
  color: #9e9e9e;
}

html.docs .toc-container ul {
  border-left-color: #7a7a7a;
}

html.docs .search input {
  background-color: transparent;
  border-bottom-color: #7a7a7a;
}

header {
  box-shadow: 0 0 2.25rem #142127;
}

@media (min-width: 768px) {
  html.docs .toc-container {
	  box-shadow: 0 0 .5rem #142127;
  }
}

html.docs .toc-container::-webkit-scrollbar-thumb {
  border: 1px solid #555;
}
}
