css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://learn2.acseducation.edu.au/mod/forum/post.php") { 
  .col-md-9{
    width:100%!important;
  }
  /*the left panel, showing the students post (in the meeting place forum)/ or the forum questions (in the dF) */
  div[role="main"]>div:nth-of-type(1){
    display:relative;
    max-height:1000px;
    width:40%;
    float:left;
    overflow:scroll;
    margin-left:12px;
    margin-right:20px!important;
  }
  div[role="main"]>h2{
    margin-top:6px!important;;
    margin-left:14px;
    font-weight:bold;
  }
   /* The post to forum button */
  #fgroup_id_buttonar{
    position:absolute;
    top:76px;
    right:32px!important;
    box-shadow: none;
    padding:2px!important;
    padding-left:4px !important;
    background: none;
  }
  
  /* The boxes I enter into */

  .felement.feditor, .felement.ffilemanager{
    width:100%!important;
    padding-right:30px!important;
  }
  .fitem.required.fitem_ftext{
    margin-bottom:0px;
  } 
  .fitemtitle{
    display:none;
  }
  input#id_subject{
    width:287px;
  }
  #fitem_id_subject::before{
    content:" Subject"!important;
    font-size:1.2em;
    font-weight:bold;
    margin-left:14px;
  }
  #fitem_id_discussionsubscribe{
    display:none;
  }
  #fitem_id_attachments::before{
    content:" Attachments"!important;
    font-weight:bold;
    font-size:1.2em;
    position:relative;
    width:100px;
    height:24px;
    top:12px;
    left:16px;
  }
  #fitem_id_mailnow::after{
    content:" Mail Now"!important;
    font-weight:bold;
    display:inline-block;
    position:relative;
    left:-480px;
    top:6px;
    width:100px;
    height:24px;
  }
  /*The instructions for the student - hide them until mouse-over 
  div[role="main"]>div:nth-of-type(1){
    display:relative;
    height:150px;
    margin-left:10px;
    overflow:hidden;
    border:2px solid rgb(23,34,125);
    border-radius:4px;
    box-shadow: -5px 5px 7px -3px rgba(0,0,40,.4);
    width:80%;
    margin-bottom:10px!important;
  }
  div[role="main"]>div:nth-of-type(1)::before{
    content:"Mouse-Over to see student instructions (and the questions for you to copy and paste)";
    font-weight: bold;
    color:rgb(0,160,0);
    background: rgba(230,230,255,1);
    padding:14px;
    width:100%;
    text-align:center;
  }
  div[role="main"]>div:nth-of-type(1):hover::before{content:none}
  
  div[role="main"]>div:nth-of-type(1):hover{
    width:initial;
    height:auto;
    max-height:700px;
    position:absolute;
    overflow:scroll;
    background:none;
    color:black;
    left:30px!important;
    top:-3px;
    width:90%;
    background: white;
    box-shadow: -7px 7px 17px 13px rgba(0,0,40,.4);
    z-index:30;
  }
*/
  
  /* The vericite warning */
  div[role="main"]>div:nth-of-type(2){
    width:290px;
    overflow:hidden;
    height:22px;
    line-height:18px;
    float:right;
    position:absolute;
    margin:0px!important;;
    left:646px;
    top:-15px;
    background: white;
    border:1px solid rgb(23,34,125);
    border-radius: 4px;
    text-align:center;
  }
  div[role="main"]>div:nth-of-type(2)::before{
    content:"Mouse-over to see Vericite message";
    display:inline-block;
    width:100%;
  }
  div[role="main"]>div:nth-of-type(2):hover::before{content:none}
  
  div[role="main"]>div:nth-of-type(2):hover{
    width:initial;
    height:auto;
    position:absolute;
    background:none;
    color:black;
    background: white;
    box-shadow: -7px 7px 17px 13px rgba(0,0,40,.4);
    z-index:31;
    content:none;
    padding:20px!important;
    
  } 
  div[role="main"]>div:nth-of-type(2):hover::before{
    content:none
  }
  

}
