css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://learn2.acseducation.edu.au/login/learnbooklogin.php") {
  
  
 html { 
    height:100%;
    width:100%;
    background: url("http://learn2.acseducation.edu.au/pluginfile.php/1/theme_learnbook/slideshow1/1477610439/DeskTop.jpg") no-repeat center center fixed!important; 
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover!important;
}
  
  body { 
    height:100%;
    width:100%;
    background:none;
  }


  #learnbooklogin .container{
    display:block;
    margin:0px;
    padding:0px;
    width:527px;
    height:166px;
    position: absolute;
    top: 30%;
    left: 50%;
    margin-top: -83px;
    margin-left: -265px; 
  }
  #learnbooklogin .row{
    display:block;
    width:100%;
    margin:0px!important;
    padding:0px;
  }
  #learnbooklogin #loginbox{
    width:100%;
    margin:0px;
    padding:0px;
  }
  #learnbooklogin #supersized{
    display:block;
    display:none;
  }
  #learnbooklogin .login-header{
    display:block;
    position:relative;
    float:left;
    width:239px;
    padding-left:6px!important;
    padding-right:6px!important;
    background-color: white;
    
  }
  #learnbooklogin .logininner{
    display:block;
    position:relative;
    float:left;
    width:288px;
    padding:0px!important;
    padding-left:12px!important;
    padding-right:12px!important;
    margin:0px!important;
    padding-top:12px!important;
    padding-left:12px!important;
    padding-bottom:8px!important;
  }
  #learnbooklogin legend{
    display:none;
    margin:0px;
    margin-top:0px;
    padding:2px;
  }
  #learnbooklogin #loginbutton{
    display:block;
    padding:2px!important;
  }
}
