css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://learn2.acseducation.edu.au/user/index.php") {
  #page-content{
    margin-top:70px;
  }

  .col-md-9 div h2{
    position: absolute!important;
    top:-49px!important;
    font-weight:bold;
  }
  
  .action-icon{
    top:-130px;
    left:-120px;
    position:relative;  
  }
  
  /* all drop down boxes */
  select,input{
    height:28px!important;
    line-height:24px!important;
    padding: 0 6px 0 6px!important;
  }
  
  /* The "My Courses" dropdown */
  #courseform{
    position: relative;
    top:-24px;
    width:340px;
  }
  
  /* The current role of people displayed */
  #rolesform{
    position: relative;
    top:-40px;
    width:240px;
  }
  .col-md-9 label{
    margin-right:4px;
  }
  
  /* The select all, deselect buttons all and choose dropdown */
  div.buttons{
    position:fixed;
    top:170px;
    left:254px;
    width:500px;
    overflow: visible;
  }
  input#checkall{
    border:rgb(1,84,162) 2px solid;
    position: absolute;
    left:2px;
    height:30px;
    line-height: 28px;
    padding:0 8px 0 8px;
    -webkit-box-shadow:  2px 2px 20px -2px rgba(0,0,0,0.55);
    box-shadow: 2px 2px 20px -2px rgba(0,0,0,0.55);
  }
  input#checknone{
    border:rgb(1,84,162) 2px solid;
    position: absolute;
    left:84px;
    padding:0 8px 0 8px;
    -webkit-box-shadow:  2px 2px 20px -2px rgba(0,0,0,0.55);
    box-shadow: 2px 2px 20px -2px rgba(0,0,0,0.55);
  }
  input#checkall:hover,  input#checknone:hover{
    background:#337AB7;
    color:white;
  }
  .helptooltip img.iconhelp{
    position:absolute;
    left:188px;
    top:5px;
    width:20px;
    height:20px;
  }
  select#formactionid{
    border:rgb(1,84,162) 2px solid;
    left:220px;
    top:0px;
    display:block;
    position: absolute;
    padding:0 8px 0 8px;
    -webkit-box-shadow:  2px 2px 20px -2px rgba(0,0,0,0.55);
    box-shadow: 2px 2px 20px -2px rgba(0,0,0,0.55);
  }
  label[for="formactionid"]{
    display:block;
    position: absolute;
    left:370px;
    top:6px;
    width:200px;
  }

  
  /* The search form label, textbox, and search button */
  .searchform{
    position:absolute;
    top:40px;
    right:-72px;
    width:400px;
  }
  .searchform input{
    display:inline-block!important;
    float:left!important;;
  }
  .searchform label{
    display:inline-block!important;
    float:left!important;
    margin-right:5px;
    line-height: 30px;
  }
  .searchform #search{
    width:200px;
  }

   /* The "Show All" button */
  #showall{
    position:fixed;
    top:160px;
    background:white;
    -webkit-box-shadow:  2px 2px 20px -2px rgba(0,0,0,0.55);
    box-shadow: 2px 2px 20px -2px rgba(0,0,0,0.55);
  }
  #showall a{
    display:inline-block;
    min-width:100px;
    width:auto;
    height:28px;
    line-height:24px;
    text-decoration: none;
    text-align: center;
    color:black;
    border-radius: 6px;
    border:rgb(1,84,162) 2px solid;
    padding:0 6px 0 6px;
  }
  #showall a:hover{
    background:#337AB7; 
    color:white;
  }
  
  /*THis is displayed when not all students are showing on the one page */
  .paging{
    position:absolute;
    top:-16px;
    left:100px;
    padding-left:10px;
    background-color:white;
    line-height: 34px;
  }
  .paging a:hover{
    background:#337AB7!important;;
    color:white;
  }

  
  /*The 26 first name initial links and 26 surname */
  .initialbar{
    position: absolute;
    right:-2px;
    top:-130px;
  }
  .initialbar.firstinitial{
  }
  .initialbar.lastinitial{
    top:-110px;
  }
  
  /* the little description of the role currently selected (teacher, student, All Participants ) */
  .userlist h3{
    position: relative;
    top:-67px!important;
    left:236px!important;
    width:300px;
    font-size:16px;
  }

    
  /* The "Inactive For More Than dropdown */
  #timeoptions{
    position:absolute;
    right:-30px;
    top:-20px;
    width:300px;
  }
  
  /* The UserList drop down (brief, user Details) */ 
 #formatmenu{
    position:absolute!important;
    top:12px!important;
    right:2px;
    width:300px;
  }
  
  /* This shows if make a navigation selection e.g. choose first name "a" */
  .resettable.mdl-right {
    position: absolute;
    right:8px;
    top:-80px;
    width:180px;
    height:30px;
    line-height:28px;
    background: #17227d; /* Old browsers */
    background: linear-gradient(0deg, #17227d 0%,#0154a2 100%); 
    text-align: center;
    color:white;
    border-radius: 6px;
    border:rgb(1,84,162) 1px solid;
  }
  .resettable.mdl-right>a {
    text-decoration: none;
    color:white;
    display:block!important;
    position: absolute!important;
    top:0px!important;
    left:0px!important;
    width:100%!important;
    height:100%!important
  }
  .resettable.mdl-right>a:hover{
    background: linear-gradient(135deg, #AB0D29 0%,#D62833 100%);
  }
  .no-overflow{
    top:20px;
    position:relative;
  }
  
  
  /* The List of Students */
  
  /* The list containing all people , from teh headings down */
  #participantsform{
    position:relative!important;
    top:-36px; 
  }
  .userpicture{
    width:20px;
    height:20px;
    position:relative;
  }
  .userpicture:hover{
    width:40px;
    height:40px;
    display:block;
    position:absolute;
    top:2px;
  }
  #participantsform table td{
    padding:0px!important;
    position:relative; /* did this so that resizing of images would work with mouse over and absolute */
  }
  #participantsform table tr:hover{
    background:rgba(50,110,190,.25)!important;
  }
}
