css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://online.melbournepolytechnic.edu.au/") {
  .btn.btn-danger, .btn.btn-success{
    position:fixed;
    right:20px;
    top:-4px;
  }
  #wrapper .block_settings  p{
  }
  /* switch role */
  #wrapper .block_settings  ul:nth-Child(1)>li:nth-Child(2){
    position:fixed!important;;
    right:160px;
    top:-4px;
    background-color:#5bb75b;
    color:white;
    border-radius: 4px;
    height:32px;
    line-height: 30px;
    width: 120px;
    overflow:hidden;
  }
  #wrapper .block_settings  ul:nth-Child(1)>li:nth-Child(2):hover{
    height:auto;
    z-index: 99999;
    border:thin orange solid;
  }
   #wrapper .block_settings span,
   #wrapper .block_settings a,
  #wrapper .block_settings p{
    color:white;
  }
  /*navigation block, including particpants */
  .block_navigation.block ul[role="group"]> li:nth-child(1){}
  .block_navigation.block{
    position:fixed;
    right:290px;
    top:-18px;
    background-color:#5bb75b;
    color:white!important;;
    border-radius: 4px;
    width:160px;
    height:10px;
    line-height: 10px;
    overflow:hidden;   
    z-index:90;
  }
  .block_navigation.block h2,
  .block_navigation.block img{
    display:block;
    position:relative;
    top:5px;
  }
  .block_navigation.block:hover{
    height:auto;
    width:auto;
    z-index: 99999;
    border:thin orange solid;
  }
  .block_navigation.block span,
  .block_navigation.block h2,
  .block_navigation.block h2:before,
  .block_navigation.block a,
  .block_navigation.block p{
    color:white!important; 
  }
}
